INSERT INTO `etk_faqs` VALUES ('9', '14', 'Multi-user account access', 'multi-user-account-access-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Multi-user account access</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Give account access to subusers</li>\r\n                                <li>\r\n                                    Control access to events, actions taken and emails received by subusers</li>\r\n                                <li>\r\n                                    Subuser emails cannot already be associated with an Eventbrite account</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can only add as subusers email addresses that are not already associated with an Eventbrite account. If you\'re getting an error message that the subuser you\'re trying to add already has an Eventbrite account, go <a href=\"/en_us/t/subuser-already-registered-error\">here </a>for help.</p>\r\n                                <p>\r\n                                    If you\'re trying to make an existing subuser on your account the administrator for that account, click <a href=\"/en_us/t/make-a-subuser-the-account-admin\">here</a> to learn how.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After logging in to your Eventbrite account, select the <strong>Account</strong> tab located at the top right side of the page. <img style=\"border-width: 0px; border-style: solid;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multiuser/header-1.png\" alt=\"How to add multi-users to your account\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Multi-User Access</strong> in the menu on the left. Then click <strong>Add Email Address</strong> to add the new subuser email address.&nbsp; <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multiuser/multi-user%20access.png\" alt=\"How to add multi-users to your account\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the new subuser\'s email address, which they will use to log in to Eventbrite. Then indicate the level of access and permissions you want to give this subuser. By default, they will be able to access all events, perform any action and will receive all emails--though you can customize these parameters as you wish.&nbsp; <img width=\"610\" height=\"383\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multiuser/permissions.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you click <strong>Save</strong>, you will see a message indicating that the subuser has been added, and their email address will be added to the list above of email addresses with access to your account.</p>\r\n                                <p>\r\n                                    At this point, an email with a link to set a password for their new subuser account will be sent to the new subuser. They will be able to log in to your account once they click the link in the email and set a password. <img width=\"606\" height=\"503\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multiuser/save.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228536', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('10', '14', '\"Subuser already registered\" error', '-subuser-already-registered--error-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>\"Subuser already registered\" error</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Subuser email address already registered with Eventbrite</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Note: </b>If an email address is already associated with an Eventbrite account, you will not be able to add that email as a subuser. Why? When adding a subuser to your account, you are also creating an account for them, and an email address cannot be associated with two Eventbrite accounts. To bypass the following steps, just add an email address--the potential subuser\'s personal email address, for instance--that is not already registered with Eventbrite.</p>\r\n                                <p>\r\n                                    If you\'re trying to make the current subuser on an account the administrator on that account, click <a href=\"/en_us/t/making-a-subuser-the-account-admin/\">here</a> for instructions.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the subuser\'s email is already registered with an Eventbrite account, you will see the following error.</p>\r\n                                <a href=\"http://eventbrite.com/resetpassword\"><img width=\"585\" height=\"392\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multi-user-already-registered/subuserhelp1.png\"></a>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The subuser that you are unable to add needs to log in to their Eventbrite account and close their account. Have them click on the<b> Account</b> tab located at the top right side of the page.</p>\r\n                                <img width=\"583\" height=\"61\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multi-user-already-registered/subuserhelp2.png\" alt=\"How to add multi-users to your account\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> If the subuser is having trouble remembering their password to log in to their account, they can reset their password by clicking <a href=\"http://eventbrite.com/resetpassword\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <b>Account </b>tab, the subuser needs to click on <b>Close Account</b>. They will be asked to provide a reason for why they are closing their account (they can say something like \'switching email addresses on account\'), type in CLOSE, and their password. Lastly, they need to click on the green button that says <b>Close Account</b>.</p>\r\n                                <img width=\"610\" height=\"383\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multi-user-already-registered/subuserhelp33.png\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Their Eventbrite account is now closed and their email address is free for use!&nbsp;</p>\r\n                                <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multi-user-already-registered/subuserhelp4.png\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'re almost there! Log in to your account--i.e., the admin account--and click on the<b> Account</b> tab located at the top right side of the page. Then, select <b>Multi-User Access </b>on the left hand side.</p>\r\n                                <div class=\"step\">\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/multi-user-already-registered/subuserhelp6.png\">\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can now enter the subuser\'s email address! To complete the process, you can click <a href=\"/en_us/t/how-to-add-multi-users/\">here</a> to view steps to add the subuser.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228550', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('11', '14', 'Stop receiving order confirmation emails', 'stop-receiving-order-confirmation-emails-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Stop receiving order confirmation emails</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Stop receiving copies of <a href=\"/en_us/t/order-confirmation-customization\">order confirmation emails</a> that attendees receive</li>\r\n                                <li>\r\n                                    Applies to all events in your account</li>\r\n                                <li>\r\n                                    Subuser email preferences are controlled by the admin account holder</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re a subuser on an Eventbrite account, contact your admin account holder to disable email confirmations for you, as described <a href=\"/en_us/t/multiuser-account-access\">here</a>.&nbsp;Only an account admin can disable email confirmations for subuser accounts.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To stop receiving an order confirmation email each time someone registers for your event, first <a href=\"https://www.eventbrite.com/login/\">log in</a> to your Eventbrite account. Then navigate to the <strong>Email Preferences</strong> section in the <strong>Account</strong> tab, by clicking <a href=\"https://www.eventbrite.com/email-preferences\">here</a>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Uncheck the <strong>Notify me when attendees order tickets</strong> option. Then click <strong>Save</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From this point forward, you won\'t receive a copy of the order confirmation emails for any event in your account. (Your attendees will continue to receive these emails after ordering.) You can always reenable this option if you change your mind at any point in the future!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228563', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('12', '14', 'Language and region settings', 'language-and-region-settings-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Language and region settings</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Localized formatting for dates, times and language</li>\r\n                                <li>\r\n                                    Available for the UK, Ireland, Canada, Australia, New Zealand, France, Spain, Germany, Netherlands, Italy or Portugal</li>\r\n                                <li>\r\n                                    <a href=\"/en_us/t/charge-tax-on-tickets-limited-availability\">Tax support</a> to add tax to your ticket price</li>\r\n                                <li>\r\n                                    Showcase of events in your region on our homepage</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can switch back and forth between our US (.com) and any of our localized sites at any time. See below to learn how to make the switch!</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align:center\">\r\n                            <br>\r\n                            <span style=\"font-size: large;\"><strong>Here\'s how to switch your account to your region!</strong></span><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If we detect that you’re connecting to Eventbrite from an IP address in one of the countries listed above, you’ll see a banner at the top of the page prompting you to switch to that particular site. All you need to do is click <strong>Switch me!</strong>. That’s it!</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-switch-to-our-UK-site/banner.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You can click <strong>Not now</strong> or the grey <strong>x</strong> on the right side of the banner to hide it if you decide you’d like to stay on our US site. (If you decide later that you’d like to switch to your localized site, see step 2 below for how to do so.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also switch between our US and other, localized sites at any time by clicking the region shown in the grey footer at the bottom of any page in your Eventbrite account.</p>\r\n                                <p>\r\n                                    <img style=\"width: 525px; height: 190px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-switch-to-our-UK-site/footer22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the popup window, choose your region and language. None of your event information will be lost when you switch regions or languages!</p>\r\n                                <p>\r\n                                    <img style=\"width: 525px; height: 364px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-switch-to-our-UK-site/selection.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228581', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('13', '14', 'Email preferences', 'email-preferences-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Email preferences</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Set your email preferences from within your account</li>\r\n                                <li>\r\n                                    Control receipt of Eventbrite newsletters, updates, and notifications</li>\r\n                                <li>\r\n                                    Manage invitations and attendee emails sent by event organizers</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p style=\"text-align:center\">\r\n                            <span style=\"font-size: large;\"><strong>Managing your email preferences</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To manage your email preferences, <a href=\"https://www.eventbrite.com/login\">log in</a> to your Eventbrite account, then select the <strong>Account</strong> tab located on the top right.</p>\r\n                                <p>\r\n                                    <img src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-unsubscribe/header_bar.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then click on <strong>Email Preferences</strong> in the menu on the left hand side.</p>\r\n                                <p>\r\n                                    <img src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-unsubscribe/Email_Preferences.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Select the communications you wish to receive or deselect the ones you no longer want to receive, and click&nbsp;<strong>Save Preferences </strong>at the bottom of the page.</p>\r\n                                <p>\r\n                                    <img src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-unsubscribe/total_email_preferences.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <p>\r\n                                    <br>\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></p>\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The options displayed in the above images may differ from the options available to you depending on whether you are an event attendee or an event organizer on Eventbrite.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align:center\">\r\n                            <span style=\"font-size: large;\"><strong>Eventbrite newsletters, updates and notifications</strong></span></p>\r\n                        <br/>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To unsubscribe from a newsletter or updates and notifications from Eventbrite, look for the link at the bottom of the email you prefer not to receive, then click through and confirm. Keep in mind that you can also turn these on or off in your <strong>Email Preferences</strong> as detailed above. This will <strong>not</strong> affect your ability to receive email invitations from event organizers.</p>\r\n                                <p>\r\n                                    <img width=\"600\" height=\"342\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-unsubscribe/newsletter.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <br/>\r\n                        <p style=\"text-align:center\">\r\n                            <span style=\"font-size: large;\"><strong>Email invitations sent by a particular event organizer</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    At the bottom of each email invitation, you\'ll find a link to unsubscribe you from receiving any more invitations from the event organizer who sent you that email invitation. You will still be able to receive email invitations from other organizers, as well as emails from us at Eventbrite.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-unsubscribe/email.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align:center\">\r\n                            <br>\r\n                            <span style=\"font-size: large;\"><strong>All invitations and attendee emails sent by any event organizer</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You will no longer receive email invitations or attendee emails from <strong>any</strong> event organizer. You can unsubscribe <a href=\"http://www.eventbrite.com/inviteunsubscribe?\r\n\r\n                                                                                                                                                                      oclass=0\">here</a>. This will <strong>not</strong> affect your ability to receive emails sent from us at Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228589', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('14', '14', 'Reset password', 'reset-password', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Reset password</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Easily reset your account password</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Visit <a href=\"https://www.eventbrite.com/resetpassword\">this page</a> to get started.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter your Eventbrite account\'s email address in the text box, then click the <strong>Reset Password</strong> button. A password-reset email will be sent to that email address.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Check your email inbox and open the email from Eventbrite with the subject line <strong>Your new Eventbrite password</strong>. Click the <strong>Set a new password</strong> button in the email.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll now be taken back to Eventbrite to enter and verify your new password. Click the <strong>Reset password</strong> button when you\'re finished.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228596', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('15', '14', 'Make a subuser the account admin', 'make-a-subuser-the-account-admin', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Make a subuser the account admin</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Convert a subuser on an account to the account admin</li>\r\n                                <li>\r\n                                    Useful if the account admin has left your organization</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> An email address cannot be both the admin and a subuser on an account. To make a subuser email address the admin email address on an account, follow the directions below!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Log in to the admin account and click on the<strong> Account</strong> tab located at the top right side of the page and select <strong>Multi-User Access </strong>on the left hand side.</p>\r\n                                <p>\r\n                                    <a href=\"http://eventbrite.com/resetpassword\"><img width=\"616\" height=\"358\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/making-a-subuser-the-administrator-on-an-account/subusertomainadmin1.png\"></a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Are you not the admin on the account? Maybe the admin has left your organization? In that case, you\'ll need to get the admin user\'s account password so you can log in to their account--as detailed in step 1 above--and then continue on to the next steps below.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Delete</strong> next to the subuser email that is going to become the main administrator.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"202\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/making-a-subuser-the-administrator-on-an-account/subusertomainadmin2.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Clicking on <strong>Delete</strong> next to the subuser\'s email completely closes the Eventbrite account under that subuser\'s email. Before deleting the subuser\'s email, you may want to alert the subuser in case they ordered tickets through our site or have any other information to save before their account is completely closed.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    While still in the <strong>Account</strong> tab, click on the <strong>Contact Info</strong> link on the left side. Next to the current account email address, click on <strong>Change</strong>, enter the desired email address, the current password for the main account, and then click save.</p>\r\n                                <div class=\"step\">\r\n                                    <img width=\"583\" height=\"195\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/making-a-subuser-the-administrator-on-an-account/subusertomainadmin5.png\">\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you receive an error message saying that the new email address is already registered with Eventbrite, click <a href=\"/en_us/t/subuser-already-registered-error\">here</a> to learn what to do.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Congrats! The subuser\'s email is now listed as the main account email address.</p>\r\n                                <div class=\"step\">\r\n                                    &nbsp; <img width=\"567\" height=\"67\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/making-a-subuser-the-administrator-on-an-account/subusertomainadmin6.png\">\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can always change the password on the account by clicking on the <strong>Password</strong> link on the left side of the page when you\'re in the <strong>Account</strong> tab.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228605', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('16', '14', 'The Account tab', 'the-account-tab', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>The Account tab</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Access much of your account information in one place</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            <img width=\"433\" height=\"59\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/account_tab.png\" alt=\"Eventbrite Account Tab\"></p>\r\n                        <p>\r\n                            Here are all the things you can do from your <b>Account </b>tab:</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Change your account email address. Add and update contact information about yourself. This is the information that will be auto-filled in the registration form if you\'re logged in while registering for an event.</p>\r\n                                <img width=\"612\" height=\"267\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/basic_info.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div><div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Change your account password.</p>\r\n                                <img width=\"538\" height=\"228\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/password.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div><div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    View and pay <a href=\"/en_us/t/invoicing-explanation-and-payment\">invoices</a>.</p>\r\n                                <img width=\"619\" height=\"151\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/invoices.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Set your email preferences. You can receive a copy of each order confirmation for your events, the Eventbrite newsletter, and the Eventbrite Events (attendee) newsletter.</p>\r\n                                <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/email_prefs.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Manage your payment accounts. These are all of the PayPal and Google Checkout accounts that you have used in the past to collect money for events. You can also specify a PayPal account you would like to receive affiliate payouts to if you are an Eventbrite affiliate.</p>\r\n                                <img width=\"617\" height=\"241\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/payment_accounts.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Manage and add <a href=\"/en_us/t/multiuser-account-access\">multi-users on your account</a>. You can specify the events and actions that users can access within your account.</p>\r\n                                <img width=\"619\" height=\"246\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/multi_user.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Manage and clean up unused venue and organizer records. When you create a venue or organizer record but don\'t use it for any of your events, we list those here so you can delete them if desired.</p>\r\n                                <img width=\"490\" height=\"404\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/unused_venues.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Sign up for and manage your referral links for the <a href=\"/en_us/t/our-referral-program\">Eventbrite referral program</a>.</p>\r\n                                <img width=\"616\" height=\"202\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/affiliate.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Sign up for and manage your affiliate links for event-specific affiliate programs.</p>\r\n                                <img width=\"621\" height=\"306\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/affiliate%20program.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Obtain your unique API user key.</p>\r\n                                <img width=\"618\" height=\"248\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/api_user_key.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Close your Eventbrite account. Let us know why you\'re leaving before you go!</p>\r\n                                <img width=\"619\" height=\"268\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/account_tab/close_account.png\" alt=\"The Account Tab\">\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228611', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('17', '14', 'Locate API user key', 'locate-api-user-key', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Locate API user key</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    For use with third-party apps or tools</li>\r\n                                <li>\r\n                                    Unique key for your account</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To find your API User Key, log in to your Eventbrite account and follow this URL: <a href=\"http://www.eventbrite.com/userkeyapi\">http://www.eventbrite.com/userkeyapi</a><strong style=\"text-align: center; font-size: medium; \">&nbsp;</strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align:center\">\r\n                            <span style=\"font-size: medium;\"><strong>You can also follow these steps:</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Log in to your Eventbrite account.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on <strong>Account</strong> in the upper right.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on <strong>API User Key</strong> on the left.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228617', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('18', '14', 'Safe Harbor (EU)', 'safe-harbor--eu-', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Safe Harbor (EU)</h3>\r\n                            \r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        Eventbrite participates in both the EU-US and Swiss-US Safe Harbor Frameworks. You can find out more about Eventbrite’s participation by reviewing our privacy policy, located\r\n                        <a href=\"http://www.eventbrite.com/privacypolicy\">here</a>\r\n                        <!--step-->', '1349228638', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('19', '14', 'Privacy policy, and security and safety information', 'privacy-policy--and-security-and-safety-information', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Privacy policy</h3>\r\n\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        You can find our Privacy Policy by clicking\r\n                        <a href=\"http://www.eventbrite.com/privacypolicy/\">here</a>\r\n                        <!--step-->', '1349228674', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('20', '14', 'Terms of Service (TOS)', 'terms-of-service--tos-', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Terms of Service (TOS)</h3>\r\n\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        To view our Terms of Service, click\r\n                        <a href=\"http://www.eventbrite.com/tos/\">here</a>\r\n                        <!--step-->', '1349228697', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('21', '14', 'How to find the event ID', 'how-to-find-the-event-id', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>How to find the event ID</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    For use with our API or a third-party widget</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To find the event ID for an event in your account, first log in to Eventbrite and navigate to the <strong>Manage</strong> page for your event by clicking on <strong>My Events</strong> at the top of the screen and then on the name of your event.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once on the <strong>Manage </strong>page, look in the address bar of your browser at the URL for the page you\'re on. You\'ll see the following in the URL: <strong>eclass=XXXXXXXXXX</strong>. This ten-digit number after <strong>eclass= </strong>is the event ID.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You\'ll see that <strong>eclass=XXXXXXXXXX </strong>also appears in the URL for any event\'s <strong>Edit</strong> or <strong>Event Details </strong>page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228705', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('22', '15', 'iPad ticket sales at the door', 'ipad-ticket-sales-at-the-door', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>iPad ticket sales at the door</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Credit card or cash sales at the door</li>\r\n                                <li>\r\n                                    Attendee names and email addresses collected</li>\r\n                                <li>\r\n                                    Access total sales snapshot on iPad</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div style=\"text-align: center;\">\r\n                            <a href=\"http://itunes.apple.com/us/app/at-the-door/id488236441?ls=1&amp;amp;mt=8\"> <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/AppStoreButton.jpg\" alt=\"\"></a>\r\n                        </div>\r\n                        <br/>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Important Note: </strong>At this time, you can only sell tickets using At The Door for non-<a href=\"/en_us/t/repeating-events\">repeating events</a> priced in USD that are using our default <a href=\"/en_us/t/credit-card-processing-faq\">credit card processing</a>.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Create At The Door ticket types</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’ve already created an event, click <strong>My Events</strong> at the top of the screen. Then click on <strong>Edit</strong> to the right of your event’s name to go to the <strong>Event Details</strong> page.<br>\r\n                                    <img width=\"596\" height=\"258\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atdedit.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You must be using our <a href=\"/en_us/t/more-info-about-our-new-design\">new design</a> in order to create At The Door ticket types, and then use our At The Door app to sell tickets on your iPad at your event.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Event Details</strong> page, go to <strong>Step 2: Create Tickets</strong> to create an <strong>At The Door</strong> ticket type.</p>\r\n                                <p>\r\n                                    Click the <strong>Add Ticket</strong> button to create a new <strong>Paid</strong> or <strong>Free</strong> ticket type.&nbsp;</p>\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name</strong>, <strong>Ticket Quantit</strong><strong>y</strong>, and <strong>Ticket Price </strong>for that ticket type.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>At The Door - On Sale</strong> from the <strong>Sales Status</strong> dropdown menu for that ticket type, as shown in the image below. <img width=\"598\" height=\"286\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/ATD1.png\" alt=\"How to create ticket types\"></p>\r\n                                <p>\r\n                                    Click <strong>Save</strong> in the upper right to save your changes.</p>\r\n                                <p>\r\n                                    Ticket types with a <strong>Sales Status</strong> of <strong>At The Door - On Sale</strong> (or <strong>At The Door - Hidden</strong>) will not be viewable on your event page online. (To sell the same ticket on your online event page, you must create a ticket type of the same name, but choose <strong>On Sale</strong> from the <strong>Sales Status</strong> dropdown menu.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you want to hide your At The Door ticket type so it doesn\'t appear on the iPad app--e.g., if you\'re having a two-day event and you want to hide the second day\'s ticket type(s)--select&nbsp;<strong>At The Door - Hidden</strong> from the <strong>Sales Status </strong>column for that ticket type.</p>\r\n                                <p>\r\n                                    To make the ticket type appear later, select <strong>At The Door - On Sale</strong> from the <strong>Sales Status </strong>dropdown menu and save your changes.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Repeat step #2 above to create multiple ticket types to sell on your iPad using At The Door. Once you\'ve created all of your <strong>At the Door</strong> ticket types, you\'re ready to start selling tickets on your iPad!</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Get started with At The Door</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To sell tickets on-site at your event, download our At The Door iPad app <a href=\"http://itunes.apple.com/us/app/at-the-door/id488236441?ls=1&amp;amp;mt=8\">here</a>.</p>\r\n                                <p>\r\n                                    On your iPad, tap on the <strong>At the Door</strong> app icon and log in using the same email and password that you use to log in to your Eventbrite account.</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-login1.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once logged in, tap the calendar icon in the lower left and select the event that you want to sell tickets to--and you\'re ready to sell!</p>\r\n                                <p>\r\n                                    (Only events with <strong>At The Door</strong> ticket types will appear in the app. If your event doesn\'t appear, be sure to create at least one <strong>At The Door</strong> ticket type by following the instructions beginning with step #1 above.)</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-findevent.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To control <strong> </strong><a href=\"/en_us/t/multiuser-account-access\">multi-user (subuser) access</a> to At The Door, first log in to the admin account on Eventbrite.com. Click <strong>Account</strong> in the upper right, then click <strong>Multi-User Access</strong>. Click&nbsp;<strong>Edit</strong> next to a subuser\'s email address and, in the <strong>Actions this user can perform</strong> section, check the boxes next to <strong>Access Eventbrite At The Door</strong> and <strong>Manage orders and attendees</strong>.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Sell tickets for cash or credit</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the main <strong>Sell Tickets </strong>screen in the iPad app, simply tap the<strong> ticket buttons</strong> (e.g., <strong>General Admission</strong>, <strong>VIP</strong> or <strong>Und</strong><strong>er 21</strong> in the image below) to add those ticket types to your cart. You can tap the buttons multiple times to add multiple tickets to the order, or you can add and subtract tickets by using the <strong>+</strong>&nbsp;and <strong>-</strong> buttons next to the ticket quantity.&nbsp;</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-addtickets.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Tap the <strong>Cash</strong> button in the upper right to process a cash sale. Enter the amount tendered to display the change that\'s due, or just fly through by tapping <strong>Paid</strong> without entering anything.<strong>&nbsp;</strong></p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <strong><img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-paycash.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>Cash sales processed using At The Door are&nbsp;<strong>not</strong> charged a fee--they are <strong>completely free</strong>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If an attendee is using a credit card to pay, tap the <strong>Credit</strong> button and enter the attendee’s credit card information using the on-screen keypad. Once you\'ve entered all the required information (number, expiration date, CVV), tap <strong>Process</strong>.&nbsp;</p>\r\n                                <p>\r\n                                    To swipe a credit card instead of keying in the card number, check out our <a href=\"/en_us/t/credit-card-reader-for-ipad\">At The Door Card Reader</a>!</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-processcard.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Credit card orders processed using At The Door are <strong>not</strong> charged the 2.5% + $0.99 Eventbrite fee, only the 3% credit card processing fee. This credit card processing fee will be included in the total, and cannot be passed on to the buyer--this is a legal requirement.</p>\r\n                                <p>\r\n                                    If the buyer’s total is more than $25, they’ll be asked to sign on the iPad using their finger. After they sign, tap <strong>Confirm</strong> to process the payment.</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-signbelow.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re processing an order that includes only free tickets, tap the&nbsp;<strong>Assign</strong> button in the upper right to finish processing the order.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Transactions cannot be processed without an Internet connection. If you lose Internet connectivity while on the <strong>Sell</strong> <strong>Tickets</strong> screen, the <strong>ticket buttons</strong> will become untappable until the Internet connection is restored. If you lose Internet connectivity in the middle of a transaction, an <strong>alert</strong> will pop up to notify you.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Collect attendee information</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After taking payment, you have the option to capture the buyer’s name and email. This information will appear on their order and in all reporting on Eventbrite.com.</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-collectinfo.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you’d prefer not to collect this information, go to <strong>Settings</strong>, found on the left sidebar of the app,<strong> </strong>to turn off this feature.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Email or print receipt</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you didn’t collect the attendee’s email for your own purposes, you can still send them an <strong>email receipt</strong>. This email isn\'t saved; it is just used to send the receipt. &nbsp;</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <strong><img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-receipt.png\" alt=\"\"></strong></p>\r\n                                <p>\r\n                                    To learn how to print tickets and/or a receipt from a supported printer, go <a href=\"/en_us/t/print-tickets-and-receipts-with-ipad\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Quick Stats</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    This section was designed to give you a quick glance at the sales activity for the currently logged-in user on the current iPad on the current day. Its most common use is to reconcile a cash box at the end of (or throughout) an event.&nbsp;</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <strong><span style=\"font-size: large;\"><img width=\"500\" height=\"415\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/atd-quickstats.png\" alt=\"\"></span></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> <a href=\"/en_us/t/repeating-events\">Repeating events</a> currently aren\'t supported by At The Door. If you have repeating events, you must create a new non-repeating event for your At The Door tickets. (After your event, you can run a <a href=\"/en_us/t/multipleevent-reports\"><strong>Cross Event Report</strong></a> to capture and/or export all attendee information for the two [or more] events.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> To provide secure log-in to the app, we require that your device run iOS4.2.1 or above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div style=\"text-align: center;\">\r\n                            <a href=\"http://itunes.apple.com/us/app/at-the-door/id488236441?ls=1&amp;amp;mt=8\"> <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/atd-ipad-app/AppStoreButton.jpg\" alt=\"\"></a></div>\r\n                        <!--step-->', '1349228854', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('23', '15', 'Email reminders for attendees', 'email-reminders-for-attendees', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Email reminders for attendees</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Message already-registered attendees</li>\r\n                                <li>\r\n                                    Great for FAQs like restrictions and transit info</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can only email people who have already registered for your event. If you would like to send an email to those invited, but not yet registered, feel free to send out additional <a href=\"/en_us/t/email-invitations\">email invitations</a>.</p>\r\n                                <p>\r\n                                    By default, Eventbrite sends a 48-hour reminder email to your attendees. While you cannot edit its text, you can delete this automated reminder and replace it with a reminder email of your own design, as detailed below. To disable these automated 48-hour reminder emails for all events in your account, please <a href=\"http://www.eventbrite.com/contact-us/\">contact us</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.<br>\r\n                                    Go to your event\'s&nbsp;<strong>Manage</strong> page by clicking <strong>My Events</strong> at the top of the screen and then clicking on the name your event.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"272\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/manage.png\"></p>\r\n                                <p>\r\n                                    From the <strong>Manage</strong> page, choose <strong>Email Attendees</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/features2.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click the <strong>Create New Attendee Email</strong> button to create a new message.</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"235\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/create.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can customize a number of the fields, like&nbsp;<strong>Name</strong>, <strong>Reply-To Email</strong> (this is the email address where any replies to your email will be sent), and <strong>Subject</strong>. Select whom you want to send the email to using the <strong>To</strong> dropdown menu.</p>\r\n                                <p>\r\n                                    Feel free to highlight and delete the pre-filled text in the <strong>Message</strong> field to create customized wording!</p>\r\n                                <p>\r\n                                    If your event uses <a href=\"/en_us/t/order-confirmation-customization\">PDF tickets</a>, you can also include a link to print the tickets in the attendee email.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you have any files--e.g., slides or a presentation--to send to your recipients, you can upload these files to an outside site and then include in your email a link to download the files. Unfortunately, it isn\'t possible to attach files directly to an email to your attendees.</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"569\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/message.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>Send Email</strong> section, select when you\'d like to send the email: now, a specific date/time, or a relative date/time to the start of the event.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"210\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/when.png\" alt=\"How to schedule and send automated email reminders\"></p>\r\n                                <p>\r\n                                    Back on the main&nbsp;<strong>Email Attendees</strong> screen, you can easily&nbsp;<strong>copy </strong>or&nbsp;<strong>delete</strong> any email that you have scheduled to send, including the automated 48-hour reminder email.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"227\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/schedule.png\" alt=\"How to schedule and send automated email reminders\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228861', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('24', '15', 'Android for check-in', 'android-for-check-in', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Android for check-in</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Check in attendees on your phone</li>\r\n                                <li>\r\n                                    Scan ticket barcodes or check in by name</li>\r\n                                <li>\r\n                                    Scans barcodes from Android and iPhone Eventbrite apps for attendees</li>\r\n                                <li>\r\n                                    Works with multiple devices and/or laptops</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Does your team have Android and iPhone devices? Your iPhone owners can download Entry Manager from Apple’s App Store. All check-ins and data will sync seamlessly across both platforms.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 464px; height: 336px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/android-easy-entry-app/hand-long.png\" alt=\"\"><br>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: large;\"><strong>Getting started</strong></span><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now that you\'ve created an event and sold tickets, go to Google Play on your Android device and search for and download <strong>Eventbrite Entry Manager</strong>, which has this logo:</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img width=\"43\" height=\"43\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/android-easy-entry-app/entry-manager-android-logo.png\" alt=\"\"></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    (Or you can visit <a href=\"http://www.eventbrite.com/entrymanager\">this page</a> for a direct link and a better look at the app.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you\'re ready to check in your guests at the door of the event, log in to the app using your Eventbrite account\'s log-in ID and password. From the list of your live events that\'s shown, choose the event for which you\'d like to check in your guests or view real-time stats.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong><a href=\"/en_us/t/multiuser-account-access\">Multi-user (subuser) access</a> to Entry Manager is set in the admin account by clicking <strong>Account</strong> in the upper right, then clicking <strong>Multi-User Access</strong>, then clicking <strong>Edit</strong> next to a subuser\'s email address, and, in the <strong>Actions this user can perform </strong>section, checking the box next to <strong>Check in attendees</strong>.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">View real-time ticket sales and check-in stats</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access real-time sales stats for your event, tap <strong>Stats</strong>. <strong>Tickets Sold</strong> displays the number of tickets sold out of the total number of tickets available. <strong>Attendance</strong> displays the total number of attendees that have been checked in across all devices in use.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Check in attendees by name</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To start checking in your attendees, tap <strong>Check In</strong> to view your attendee list. Scroll to find the attendee you\'re looking for, or tap the search button at the bottom to look them up by name. You can also access the search through the menu button as well.</p>\r\n                                <p>\r\n                                    To check in an attendee, simply tap their name, then tap the green <strong>Check In</strong> button that pops up. The attendee\'s name will then gray out. To undo a check-in, tap the name of a checked-in attendee, then tap the red <strong>Undo Check In</strong> button.</p>\r\n                                <p>\r\n                                    The number at the top of the screen reflects the check-in count on that particular device. If all check-in devices are connected to the Internet, you can get the total attendance directly from our servers on the <strong>Stats</strong> screen.</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 268px; height: 469px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/android-easy-entry-app/ent-android-searchlist.png\" alt=\"\"><br>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Check in attendees using the barcode scanner</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Tap the<strong> 2D barcode icon</strong> in the top action bar to launch the barcode scanner. You can also open the scanner through the Menu button at the bottom. The barcode scanner uses the device\'s camera to detect the barcode. Simply center the barcode inside the viewfinder and wait for the feedback:<strong> valid</strong> (let them in!), <strong>duplicate</strong> (ticket has already been used) or <strong>invalid </strong>(fake!).</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 472px; height: 371px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/android-easy-entry-app/ent-android-scan-options.png\" alt=\"\"></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    As a reminder, the number at the top of the screen reflects the check-in count on that device only.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Options and other notes</span></strong><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you have multiple ticket types for your event and would like to limit entry to certain ticket types, tap the menu button and select <strong>Limit Entry</strong>. Choose the <a href=\"/en_us/t/ticket-type-creation\">ticket type(s)</a> that you want to be active on that device. Attendees having other ticket types will be hidden from the attendee list, and their barcodes will scan as <strong>Limited Entry</strong>. This feature would be useful if, for example, you had a separate entrance for your VIP ticket holders.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Should you lose your connection to the Internet during check-in, you will receive a warning from the app, but you can continue to check in attendees offline. All your check-ins will automatically sync once your Internet connection is restored.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Scanning barcodes can severely reduce your battery life. Battery life ranges depending on your device and the number of applications running in the background. Performance, particularly with scanning, also ranges significantly depending on the device. For example, the Nexus S will scan a barcode in less than 1 second, but the Droid X can take up to 5 seconds.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> For events larger than 1,000 attendees, performance may be slower depending on your device.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Entry Manager is currently compatible with Android versions 2.2.x, 2.3.x and - 4.0.x.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228868', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('25', '15', 'iPhone for check-in', 'iphone-for-check-in', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>iPhone for check-in</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Check in attendees on your phone</li>\r\n                                <li>\r\n                                    Scan ticket barcodes or check in by name</li>\r\n                                <li>\r\n                                    Scans barcodes from Android and iPhone Eventbrite apps for attendees</li>\r\n                                <li>\r\n                                    Works with multiple devices and/or laptops</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Does your team have iPhone and Android devices? Your Android owners can download Entry Manager from <a href=\"https://play.google.com/store/apps/details?id=com.eventbrite.organizer\">Google Play</a>. All check-ins and data will sync seamlessly across both platforms.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: medium;\"><strong>Here\'s a handy tutorial video (or find written instructions below):</strong></span></p>\r\n                        <br>\r\n                        <p style=\"text-align: center;\">\r\n                        <div bginactive=\"url(chrome://flashblock/content/flash.png) no-repeat center\" bgactive=\"url(chrome://flashblock/content/flashplay.png) no-repeat center\" style=\"background: url(\'&quot;chrome://flashblock/content/flash.png\') no-repeat scroll center center transparent ! important; min-width: 32px ! important; min-height: 32px ! important; width: 555px; height: 390px; border: 1px solid rgb(223, 223, 223); cursor: pointer; overflow: hidden; display: inline-block; visibility: visible ! important; -moz-box-sizing: border-box; margin-right: auto; margin-left: auto;\" title=\"http://www.youtube.com/v/hOqWrQp60rE?version=3&amp;hl=en_US&amp;rel=0\" dataattribute=\"http://www.youtube.com/v/hOqWrQp60rE?version=3&amp;hl=en_US&amp;rel=0\" tabindex=\"0\" role=\"button\"></div></p>\r\n                        <p>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: large;\"><strong>Getting started</strong></span><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now that you\'ve created an event and sold tickets, go to the App Store on your iPhone or iPad and search for and download <strong>Eventbrite Entry Manager</strong>, which has this logo:</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img width=\"43\" height=\"43\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/entry-manager-ios-logo.png\" alt=\"\"></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    (Or you can visit <a href=\"http://www.eventbrite.com/entrymanager\">this page</a> for a direct link and a better look at the app.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you\'re ready to check in your guests at the door of the event, log in to the app using your Eventbrite account\'s log-in ID and password. From the list of your live events that\'s shown, choose the event for which you\'d like to check in your guests or view real-time stats.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong><a href=\"/en_us/t/multiuser-account-access\">Multi-user (subuser) access</a> to Entry Manager is set in the admin account by clicking <strong>Account</strong> in the upper right, then clicking <strong>Multi-User Access</strong>, then clicking <strong>Edit</strong> next to a subuser\'s email address, and, in the <strong>Actions this user can perform </strong>section, checking the box next to <strong>Check in attendees</strong>.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">View real-time ticket sales and check-in stats</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access real-time sales stats for your event, tap <strong>Stats</strong>. <strong>Tickets Sold</strong> displays the number of tickets sold out of the total number of tickets available. <strong>Attendance</strong> displays the total number of attendees that have been checked in across all devices in use.<br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Check in attendees by name</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To start checking in your attendees, tap <strong>Check In</strong> to view your attendee list. Scroll to find the attendee you\'re looking for, or use the search bar at the top to look them up. To check in an attendee, simply tap their name, then tap the green <strong>Check In</strong> button. The attendee\'s name will then gray out. To undo a check-in, tap the name of a checked-in attendee, then tap the red <strong>Undo Check In</strong> button.</p>\r\n                                <p>\r\n                                    The number at the top of the screen reflects the check-in count on that particular device. If all check-in devices are connected to the Internet, you can also get the total directly from our servers on the <strong>Stats</strong> screen.</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 252px; height: 454px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/ent-ee-searchlist.png\" alt=\"\"></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If your event page has <a href=\"http://help.eventbrite.com/customer/en_us/portal/articles/426190\">offline payments</a> enabled and an attendee chose to pay offline, you\'ll notice a message that lets you know that a payment is due.</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 252px; height: 454px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/iphonenotpaid.png\" alt=\"\"></p>\r\n                        <p>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Check in attendees using the barcode scanner</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Tap <strong>Scan</strong> in the bottom tab bar to launch the barcode scanner. The barcode scanner uses the device\'s camera to detect the barcode. Simply center the barcode inside the viewfinder and wait for the feedback:<strong> valid</strong> (let them in!), <strong>duplicate</strong> (ticket has already been used) or <strong>invalid </strong>(fake!).</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <img style=\"border-width: 0px; border-style: solid; width: 406px; height: 470px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/ent-ee-scanscreen.png\" alt=\"\"></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    As a reminder, the number at the top of the screen reflects the check-in count on that device only.</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Options and other notes</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you have multiple ticket types for your event and would like to limit entry to certain ticket types, tap <strong>Options</strong> in the bottom tab bar and then tap<strong> </strong><strong>Ticket Types</strong>. Select the ticket type(s) that you want to be active on that device.</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 252px; height: 454px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/iphoneoptions.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Attendees having other ticket types will be hidden from the attendee list, and their barcodes will scan as <strong>Limited Entry</strong>. This feature would be useful if, for example, you had a separate entrance for your VIP ticket holders.<br>\r\n                                    <br>\r\n                                    If payment is due on a ticket that you scan or tap to check in, you will be prompted to verify that the attendee has paid.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Should you lose your connection to the Internet during check-in, you will receive a warning from the app, but you can continue to check in attendees offline. All your check-ins will automatically sync once your Internet connection is restored.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Feature and device compatibility </span></strong><br>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\"><img width=\"508\" height=\"166\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/easy-entry-iphone-app/grid3.png\" alt=\"\"></span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Scanning barcodes can severely reduce your battery life. On the iPhone 4, you can scan for up to 4 hours continuously without the LED flash, and for only 2 hours continuously if using the LED flash. On the iPod Touch 4, you can scan continuously for up to 2.5 hours. You can extend your scanning time by purchasing external battery packs from the Apple Store.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> To provide secure log in to the app, we require that your device run iOS4.2.1 or above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228875', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('26', '15', 'Print name badges', 'print-name-badges', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Print name badges</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Generate and print attendee name badges</li>\r\n                                <li>\r\n                                    More info about customization options outside of Eventbrite, like mail merges</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> In order to use the <strong>Print Name Badges</strong> feature, you must have <a href=\"http://get.adobe.com/flashplayer/\">Adobe Flash</a> and <a href=\"http://get.adobe.com/reader/\">Adobe Reader</a> installed on your computer.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the name badge tool, from the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage</strong> page, select <strong>Print Name Badges</strong> from the features menu on the left side of the page. <img border=\"0\" title=\"Print Name Badges\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_badges/features%20menu.png\" alt=\"Print Name Badges\"></p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose the set of attendees for whom you would like to print badges. You can choose <strong>All Unique Attendees, Specific Attendees</strong> (choose names from a list), or <strong>Attendees by Ticket Type </strong>(attendees who registered for a particular ticket type). <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_badges/select%20attendees.png\" alt=\"How to customize and print name badges\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Then choose from a list of the available commonly used <strong>Avery </strong>badge label templates. <img width=\"611\" height=\"207\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_badges/badge%20style.png\" alt=\"How to customize and print name badges\"></p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>For more information about the different label/badge styles and to order online, go to the <a href=\"http://www.avery.com/\">Avery website</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Customize the layout of the name badge by choosing which information should appear where on the badge. You can also change the font and alignment. <img width=\"609\" height=\"171\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_badges/customize%20layout.png\" alt=\"How to customize and print name badges\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you are happy with the preview of the name badge, click the <strong>Generate Badges</strong> button and a PDF file will generate for you to open and print onto Avery badge labels.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re using a Mac, you\'ll need to click <strong>Generate Badges</strong> to preview the name badges, as the PDF preview pane cannot display in a Mac environment. <img width=\"610\" height=\"308\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_badges/preview.png\" alt=\"How to customize and print name badges\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'d like to include a logo on your name badges, we recommend using the Mail Merge feature in Microsoft Word. To run a mail merge, first export an <strong><a href=\"/en_us/t/attendance-summary\">Attendee Summary</a></strong> report to Excel to use as your <strong>data source</strong>, and then run the mail merge in Microsoft Word.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228881', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('27', '15', 'Laptop for check-in', 'laptop-for-check-in', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Laptop for check-in</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Use one or more laptops to check in attendees at event</li>\r\n                                <li>\r\n                                    Internet connection needed</li>\r\n                                <li>\r\n                                    Works with our Entry Manager <a href=\"/en_us/t/iphone-for-checkin\">iPhone</a> and <a href=\"/en_us/t/android-for-checkin\">Android</a> apps</li>\r\n                                <li>\r\n                                    Go <a href=\"/en_us/t/disable-paper-tickets\">paperless</a>!</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the Entry Management tool from within your account, click on the title of your event on the <strong>My Events </strong>page to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Check In Attendees</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"233\" height=\"137\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/sidebar1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Multi-user (subuser) access to Entry Management is set in the admin account by clicking <strong>Account</strong> in the upper right, then clicking <strong>Multi-User Access</strong>. Then click <strong>Edit</strong> next to a subuser\'s email address, and, in the <strong>Actions this user can perform </strong>section, check the box next to <strong>Check in attendees</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    There are columns for check-in <strong>Status</strong>, <strong>Attendee Name </strong>(sorted by first name), <strong>Email</strong>, and <strong>Ticket Type</strong> that you can sort by clicking on the name of each column. The status bar at the top shows the number and percentage of attendees that are currently checked in.</p>\r\n                                <p>\r\n                                    You can also search for an attendee by their name or email address. The attendee list will automatically filter as you type in the search box.</p>\r\n                                <p>\r\n                                    <strong><img width=\"605\" height=\"526\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/interface.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click the green <strong>Check In </strong>button to check in an attendee. The button will then change to show <strong>Checked In</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"83\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/checked%20in.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    For events in which you are collecting basic information or information from the ticket buyer only, a dropdown menu showing the number of tickets checked in out of the total number of tickets for the order will display for multiple-ticket orders.</p>\r\n                                <p>\r\n                                    (Click <a href=\"/en_us/t/collect-information-from-attendees\">here</a> to learn more about the different options for collecting information from your attendees.)</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"82\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/multiple.png\" alt=\"How to refund an order or attendee\"></p>\r\n                                <p>\r\n                                    <strong>Checked In</strong> will show when all tickets in a multiple-ticket order have been checked in.</p>\r\n                                <p>\r\n                                    <img width=\"619\" height=\"82\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/2%20of%202.png\" alt=\"How to refund an order or attendee\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To check out an attendee that has already checked in, mouse over <strong>Checked In</strong> and click the gray <strong>Check Out</strong> that appears. A green <strong>Check In </strong>button will then show again, indicating that this attendee has been checked out.</p>\r\n                                <p>\r\n                                    <img width=\"621\" height=\"82\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/check%20out.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    For multiple-ticket orders in events in which you are collecting basic information or information from the ticket buyer only, click the gray <strong>Check Out </strong>button to check out one attendee.</p>\r\n                                <p>\r\n                                    You can then adjust the total number of attendees you would like to check out using the dropdown menu.</p>\r\n                                <p>\r\n                                    <img width=\"620\" height=\"84\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/multiple2.png\" alt=\"How to refund an order or attendee\"></p>\r\n                                <p>\r\n                                    \"0\" out of the total number of tickets in this order will again show when all tickets in a multiple-ticket order have been checked out.</p>\r\n                                <p>\r\n                                    <img width=\"620\" height=\"83\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/multiple.png\" alt=\"How to refund an order or attendee\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After your event, you can easily view detailed check-in information for your event by selecting <strong>Charts </strong>on the left side of the <strong>Manage</strong> page for that event.</p>\r\n                                <p>\r\n                                    <strong><img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/entry-management/charts.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228888', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('28', '15', 'Print check-in list', 'print-check-in-list', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Print check-in list</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Sortable, PDF check-in list</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Want to go paperless with your check-in? Check out our <a href=\"/en_us/t/laptop-for-checkin\">laptop check-in tool</a> or our Entry Manager <a href=\"/en_us/t/iphone-for-checkin\">iPhone</a> and <a href=\"/en_us/t/android-for-checkin\">Android</a> check-in apps--both meant to make your day-of-event entry management a breeze!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access your PDF check-in list, from the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Print Check-In List</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" title=\"Print Attendee Check-In List\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_guest_list/menu.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the way in which you would like the attendee list to be organized (by Last Name, First Name or Ticket Type). Then click the <strong>Download </strong>button to download or open this PDF file to Excel.</p>\r\n                                <img width=\"607\" height=\"223\" border=\"0\" title=\"Print Attendee Check-In List\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_guest_list/choose.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    A PDF of your attendee check-in list will open up or save on your computer. You can then easily print your check-in list to use at the entrance to your event.</p>\r\n                                <img width=\"610\" height=\"320\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/print_guest_list/check-in%20list.JPG\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can make sure that you have the most recent version of Adobe Reader by<br>\r\n                                    clicking <a href=\"http://www.adobe.com/downloads\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228894', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('29', '15', 'Print receipts and tickets with iPad', 'print-receipts-and-tickets-with-ipad', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Print receipts and tickets with iPad</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Supports <a href=\"http://www.starmicronics.com/Printer/PrinterDesc.aspx?PageId=1&amp;PrinterId=40\">Star TSP143L</a> printer only\r\n                                </li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Only the Star TSP143L printer is supported for use with the At The Door app at this time. Click <a href=\"http://www.cdw.com/shop/custompages/Default.aspx?CustomPageKey=0B9577C178334CBA9FA87F323513DB03\">here</a> for purchase information from our retail partner.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Download the app and configure your event</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first go <a href=\"/en_us/t/ipad-ticket-sales-at-the-door\">here</a> to download the At The Door app and learn how to configure your event for iPad sales.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Connect the printer</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Turn on the printer, and connect it to the router using an Ethernet cable. (The printer must be connected via an Ethernet cable to the router, or else it won\'t be detected by the iPad.) Then make sure that your iPad is connected to the same network created by that router.</p>\r\n                                <p>\r\n                                    <strong><img width=\"487\" height=\"390\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/connect-printer-to-at-the-door-ipad/atd-settings.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To connect your printer with the At The Door app, open the app and tap&nbsp;<strong>Settings</strong>, on the lower left side of the screen.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Tap <strong>Printing</strong>, and then turn on&nbsp;<strong>Printing</strong> to display the available printers in your network.&nbsp; <strong> </strong></p>\r\n                                <p>\r\n                                    <strong><img width=\"487\" height=\"390\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/connect-printer-to-at-the-door-ipad/atd-printing-off.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select your printer. A check mark will appear once you\'re connected. That\'s it!</p>\r\n                                <p>\r\n                                    <img width=\"487\" height=\"390\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/connect-printer-to-at-the-door-ipad/atd-printing-dialogue.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Sell tickets and print receipts and/or tickets</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve connected your printer, sell tickets as you normally would. Once a sale is processed, you\'ll be given the option to print tickets and a receipt, or just the tickets. (You can also choose to email a receipt instead or skip this step entirely.)</p>\r\n                                <p>\r\n                                    <img width=\"487\" height=\"390\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/connect-printer-to-at-the-door-ipad/atd-receipt-choice.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Printed tickets are fully scannable using our <a href=\"http://www.eventbrite.com/entrymanager/\">Entry Manager check-in app</a> for iPhone and Android.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349228900', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('30', '15', 'Credit card reader for iPad', 'credit-card-reader-for-ipad', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Credit card reader for iPad</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Swipe credit cards for payment on your iPad\r\n                                </li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Order a Card Reader</span></strong>\r\n                        </p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Visit the <a href=\"http://eventbritestore.com/\">Brite Store</a> to order your Card Reader! For more information about the At The Door Card Reader, check out our <a href=\"http://eventbritestore.com/pages/faqs\">FAQs page</a> in the Brite Store.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The At The Door Card Reader is 2 3/8 inches wide; 1 1/4 inches tall, excluding dock connector (1 1/2 inches tall including dock connector); and 1/2 inch thick.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Download the app and configure your event</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first go <a href=\"/en_us/t/ipad-ticket-sales-at-the-door\">here</a> to download the At The Door app and learn how to configure your event for iPad sales.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Connect the Card Reader and swipe credit cards</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To use the Card Reader, simply plug it into the iPad\'s power outlet.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you tap <strong>Credit</strong> to process a credit card sale, you\'ll see an indicator on the screen noting that the Card Reader is detected.</p>\r\n                                <p>\r\n                                    <strong><img width=\"487\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/set-up-at-the-door-card-reader/atd-swipe-screen.png\" alt=\"\"></strong></p>\r\n                                <p>\r\n                                    Simply swipe the credit card to take payment. If the total is over $25, the buyer will be asked to sign on the iPad using their finger. That\'s it!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349228907', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('31', '10', 'Discount codes & access codes', 'discount-codes---access-codes', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Discount codes & access codes</h3>\r\n                            <ul>\r\n                                <ul>\r\n                                    <li>\r\n                                        Offer discounts to your attendees</li>\r\n                                    <li>\r\n                                        Restrict access to hidden ticket types</li>\r\n                                    <li>\r\n                                        Bulk upload discount and access codes</li>\r\n                                    <li>\r\n                                        View and/or export code-usage data</li>\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Promotional codes are <strong>not</strong> case sensitive. This means that your attendees can enter in the discount or access code you provide to them without worrying about getting the capitalization of the code exactly right!</p>\r\n                                <p>\r\n                                    An attendee can only use one discount or access code per order. If you\'d like to offer access code users a discount, just set the price of the access code-protected hidden ticket to the discounted amount!</p>\r\n                                <p>\r\n                                    Now you can bulk upload up to 3,000 discount or access codes in a single .csv or .txt file!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To create or manage your discount and/or access codes, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    For an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"272\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2manage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage </strong>page, select <strong>Create Promotional Codes</strong> from the features menu on the left side of the page.</p>\r\n                                <br><p>\r\n                                    <img width=\"229\" height=\"173\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2features2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3a</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To create a <strong>discount code</strong>, first select <strong>Discount</strong> from the <strong>Code Type </strong>dropdown menu. (If you\'d like to create an access code, go to step 3b below.)</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"148\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2discount2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then enter the following information and click <strong>Save </strong>once you\'re finished:</p>\r\n                                <p>\r\n                                    -<strong> Code</strong> - This is the discount code that you\'ll provide to your attendees to use when they order. No two discount or access codes can be exactly the same; all codes must be unique.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you have many discount codes to create that will all have the same attributes (<strong>discount amount</strong>, <strong>uses</strong>, etc.), feel free to put all those codes into a .txt or .csv file (the latter using Microsoft Excel) and then upload the file into our system. Once you\'ve bulk uploaded the list of codes, you can then set the attributes for all of these codes in Eventbrite, as detailed directly below.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    -<strong> Discount Amount </strong>- The discount can either be a fixed amount, or a percentage off the ticket price.</p>\r\n                                <p>\r\n                                    -<strong> Applies To</strong> - Which of your ticket types this discount will apply to.</p>\r\n                                <p>\r\n                                    -<strong> Uses</strong> - The total number of tickets this discount code can be applied to.</p>\r\n                                <p>\r\n                                    -<strong> Starts</strong> - When this discount code will become valid.</p>\r\n                                <p>\r\n                                    -<strong> Ends</strong> - Until when this discount code will be valid.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"728\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2discount22.png\" alt=\"How to create and manage discount codes\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Once you\'ve created and saved a discount code, you can always click the name of the discount code while on the main <strong>Create Promotional Codes</strong> screen to see the details for that code. At the bottom of a particular <strong>Edit discount code</strong> screen, you\'ll see a link/URL with that discount code embedded in it. Anyone clicking this link will be taken to your event page, where the discount code will be auto-filled and ticket price(s) already reduced. You can provide this link to your attendees to make it even easier for them to register at the discounted price!</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"152\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2URL23.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3b</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To create an <strong>access code</strong>, first select <strong>Access</strong> from the <strong>Code Type </strong>dropdown menu. (If you\'d like to create a discount code, go to step 3a above.)</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"151\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2access.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then enter the following information and click <strong>Save </strong>once you\'re finished:</p>\r\n                                <p>\r\n                                    -<strong> Code</strong> - This is the access code that you\'ll provide to your attendees to enter before ordering to make the hidden ticket type appear. No two discount or access codes can be exactly the same; all codes must be unique.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you have many access codes to create that will all have the same attributes (<strong>discount amount</strong>, <strong>uses</strong>, etc.), feel free to put all those codes into a .txt or .csv file (the latter using Microsoft Excel) and then upload the file into our system. Once you\'ve bulk uploaded the list of codes, you can then set the attributes for all of these codes in Eventbrite, as detailed directly below.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    -<strong> Applies To</strong> - Which of your <strong>hidden</strong> ticket types this access code will apply to.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Access codes will only work with <strong>hidden</strong> ticket types. You can hide a ticket type by clicking the <strong>Edit</strong> tab in the upper left, and then clicking <strong>Hide</strong> (or selecting <strong>Hidden</strong> from the <strong>Sales Status</strong> dropdown menu, if you\'re using our <a href=\"/en_us/t/more-info-about-our-new-design\">new design</a>) next to the name of that ticket type. Once you\'ve hidden the relevant ticket(s), return to the <strong>Create Promotional Codes</strong> screen to set up your access code(s). <strong> </strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    -<strong> Uses</strong> - The total number of tickets this discount code can be applied to.</p>\r\n                                <p>\r\n                                    -<strong> Starts</strong> - When this discount code will become valid.</p>\r\n                                <p>\r\n                                    -<strong> Ends</strong> - Until when this discount code will be valid.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"685\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2access22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Once you\'ve created and saved an access code, you can always click the name of the access code while on the main <strong>Create Promotional Codes</strong> screen to see the details for that code. At the bottom of a particular <strong>Edit access code</strong> screen, you\'ll see a link/URL with that access code embedded in it. Anyone clicking this link will be taken to your event page, where the access code will be auto-filled and the hidden ticket type will already be showing. You can provide this link to the relevant people to make it even easier for them to register for the hidden ticket type!</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"152\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2URL24.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can view, edit or delete any of the discount or access codes you\'ve created by clicking on <strong>Create Promotional Codes</strong> on the left side of your event\'s <strong>Manage</strong> page at any time, as detailed in step 2 above.</p>\r\n                                <p>\r\n                                    <img width=\"618\" height=\"350\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To track the usage of a particular discount or promotional code, first click <strong>Event Reports</strong> on the features menu on the left side of your event\'s <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    <img width=\"231\" height=\"199\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/event%20reports22.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Filter the <strong>Report Type</strong> dropdown menu to <strong>Promotional code usage</strong>. Then select the particular discount or access code you\'d like to view from the <strong>Promotional code</strong> dropdown menu.</p>\r\n                                <p>\r\n                                    If you\'re looking at a discount code, you\'ll see that code and the discount percentage or amount shown in the <strong>Code</strong> column. (If you\'re looking at an access code, the <strong>Code</strong> column will be blank.)</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"330\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_discount_code/2reports2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To view the usage of all your discount and/or access codes in one place, you\'ll want to run an <strong>Attendee Summary</strong> report. Learn how to do that <a href=\"/en_us/t/attendance-summary\">here</a>! And to see how an attendee would enter a discount or access code when ordering, click <a href=\"/en_us/t/apply-a-discount-code\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229094', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('32', '10', 'Discount codes & access codes', 'discount-codes---access-codes-1', 'Discount codes & access codes', '1349229124', null, '0', '1');
INSERT INTO `etk_faqs` VALUES ('33', '10', 'Waitlist setup', 'waitlist-setup', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Waitlist setup</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Waitlist automatically kicks in when triggeredâ€¨</li>\r\n                                    <li>\r\n                                        Trigger can be ticket type selling out or reaching event capacity</li>\r\n                                    <li>\r\n                                        Not available for repeating events</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>This feature isn\'t currently available for repeating events.</p>\r\n                                <p>\r\n                                    If you have already enabled and set up a waitlist for your event, go <a href=\"/en_us/t/waitlist-management\">here </a>to learn more about managing that waitlist.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events </strong>page, click on the title of your event to go to the <strong>Manage</strong> page for that event. On the <strong>Manage</strong> page, select <strong>Waitlist Settings</strong> from the features menu on the left side of the page.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Enabled</strong> and then set your <strong>Waitlist Trigger</strong>. The <strong>Waitlist Trigger </strong>can activate your waitlist when a particular ticket type sells out or once total <a href=\"/en_us/t/change-number-of-tickets-on-sale\">event capacity</a> is reached.</p>\r\n                                <img width=\"612\" height=\"209\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-set-up-a-waitlist/trigger.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you would like to limit the waitlist to a maximum number of people, enter your <strong>Maximum Waitlist Size</strong>. Leave the setting at \"0\" if the waitlist can be an unlimited number of people.</p>\r\n                                <img width=\"613\" height=\"81\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-set-up-a-waitlist/max%20size.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the information you would like to collect from a waitlisted attendee when they sign up for the waitlist by checking the appropriate boxes in <strong>Attendee Information Collected</strong>.</p>\r\n                                <img width=\"615\" height=\"106\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-set-up-a-waitlist/info%20collected.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"messages\"></a>5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Finally, you can customize the <strong>Auto-Response Message</strong> a waitlisted attendee sees when they secure a spot on the waitlist, as well as the <strong>Waitlist Ticket Release Message</strong> that appears in the email a waitlisted attendee receives once you release a ticket to them.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"318\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-set-up-a-waitlist/message.png\" alt=\"Waitlist Management\"></p>\r\n                                <p>\r\n                                    You can also set the maximum amount of time a waitlisted attendee has to register or buy tickets for your event after you release a ticket to them.</p>\r\n                                <img width=\"611\" height=\"79\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-set-up-a-waitlist/max%20time.png\" alt=\"\">\r\n                            </div>\r\n                            <div>\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Click here to learn more about <a href=\"/en_us/t/waitlist-management\">managing a waitlist</a>.</strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229170', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('34', '10', 'Change number of tickets on sale', 'change-number-of-tickets-on-sale', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Change number of tickets on sale</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n\r\n                                    <li>\r\n                                        Increase or decrease the number of tickets on sale as needed</li>\r\n                                    <li>\r\n                                        Changes take effect instantly</li>\r\n\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you want to change the quantity of a certain <em>type </em>of ticket available for purchase, make sure to change the <strong>Quantity Available</strong> for that ticket type, <strong>not </strong>the <strong>Event Capacity</strong>. The difference between the two is explained <a href=\"/en_us/t/event-capacity-explained\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To change the <strong>Quantity Available</strong> for a particular <a href=\"/en_us/t/ticket-type-creation\">ticket type</a>, from the <strong>My Events</strong> page, click on <strong>Edit</strong> under <strong>Quick Links</strong> to the right of the name of your event. This will take you to the <strong>Edit</strong> page for your event.<br><img width=\"615\" height=\"278\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/edit.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket Information</strong>, click on the name of the ticket type (in blue) whose quantity available you\'d like to change.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                                <img width=\"614\" height=\"259\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/step%202.png\" alt=\"How to create ticket types\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you have created only one ticket type for this event, erase any number entered into the <strong>Event Capacity</strong> field, as it is redundant in this case, and the Event Capacity would override any changes you make to the <strong>Quantity Available </strong>of a particular ticket type. <strong>Event Capacity</strong> is explained in more detail at the end of this tutorial.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>Edit Ticket</strong> window, change the <strong>Quantity Available</strong> for this ticket type. Click <strong>Save </strong>to save your changes. The quantity of this type of ticket is now updated.</p>\r\n                                <img width=\"609\" height=\"253\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/edit%20ticket.png\" alt=\"How to create ticket types\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you have changed the quantity available of a ticket type and saved your changes, you will see this change reflected in the <strong>Status </strong>box and <strong>Sales By Ticket Type</strong> box in the <a href=\"/en_us/t/event-summary-page\"><strong>Event Summary</strong></a> on the <strong>Manage </strong>page for your event.<br><img width=\"606\" height=\"561\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/manage.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <a name=\"eventcapacity\"></a>\r\n                                <p>\r\n                                    <strong>Note: Event Capacity</strong> sets the maximum number of tickets across <strong>all </strong>ticket types that can be sold for this event. This feature lets you create two or more ticket types--each with its own quantity available--and allows you to cap the total number of tickets sold for the event across all ticket types.</p>\r\n                                <p>\r\n                                    Event Capacity will override any number that you have entered for the Quantity Available of a particular ticket type. Event Capacity is accessed in <strong>Step 2: Add Ticket Information</strong> on the <strong>Edit </strong>page for your event.</p>\r\n                                <p>\r\n                                    <img width=\"520\" height=\"154\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/eventcapacity.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    As an example, let\'s say you have a venue with a total capacity of 100 people. You\'re selling two ticket types--General Admission and VIP--and don\'t care about the mix of ticket types that you sell, as long as you don\'t sell more than 100 total tickets. In this case, you\'d want to set the <strong>Quantity Available</strong> for each ticket type to 100, and then also set the <strong>Event Capacity</strong> to 100.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Change number of tickets on sale</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Increase or decrease the number of tickets on sale as needed</li>\r\n                                        <li>\r\n                                            Changes take effect instantly</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You may have noticed that when editing your event, you’re able to set a <strong>Ticket Quantity</strong> and an <strong>Event Capacity</strong>. The difference between the two is explained <a href=\"/en_us/t/event-capacity-explained\">here</a>.</p>\r\n                                <p>\r\n                                    The <strong>Event Capacity</strong> will override any number that you have entered for the <strong>Ticket Quantity</strong> of a particular ticket type. Both the Event Capacity and Ticket Quantity fields can be accessed in <strong>Step 2: Create Tickets</strong> on the <strong>Event Details</strong> page for your event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, <a href=\"http://www.eventbrite.com/login\">log in</a> to your Eventbrite account and click <strong>My Events</strong> at the top of the screen. Then, click on <strong>Edit</strong> to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"177\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket/myevents1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To update the <strong>Ticket Quantity</strong>, go to <strong>Step 2: Create Tickets</strong>, click into the <strong>Ticket Quantity</strong> box to the right of the <strong>Ticket Name</strong> that you’re trying to update and enter the new quantity of tickets available. Remember to <strong>Save</strong> your changes.</p>\r\n                                <img width=\"595\" height=\"240\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/changeticketquantity-new2.png\" alt=\"How to create ticket types\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you’ve created only one ticket type for this event, the number entered in the <strong>Ticket Quantity</strong> field will automatically appear in the <strong>Event Capacity</strong> field. As more ticket types are created, the ticket quantities will add together and the total will update in the<strong> Event Capacity</strong> field.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To edit the <strong>Event Capacity</strong>, click into the <strong>Event Capacity </strong>box and enter the maximum number of tickets that you’re able to sell to your event. After you’ve entered the number, you’ll want to click out of the field so that the entry sticks. Click to save your changes.</p>\r\n                                <img width=\"596\" height=\"231\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/changeticketquantity-new3.png\" alt=\"How to create ticket types\"><p>\r\n                                    Once you do this, you’ll see that an <strong>Edit</strong> link to the right of the Event Capacity field appears. You can always click on this while your event is live to update the Event Capacity for your event. <img width=\"598\" height=\"223\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/changeticketquantity-new33.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you\'ve changed the quantity available of a ticket type and saved your changes, you\'ll see this change reflected in the <strong>Status </strong>box and <strong>Sales By Ticket Type</strong> box in the <a href=\"/en_us/t/event-summary-page\"><strong>Event Summary</strong></a> on the <strong>Manage </strong>page for your event.<br><img width=\"585\" height=\"478\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/changeticketquantity55.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip: </strong>Here\'s an example of when the <strong>Event Capacity</strong> box can help you! Let\'s say you have a venue with a total capacity of 100 people. You\'re selling two ticket types--General Admission and VIP--and don\'t care about the mix of ticket types that you sell, as long as you don\'t sell more than 100 total tickets. In this case, you\'d want to set the <strong>Quantity Available</strong> for each ticket type to 100, and then also set the <strong>Event Capacity</strong> to 100.</p>\r\n                                <p>\r\n                                    <img width=\"600\" height=\"238\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/changing-quantity-of-tickets-available/changeticketquantity-newtip1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229175', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('35', '10', 'People can\'t register for my event', 'people-can-t-register-for-my-event', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>People can\'t register for my event</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Tickets not showing on event page</li>\r\n                                    <li>\r\n                                        \"N/A\" or \"Sold Out\" displays next to a ticket type</li>\r\n                                    <li>\r\n                                        Troubleshooting tips to get your tickets on sale</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If people are unable to register for your event, it’s most likely because your tickets are hidden (<a href=\"#step1a\">#1 below</a>), your tickets are set to go on sale in the future (<a href=\"#step2a\">#2 below</a>), or your tickets are sold out (<a href=\"#step3a\">#3 below</a>). Scroll down to learn how to address each of these three scenarios!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step1a\"></a>1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box is not displayed on your event page, it means that your tickets are hidden. Here\'s how to show them!</p>\r\n                                <p>\r\n                                    <img width=\"601\" height=\"225\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing1.JPG\" alt=\"\"></p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit</strong> next to the title of the event to go to the <strong>Edit</strong> page for that event. In <strong>Step 2: Add Ticket Information</strong>, click the blue link labeled <strong>Show</strong> next to the ticket type name(s). You will see the <strong>Status</strong> change from <strong>Hidden</strong> to <strong>On Sale</strong>. Make sure to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"351\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing2.JPG\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step2a\"></a>2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box displays <strong>N/A</strong> instead of a dropdown menu beneath <strong>Quantity </strong>to the right of your ticket name, it means that your tickets are not yet on sale. Here\'s how to adjust the start date of sales to put them on sale!</p>\r\n                                <p>\r\n                                    <img width=\"598\" height=\"411\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing3.JPG\" alt=\"\"></p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit</strong> next to the title of the event to go to the <strong>Edit</strong> page for that event. In <strong>Step 2: Add Ticket Information</strong>, click on the name of your <strong>Ticket Type</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"597\" height=\"343\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing4.JPG\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the <strong>Edit Ticket</strong> box, under <strong>Advanced Options</strong>, edit the <strong>Start Sales</strong> date to reflect when ticket sales begin. If they should already be on sale, set this to today\'s date. Click <strong>Save</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"595\" height=\"356\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing5.JPG\" alt=\"\"></p>\r\n                            </div>\r\n                            <div>\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step3a\"></a>3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box displays <strong>Sold Out</strong> next to your ticket and the event should not be sold out, you need to adjust the <strong>Quantity Available</strong> of that particular ticket type. Here\'s how!</p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit</strong> next to the title of the event to go to the <strong>Edit </strong>page for that event. In <strong>Step 2: Add Ticket Information</strong>, click on the name of your <strong>Ticket Type</strong>. In the <strong>Edit Ticket</strong> box, check the <strong>Quantity Available</strong> and adjust if necessary.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"263\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing6.JPG\" alt=\"\"></p>\r\n                                <p>\r\n                                    If the <strong>Quantity Available</strong> is correct, but the ticket is still displaying as <strong>Sold Out</strong>, in the <strong>Step 2: Add Ticket Information box </strong>check the <strong>Event Capacity</strong> field.</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"165\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/ticket-registration-missing8.JPG\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> <strong>Event Capacity</strong> is not a required field, and can be left blank. Its function is to limit the overall tickets sold across <em>multiple</em> ticket types.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Help! People can\'t register for my event</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Tickets not showing on event page</li>\r\n                                        <li>\r\n                                            \"N/A\" or \"Sold Out\" displays next to a ticket type</li>\r\n                                        <li>\r\n                                            Troubleshooting tips to get your tickets on sale</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If people are unable to register for your event, it’s most likely because your tickets are hidden (<a href=\"#step1\">#1 below</a>), your tickets are set to go on sale in the future (<a href=\"#step2\">#2 below</a>), or your tickets are sold out (<a href=\"#step3\">#3 below</a>). Scroll down to learn how to address each of these three scenarios!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step1\"></a>1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box is not displayed on your event page, it means that your tickets are hidden. Here\'s how to show them!</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"239\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_1.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click <strong>Edit </strong>to the right of the title of your event. In <strong>Step 2: Create Tickets</strong>, find the <strong>Sales Status </strong>dropdown menu and change the status from <strong>Hidden</strong> to <strong>On Sale</strong>. Click <strong>Save</strong> in the upper right to save your changes.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 607px; height: 177px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To verify that your tickets are now on sale, click <strong>Colors &amp; Options</strong> in the upper left to take a look at your event page. The <strong>Ticket Information </strong>box should now be visible and a dropdown menu under <strong>Quantity</strong> showing, meaning that your attendees will be able to register.</p>\r\n                                <p>\r\n                                    <img width=\"430\" height=\"260\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_6.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step2\"></a>2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box on your event page displays <strong>N/A</strong> instead of a dropdown menu beneath <strong>Quantity </strong>to the right of the ticket fee, it means that your tickets are not yet on sale. Here\'s how to adjust the start date of sales to put them on sale!</p>\r\n                                <p>\r\n                                    <img width=\"433\" height=\"229\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_4.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click <strong>Edit</strong> to the right of the title of your event. In <strong>Step 2: Create Tickets</strong>, you’ll see that the <strong>Sales Status </strong>dropdown menu displays <strong>Not Started</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"214\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_5a.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To change the sales start date, click <strong>Settings </strong>to the right of the<strong> Sales Status </strong>dropdown menu, then adjust the <strong>Sale Starts</strong> date to today, so that you see the <strong>Sales Status </strong>dropdown menu change to <strong>On Sale</strong>. Click <strong>Save </strong>in the upper right to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"295\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_5b.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To verify that your tickets are now on sale, click <strong>Colors &amp; Options</strong> in the upper left to take a look at your event page. A dropdown menu should now be showing <strong>Quantity</strong>, meaning that your attendees will be able to register.</p>\r\n                                <p>\r\n                                    <img width=\"430\" height=\"260\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_6.png\" alt=\"\"></p>\r\n                            </div>\r\n\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <a name=\"step3\"></a>3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Ticket Information</strong> box displays <strong>Sold Out</strong> below <strong>Quantity</strong> and the event should not be sold out, you should adjust the <strong>Ticket Quantity</strong> of that particular type of ticket. Here’s how!</p>\r\n                                <p>\r\n                                    <img width=\"440\" height=\"247\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_5cc.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit</strong> to the right of your event title. In <strong>Step 2: Create Tickets</strong>, increase the <strong>Ticket Quantity</strong> of the specific ticket type so the <strong>Sales Status </strong>changes from <strong>Sold Out</strong> to <strong>On Sale</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"604\" height=\"189\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_7.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    If the <strong>Ticket Quantity </strong>is correct but the ticket is still displaying as <strong>Sold Out</strong>, you may need to increase the <strong>Event Capacity</strong>. (This is the total number of tickets&mdash;across all ticket types&mdash;that can be sold for the event, regardless of the mix in sales of individual ticket types. The <strong>Event Capacity</strong> will always override the <strong>Ticket Quantity</strong> for your ticket types.)</p>\r\n                                <p>\r\n                                    <img width=\"604\" height=\"211\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_8.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To verify that your tickets are now on sale, click <strong>Colors &amp; Options</strong> in the upper left to look at your event page. There should now be a dropdown menu displayed under <strong>Quantity</strong>, allowing your attendees to choose how many tickets they would like purchase.</p>\r\n                                <p>\r\n                                    <img width=\"424\" height=\"262\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event-attendees-unable-to-register/cantreg_9.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229179', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('36', '10', 'Cancel or unpublish an event', 'cancel-or-unpublish-an-event', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Cancel or unpublish an event</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Cancel your event after advising and refunding your attendees</li>\r\n                                    <li>\r\n                                        Unpublish your event at any time</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You don\'t have to cancel your event if you\'re just changing the event\'s date. Instead, you can simply change the date in <strong>Step 4 </strong>on the <strong>Edit</strong>&nbsp;page of your event. After you\'ve changed the date, you\'ll want to let your guests know! <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Click here</a> for instructions on how to email your attendees.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If your event is free, you can click <strong>Cancel</strong>--found on the upper right of the <strong>Manage </strong>page of your event--at any time. This will cancel your event and no one will be able to view the event page. Keep in mind that before you cancel your event, you\'ll want to let your guests know so they\'re not confused.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"340\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cancel_your_event/cancelevent1.png\" alt=\"\"></p>\r\n                                <div>\r\n                                    &nbsp;</div>\r\n                                <p>\r\n                                    You can easily email your attendees from within your Eventbrite account to let them know that you\'ll be issuing refunds and then canceling the event. <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Click here</a> for instructions on how to email your attendees, but make sure to do so before you cancel your event.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you\'ve sold tickets to your event, you\'ll notice that the <strong>Cancel </strong>button doesn\'t appear. In order to cancel and unpublish your event, you\'ll want to refund all of your attendees first. For instructions on refunding your guests, <a href=\"/en_us/t/refund-an-order\">click here</a>.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"182\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cancel_your_event/cancelevent2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve refunded your attendees, you\'re ready to cancel and unpublish your event. Just click on the <strong>Cancel </strong>button, found in the upper right area of the&nbsp;<strong>Manage&nbsp;</strong>page for your event, and voila! It\'s now hidden from attendees and anyone looking to register.</p>\r\n                                <p>\r\n                                    If you\'d like to delete your event page after refunding all orders and canceling the event, click <strong>Delete</strong> in the upper right of your event\'s <strong>Manage</strong> page.</p>\r\n                            </div>\r\n                            <div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <br><br>\r\n                                        &nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Unpublish, cancel or delete your event</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Cancel your event after advising and refunding your attendees</li>\r\n                                        <li>\r\n                                            Unpublish your event at any time</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You don\'t have to cancel your event if you\'re just changing the event\'s date. Instead, you can simply change the date in <strong>Step 1: Add your Event Details </strong>on the <strong>Event Details </strong>page of your event. After you\'ve changed the date, you\'ll want to let your guests know! <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Click here</a> for instructions on how to email your attendees.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If your event is free, you can click <strong>Cancel</strong>--found on the upper right of the <strong>Manage </strong>page of your event--at any time. This will cancel your event and no one will be able to view the event page. Keep in mind that before you cancel your event, you\'ll want to let your guests know to avoid confusion.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"351\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cancel_your_event/free.png\" alt=\"\"></p>\r\n                                <div>\r\n                                    &nbsp;</div>\r\n                                <p>\r\n                                    You can easily email your attendees from within your Eventbrite account to let them ahead of time that you’ll be canceling your event. <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Click here</a> for instructions on how to email your attendees.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you\'ve sold tickets to your event, you\'ll want to first issue refunds to all your attendees in order to cancel/unpublish your event page. For instructions for refunding your guests, <a href=\"/en_us/t/refund-an-order\">click here</a>.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"351\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cancel_your_event/nocancel.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve refunded your attendees, you\'re ready to cancel and unpublish your event. Just click on the <strong>Cancel </strong>button&nbsp;that now appears in the upper right area of your event’s <strong>Manage&nbsp;</strong>page, and&mdash;presto!&mdash;your event page is now hidden from attendees and anyone looking to register.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"355\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cancel_your_event/cancel2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You can always access your canceled event by clicking on the <strong>Canceled</strong> tab on the <strong>My Events</strong> page.</p>\r\n                            </div>\r\n\r\n                        </div>\r\n                        <div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note:&nbsp;</strong> If you\'d like to delete your event page after refunding all orders and canceling the event, click <strong>Delete</strong> in the upper right of your event\'s <strong>Manage</strong> page. However&mdash;take care!&mdash;deleting your event will delete it entirely from your Eventbrite account.</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229185', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('37', '10', 'Waitlist management', 'waitlist-management', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Waitlist management</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Manually release tickets to people on waitlist</li>\r\n                                    <li>\r\n                                        Attendee receives email and clicks through to register</li>\r\n                                    <li>\r\n                                        One ticket per entry on waitlist</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you have <a href=\"/en_us/t/waitlist-setup\">set up a waitlist</a> for your event, once a ticket type has sold out or the event capacity has been reached, the <b>Add to Waitlist</b> link will appear in the <b>Ticket Information</b> box on your event page.</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"179\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_waitlist/ticket%20info.png\" alt=\"Waitlist Management\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Potential attendees will be able to add themselves to the waitlist by clicking <b>Add to Waitlist</b> and entering their information. Once they have done so, they will see the <a href=\"/en_us/t/waitlist-setup\"><b>Auto-Response Message</b></a> that you have set in <b>Waitlist Settings</b>.</p>\r\n                                <img width=\"583\" height=\"425\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_waitlist/add%20info.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You will receive a notification email when the first person registers for the waitlist. To manage your waitlist and release tickets, you can click the link in that email to be taken to <b>Manage Waitlist</b>. Or, from the <b>My Events</b> page in your Eventbrite account, click on the title of your event to go to the <b>Manage </b>page for that event. On the <b>Manage </b>page, select <b>Manage Waitlist </b>from the features menu on the left side of the page.</p>\r\n                                <img border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_waitlist/manage.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <b>Manage Waitlist</b> page, you can view the list of potential attendees who signed up for the waitlist and release tickets to any of these people by checking the box next to a name and clicking <b>Release Tickets</b>.</p>\r\n                                <p>\r\n                                    You can also manually add people to the waitlist by clicking <b>Add People to the Waitlist</b> and filling in the necessary information for each person. &nbsp;</p>\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> Each entry on the waitlist corresponds to one ticket/registration to your event. If you would like to offer a potential attendee on the waitlist more than one ticket, you would need to add them one or more additional times to the waitlist and then release tickets to each of these entries.</p>\r\n                                <img width=\"609\" height=\"201\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_waitlist/waitlist.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you do release a ticket for the event to someone on the waitlist, the potential attendee will receive a notification email that has a <b>Claim my spot </b>button to that will allow them to register for your event. The potential attendee <b>must </b>click on this button in the notification email in order to be able to buy a ticket/register for your event.</p>\r\n                                <p>\r\n                                    If a potential attendee does not buy a ticket/register within the allotted time that you set in <a href=\"/en_us/t/waitlist-setup\"><b>Waitlist Settings</b></a>, the link in the notification expires and will not allow them to buy a ticket/register. If you want to then release a ticket to the next person on the waitlist, you would have to do so manually.</p>\r\n                                <img width=\"461\" height=\"596\" border=\"0\" title=\"Waitlist Management\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_waitlist/attendeewaitlist.png\" alt=\"Waitlist Management\">\r\n                            </div>\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Note:</b> Ticket sales must be active at the time that the attendee clicks to register for the released waitlist ticket. To adjust the <b>End Sales</b> date/time, click on the name of the ticket type in <b>Step 2: Add Ticket Information</b> on the <b>Edit </b>page of your event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229189', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('38', '10', 'Delete or hide ticket types', 'delete-or-hide-ticket-types', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Delete or hide ticket types</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Pause or stop ticket sales by hiding or deleting all ticket types</li>\r\n                                    <li>\r\n                                        Delete a ticket type without sales</li>\r\n                                    <li>\r\n                                        Hide a ticket type with sales</li>\r\n\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> \r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>TIP:</strong> Use this feature to remove or hide ticket types from your event registration page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Edit</strong> page of your event, locate the <strong>Step 2: Add </strong><strong>Ticket Information</strong> box.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"244\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete_ticket/Step2OLD.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next to each ticket type, there is a <strong>Hide</strong> and an <strong>X </strong>link.</p>\r\n                                <p>\r\n                                    To delete your ticket type, click on the <strong>X</strong>. Deleting a ticket type is only available for ticket types that have not had any ticket sales. Deleting the ticket types will remove that ticket from your page and your account.</p>\r\n                                <p>\r\n                                    If you just want to hide the ticket, keeping it from showing up on your event registration page, click on <strong>Hide</strong>. We do want to assure you that hiding a ticket will preserve the attendee data associated with that ticket type.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Delete or hide ticket types</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Pause or stop ticket sales by hiding or deleting all ticket types</li>\r\n                                        <li>\r\n                                            Delete a ticket type without sales</li>\r\n                                        <li>\r\n                                            Hide a ticket type with sales</li>\r\n\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>TIP:</strong> Use this feature to remove or hide ticket types from your event registration page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, log in to your Eventbrite account and click <strong>My Events</strong> at the top of the screen. Then, click on <strong>Edit</strong> to the right of your event’s name to get to the <strong>Event Details</strong> page of your event.</p>\r\n                                <p>\r\n                                    <img width=\"596\" height=\"178\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket/myevents1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To delete or hide a ticket, go to <strong>Step 2: Create Tickets</strong>. To the right of the <strong>Ticket Name</strong>, you’ll see the <strong>Sales Status</strong> column. Use the dropdown menu to select <strong>Hidden</strong>. Hiding a ticket type will remove the ticket type from your event registration page, but will preserve the attendee data associated with that ticket type.</p>\r\n                                <p>\r\n                                    <img width=\"596\" height=\"228\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete_ticket/createtixnew.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To delete a ticket type, simply click on the <strong>X</strong> to the right of the ticket name.</p>\r\n                                <p>\r\n                                    <img width=\"600\" height=\"229\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete_ticket/newcreatedeletetix.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You may only delete a ticket type if there haven’t been any ticket sales for that particular ticket. Deleting a ticket type will remove that ticket from your page and your account.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229195', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('39', '10', 'Change dates for ticket sales', 'change-dates-for-ticket-sales', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Change dates for ticket sales</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Change the start or end date/time of sales for a ticket type</li>\r\n                                    <li>\r\n                                        Put \'hidden,\' \'not started,\' or \'ended\' tickets back on sale</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> \r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, log in to your Eventbrite account and click <strong>My Events</strong> at the top of the screen. Then click on <strong>Edit</strong> to the right of your event’s name to get to the <strong>Edit</strong> page of your event.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket</strong> <strong>Information</strong>, you may notice that the <strong>Status</strong> column is listing your ticket type as <strong>Not Started</strong> or <strong>Ended</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate3.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To change the sales date of your ticket type, click on the name of the ticket type you\'d like to change the sales date for.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you click the ticket name, an <strong>Edit Ticket</strong> window will appear. You can adjust the <strong>Start Sales</strong> and/or&nbsp;<strong>End Sales</strong> by choosing a specific date/time or an amount of time before the event starts. Click <strong>Save</strong> at the bottom right of the box to ensure that the changes are made.&nbsp;</p>\r\n                                <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate222.png\" alt=\"How to create ticket types\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you don\'t see a field to change the <strong>Start Sales </strong>or <strong>End Sales</strong>, you\'ll want to click <strong>Show</strong> next to <strong>Advanced </strong><strong>Options</strong>. &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Status</strong> column of your <strong>Ticket Type</strong> displays <strong>Hidden</strong>, you may simply click <strong>Show</strong> so that the <strong>Status&nbsp;</strong>changes to&nbsp;<strong>On Sale</strong> and the ticket is visible on your event page.<img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/ticketsalesdate6.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you finish updating the ticket sales dates for your event, check out your page by clicking on the <strong>View</strong> tab. Your tickets should now be on sale!<br>\r\n                                    <img width=\"595\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate5.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Change dates for ticket sales</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Change the start or end date/time of sales for a ticket type</li>\r\n                                        <li>\r\n                                            Put \'hidden,\' \'not started,\' or \'ended\' tickets back on sale</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, log in to your Eventbrite account and click <strong>My Events</strong> at the top of the screen. Then click on <strong>Edit</strong> to the right of your event’s name to get to the <strong>Event Details</strong> page of your event.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Create Tickets</strong>, you may notice that the <strong>Sales Status</strong> column is listing your ticket type as <strong>Not Started</strong> or <strong>Sales Ended</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate21.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To change the sales date of your ticket type, click on <strong>Settings</strong> to the right of the <strong>Ticket Name</strong> you\'d like to edit.</p>\r\n                                <p>\r\n                                    <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate33.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you click <strong>Settings</strong>, your ticket setting options will appear. You can adjust the<strong> Sale Starts</strong> and/or<strong> Sale Ends</strong> by choosing a specific date and time. Click <strong>Apply</strong> at the bottom right of the box to ensure that the changes are made.</p>\r\n                                <img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate31.png\" alt=\"How to create ticket types\"></div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If the <strong>Sales Status</strong> column of your ticket type displays <strong>Hidden</strong>, you may use the dropdown menu to select <strong>OnSale</strong> so that the <strong>Sales Status </strong>changes to <strong>On Sale</strong> and the ticket is visible on your event page. Make sure to click <strong>Save</strong> at the top right to save the changes.<img width=\"595\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate55.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you finish updating the ticket sales dates for your event, check out your page by clicking on the <strong>Colors &amp; Options</strong> tab. Your tickets should now be on sale!<br>\r\n                                    <img width=\"595\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-ticket-sales-date/salesdate5.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229201', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('40', '10', 'Collect donations', 'collect-donations', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Collect donations</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Attendees decide how much to donate or pay</li>\r\n                                    <li>\r\n                                        Include a suggested minimum amount in ticket description</li>\r\n                                    <li>\r\n                                        Good for sliding-scale events</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Click <a href=\"http://www.eventbrite.com/event/2389553218\">here</a> to check out a sample event page with a donation ticket type!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started collecting donations, first create <a href=\"https://www.eventbrite.com/signup\">sign up</a> or <a href=\"https://www.eventbrite.com/login\">log in</a> to Eventbrite and <a href=\"/en_us/t/event-page-creation\">create an event</a>. In <strong>Step 2: Add Ticket Information</strong> of the <strong>Edit </strong>or <strong>Create an Event</strong> page for your event, click the button that says <strong>Create a Ticket</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"491\" height=\"97\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket/create_ticket_button.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you have already created a ticket type, either on the <strong>Create an Event</strong> page or the <strong>Edit </strong>page for your already created event, the button referenced above will instead say <strong>Add a New Ticket</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name</strong> and <strong>Quantity Available</strong>. Then select <strong>Donation Format </strong>for the <strong>Price</strong> option.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"319\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/create%20ticket2.png\" alt=\"How to collect donations on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also edit a number of additional options--which are accessed by clicking <strong>Show</strong> next to <strong>Advanced options</strong>--and then click <strong>Save</strong>:</p>\r\n                                <p>\r\n                                    <strong>- Start Sales</strong>: You can choose to start the sales of this ticket type at a particular date/time, an amount of time before the event starts, or when sales end for another of your ticket types (if you have created more than one ticket type).</p>\r\n                                <p>\r\n                                    <strong>- End Sales</strong>: You can choose to end the sales of this ticket type at a particular date/time or an amount of time before the event starts.</p>\r\n                                <p>\r\n                                    <strong>- Ticket Description</strong>: Enter more details about the ticket type here. What you enter will appear as smaller text below the name of your ticket type on the event page. You have the option to auto-hide this text, so that an attendee must click <strong>more info</strong> in order to view it.</p>\r\n                                <p>\r\n                                    <strong>- Service Fee</strong>: Adjust how you want to handle any fees. (The default in Eventbrite is to add the fee on top of what the attendee is donating, though you change this setting.) Learn more about this <a href=\"/en_us/t/feehandling-options\">here</a>.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"695\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/advanced.png\" alt=\"How to collect donations on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When registering, the ticket buyer will have the option of donating a self-designated amount rather than paying a set ticket price.</p>\r\n                                <p>\r\n                                    <img width=\"487\" height=\"216\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/page.png\" alt=\"How to collect donations on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>When an attendee makes a donation on your page, an <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email </a>will be sent to them, as with any other non-donation-type ticket order. At this time, each donation will count as one attendee. If you wish to give the donor the option of registering additional attendee(s), consider creating another, free <a href=\"/en_us/t/ticket-type-creation\">ticket type</a> called \"Extra Guests.\"</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>How to collect donations online using Eventbrite</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Attendees decide how much to donate or pay</li>\r\n                                        <li>\r\n                                            Include a suggested minimum amount in ticket description</li>\r\n                                        <li>\r\n                                            Good for sliding-scale events</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Click <a href=\"http://www.eventbrite.com/event/2389553218\">here</a> to check out a sample event page with a donation ticket type!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.<br>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"618\" height=\"265\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/MyEvents.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Create Tickets</strong>, select <strong>Donation </strong>from the <strong>Add Ticket Type </strong>dropdown menu<strong>.</strong> You can set the <strong>Ticket Quantity </strong>for this type of ticket, which will control how many individual donations you will be able to accept. Or you can leave this field blank to collect an unlimited number of donations.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"214\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/donation.png\" alt=\"How to collect donations on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also edit a number of additional options, which are accessed by clicking <strong>Settings</strong> for this ticket type:</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"364\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/settings.png\" alt=\"How to collect donations on the event page\"></p>\r\n                                <p>\r\n                                    <strong>- Ticket Description</strong>: Enter more details about this ticket type here. You could include a suggested minimum (or maximum) donation, or tell what donated funds will be used for. What you enter will appear as smaller text below the name of your ticket type on your event page. You have the option to <strong>Hide the description on your event page</strong>, so that an attendee must click the <strong>more info</strong> link to view the description.</p>\r\n                                <p>\r\n                                    <strong>- Start Sales</strong>: You can choose to start the sales of this ticket type at a particular date/time, an amount of time before the event starts, or when sales end for another of your ticket types (if you have created more than one ticket type).</p>\r\n                                <p>\r\n                                    <strong>- End Sales</strong>: You can choose to end the sales of this ticket type at a particular date/time or an amount of time before the event starts.</p>\r\n                                <p>\r\n                                    <strong>- Service Fees</strong>: Choose how you want to handle any <a href=\"http://www.eventbrite.com/fees/\">fees</a>. For donation ticket types, we recommend selecting <strong>Absorb the fees into the ticket price</strong>, so that the attendee donating is charged exactly the amount they choose when registering. Learn more <a href=\"/en_us/t/feehandling-options\">here</a> about how and why to choose who pays the fees.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To take a look at how your event page looks with your new donation ticket type, click <strong>Colors &amp; Options</strong> in the upper left. Once you’ve finished creating your ticket type, make sure to click <strong>Save </strong>to save your changes or <strong>Make Event Live</strong> to publish your event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now that you’ve set up a donation ticket type, an attendee will have the option of donating a self-designated amount rather than paying a set ticket price when registering.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 487px; height: 194px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_donations/page.png\" alt=\"How to collect donations on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>When an attendee makes a donation on your event page, an <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email </a>will be sent to them, as with any other non-donation-ticket-type order. Each donation will count as one attendee. If you wish to give the donor the option of registering additional attendee(s), consider creating a free <a href=\"/en_us/t/ticket-type-creation\">ticket type</a> called \"Extra Guests.\"</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229205', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('41', '10', 'Display number of remaining tickets', 'display-number-of-remaining-tickets', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Display number of remaining tickets</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Create a sense of urgency for popular events</li>\r\n\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first create<a href=\"https://www.eventbrite.com/signup\"> sign up</a> or<a href=\"https://www.eventbrite.com/login\"> log in</a> to Eventbrite and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Scroll to the bottom of the <strong>Event Creation</strong> page to the <strong>Additional Options</strong> section and check the<strong> Show number of tickets remaining on the registration page.</strong> option.</p>\r\n                                <p>\r\n                                    <img border=\"0\" title=\"Eventbrite.com\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_tickets_remaining/remaining%20selection.png\" alt=\"Eventbrite.com\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>Ticket Information</strong> box on your event page, the <strong>Remaining</strong> column will appear.</p>\r\n                                <p>\r\n                                    <img width=\"578\" height=\"164\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_tickets_remaining/tickets%20remaining%20on%20order.png\" alt=\"How to show tickets remaining on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Show the number of tickets remaining on your event page</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Create a sense of urgency for popular events</li>\r\n\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first create<a href=\"https://www.eventbrite.com/signup\"> sign up</a> or<a href=\"https://www.eventbrite.com/login\"> log in</a> to Eventbrite and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"200\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_tickets_remaining/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 3: Promote your Event Page</strong>, find the <strong>Remaining Tickets</strong> option and select <strong>Show the number of tickets remaining on the registration page</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 165px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_tickets_remaining/option.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To take a look at how this will display on your event page, click <strong>Colors &amp; Options</strong>&nbsp;in the upper left. You’ll see that a <strong>Remaining </strong>column now appears in the <strong>Ticket Information </strong>box on your event page, showing the number of tickets remaining for each type of ticket that you’ve created.</p>\r\n                                <p>\r\n                                    <img width=\"618\" height=\"290\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_tickets_remaining/remain.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229212', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('42', '10', 'Group tickets and discounts', 'group-tickets-and-discounts', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Group tickets and discounts</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Offer a discount for buying in quantity</li>\r\n                                    <li>\r\n                                        Boost sales through pricing incentives</li>\r\n\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, from the <strong>My Events</strong> page, go to your event\'s <strong>Edit </strong>page by clicking on <strong>Edit </strong>next to the name of your event. (If you are setting up a new event page--that is, you just pressed <strong>Create Event</strong> and the page you are on says <strong>Create an Event</strong>--you can skip this step and go directly to step 2 below.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket Information</strong>, click on the <strong>Create a Ticket</strong> button if this is your first ticket, or on the <strong>Add a New Ticket</strong> button if you have already created at least one ticket.</p>\r\n                                <p>\r\n                                    <img width=\"500px\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/TStep2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name</strong> (describing what your attendees are ordering), the <strong>Price</strong>, and the <strong>Quantity Available</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Keep in mind that the price you set for your ticket will be multiplied by the number of tickets in the group or package, to equal the total price for that group or package.</p>\r\n                                <p>\r\n                                    E.g.: If a ticket is regularly priced at $35--so, regularly $105 for three tickets--and you would like to give a $30 discount to those purchasing 3 tickets, set the ticket price to $25 for a $75 total.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Set the number of tickets included in the package by clicking <strong>Show</strong> next to <strong>Advanced Options</strong> in the<strong> Create Ticket </strong>window.</p>\r\n                                <p>\r\n                                    <img width=\"500px\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/TStep3.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Enter the number of tickets included in the package in both the <strong>Min. Tickets per Order</strong> and the <strong>Max. Tickets per Order</strong> fields. Click the <strong>Save</strong> button to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"500px\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/TStep4.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> It may be helpful to utilize the <strong>Ticket Description</strong> to clearly identify the savings that your attendees are receiving or to include any other relevent information about that particular ticket type. This description will appear below the name of your ticket type on your event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You are now ready to accept group registrations! If you\'d like to collect information for each attendee on an order, rather than for just the ticket buyer, you can learn how to do so <a href=\"/en_us/t/collect-info-from-each-attendee\">here</a>. Below are some examples of what your registration and order pages will look like with a group ticket option.</p>\r\n                                <p>\r\n                                    <img width=\"621\" height=\"297\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/T_RegPage2.JPG\" alt=\"\"></p>\r\n                                <p>\r\n                                    <img width=\"620\" height=\"145\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/TOSummEx.png\" alt=\"\"><br><br><br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Group tickets and discounts</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Offer a discount for buying in quantity</li>\r\n                                        <li>\r\n                                            Boost sales through pricing incentives</li>\r\n\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit</strong> to the right of your event’s name.<br><img width=\"592\" height=\"177\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket/myevents1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Create Tickets</strong>, enter the <strong>Ticket Name</strong> (describing what your attendees are ordering), <strong>Ticket Quantity</strong>, and <strong>Ticket Price</strong> in the appropriate fields. If you’ve already created tickets, you’ll want to click on the <strong>Add Ticket Type</strong> button to open up a new ticket then fill out the necessary fields.</p>\r\n                                <p>\r\n                                    <img width=\"597\" height=\"240\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/group-ticket-new2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Keep in mind that the price you set for your ticket will be multiplied by the number of tickets in the group or package, to equal the total price for that group or package.</p>\r\n                                <p>\r\n                                    E.g.: If a ticket is regularly priced at $20--so, regularly $100 for five tickets--and you\'d like to give a $35 discount to make the price for 5 tickets total to $65, you\'ll want to divide $65 by 5. This comes to $13 per ticket. This means you\'ll want to set the price of the ticket to $13.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now, you’ll want to click on the <strong>Settings</strong> link across from the <strong>Ticket Name</strong> so you can set the number of tickets included in the package.</p>\r\n                                <p>\r\n                                    <img width=\"600\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/group-ticket-new3.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    <br>\r\n                                    Enter the number of tickets included in the package in both the <strong>Minimum</strong> and <strong>Maximum</strong> fields for the <strong>Tickets permitted per order</strong>. Click Save at the top right of the page to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"427\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/group-ticket-new33.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> It may be helpful to utilize the <strong>Ticket description </strong>box to clearly identify the savings that your attendees are receiving or to include any other relevent information about that particular ticket type. You might want to uncheck the <strong>Hide description</strong> on event pages box if you want the description to automatically appear below the name of your ticket type on your event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Congrats! You\'re now ready to accept group registrations! If you\'d like to collect information for each attendee on an order, rather than for just the ticket buyer, you can learn how to do so <a href=\"/en_us/t/collect-info-from-each-attendee\">here</a>. Below are some examples of what your registration and order pages will look like with a group ticket option.</p>\r\n                                <p>\r\n                                    <img width=\"596\" height=\"348\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/groupticket-new41.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"221\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-create-a-group-ticket/group-ticket-new44.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229220', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('43', '10', 'Early-bird pricing', 'early-bird-pricing', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Early-bird pricing</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n\r\n                                    <li>\r\n                                        Reward early buyers with a discount</li>\r\n                                    <li>\r\n                                        Create a sense of urgency</li>\r\n                                    <li>\r\n                                        Drive earlier sales for your peace of mind</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>This tutorial will help you set up a ticket that has a special price for those of your attendees who register early for your event. To learn more about creating general ticket types, click&nbsp;<a href=\"/en_us/t/ticket-type-creation\">here</a>.</p>\r\n                                <p>\r\n                                    Click&nbsp;<a href=\"http://www.eventbrite.com/event/2203101536\">here</a>&nbsp;to take a look at an example of an event with an \"early bird\" ticket type that you\'ll learn to create here!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket Information</strong>, click on the <strong>Create a Ticket</strong> button (or on the&nbsp;<strong>Add a New Ticket</strong> button if you have already created at least one ticket).</p>\r\n                                <p>\r\n                                    <img width=\"567\" height=\"188\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/earlybird2.png\" alt=\"How to create early bird pricing\"></p>\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name</strong>, <strong>Price</strong>, and <strong>Quantity Available</strong> of your \"early bird\" ticket. Then click <strong>Show</strong> to see the <strong>Advanced Options.</strong></p>\r\n                                <p>\r\n                                    <img width=\"559\" height=\"294\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/earlybird21.png\" alt=\"How to create early bird pricing\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Look to <strong>End Sales</strong> and select the date and time you\'d like the \"early bird\" special price to end. For example, if you\'d like the tickets to stop selling once a day is over, you\'ll want to select the date that follows it at midnight.&nbsp;</p>\r\n                                <p>\r\n                                    You may also want to leave a <strong>Ticket Description</strong> to provide more information for and promote the \"early bird\" ticket.</p>\r\n                                <p>\r\n                                    <img width=\"590\" height=\"635\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/earlybird23.png\" alt=\"How to create early bird pricing\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now, if you haven\'t already, you\'ll want to create a general admission ticket.</p>\r\n                                <p>\r\n                                    Repeat the steps above, but this time select a <strong>ticket type start sales date/time</strong>.</p>\r\n                                <p>\r\n                                    This will indicate when the general admission ticket type should go on sale (generally right after the \"early bird\" ticket expires).</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"593\" height=\"661\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/earlybird3.png\" alt=\"How to create early bird pricing\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When you publish your event, both ticket types will appear but only the \"early bird\" ticket type will be available for purchase until the ticket type expires and then the general admission will only be available.</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"296\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/earlybird5.png\" alt=\"How to create early bird pricing\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you only want to have one ticket to appear at a time, you can manually hide a ticket type. To do this, you\'ll want go to the <strong>Edit</strong> section of your event and look to <strong>Step 2: Add Ticket Information</strong>. To the right of the name of your ticket type you\'ll want to click the <strong>hide</strong> link. Once the link is clicked, the status of the ticket type will read <strong>hidden</strong>, meaning the registrants who view your event page won\'t be able to see it.&nbsp; To make the ticket type appear again, you can click on the <strong>show</strong> link that appears to the right of the ticket type name.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Early-bird pricing</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n\r\n                                        <li>\r\n                                            Reward early buyers with a discount</li>\r\n                                        <li>\r\n                                            Create a sense of urgency</li>\r\n                                        <li>\r\n                                            Drive earlier sales for your peace of mind</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>This tutorial will help you set up a ticket that has a special price for those of your attendees who register early for your event. To learn more about creating general ticket types, click <a href=\"/en_us/t/ticket-type-creation\">here</a>.</p>\r\n                                <p>\r\n                                    Click <a href=\"http://www.eventbrite.com/event/2203101536\">here</a> to take a look at an example of an event with an \"early bird\" ticket type that you\'ll learn to create here!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"272\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/edit2.png\" alt=\"How to create early bird pricing\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In&nbsp;<strong>Step 2: Create Tickets</strong>, first create your standard-price ticket--called \"General Admission\" in our example--if you haven\'t already.</p>\r\n                                <p>\r\n                                    Then, to create your \"early bird\" ticket type, select <strong>Paid ticket</strong> from the <strong>Add Ticket Type </strong>dropdown menu.</p>\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name </strong>(called \"Early Bird\" in our example), <strong>Ticket Quantity</strong>, and a <strong>Ticket Price</strong>--cheaper than your standard-price ticket--for your \"early bird\" ticket.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"254\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/early.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now click <strong>Settings</strong> for the Early Bird ticket type. Set the <strong>Sale Starts </strong>and <strong>Sale</strong><strong> Ends</strong> dates/times for this ticket. Consider also including a <strong>Ticket description</strong> alerting people when the price will go up, so as to persuade them to buy earlier at the lower price (see the image below for our example text).</p>\r\n                                <p>\r\n                                    Once you\'ve finished, click <strong>Settings</strong> again to close the settings for your Early Bird ticket.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"349\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/settings.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The last thing to do is to set the sale of General Admission tickets to automatically start right when the sale of Early Bird tickets has ended.</p>\r\n                                <p>\r\n                                    To do this, click&nbsp;<strong>Settings</strong> for the General Admission ticket type. Next to&nbsp;<strong>Sale Starts</strong>, click&nbsp;<strong>or when ticket sales end</strong>.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"278\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/when.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Select <strong>Early Bird</strong> from the <strong>When sales end for</strong> dropdown menu.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"339\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/settings2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You\'ll see the <strong>Sales Status </strong>of the General Admission ticket change from <strong>On</strong> <strong>Sale </strong>to <strong>Not Started</strong> when you do so.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"253\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/status.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once you\'ve finished, click <strong>Settings</strong> again to close the settings for your General Admission ticket.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To see how your event page will look, click <strong>Colors &amp; Options </strong>in the upper left. See that the <strong>Ticket Information</strong> box shows both ticket types, the ticket description for the Early Bird ticket, and that only the Early Bird ticket is on sale right now.</p>\r\n                                <p>\r\n                                    <img width=\"533\" height=\"303\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/page2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once sale of the Early Bird ticket type ends--at the date and time that you set in step 3 above--the sale of the General Admission ticket type will start, per the steps you followed in step 4 above. Well done!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you only want to have one ticket to appear at a time, you can manually hide the other ticket type. To do this,&nbsp;go to <strong>Step 2: Create Tickets</strong> on your event\'s <strong>Event Details</strong> page.&nbsp;In the <strong>Sales Status</strong> column, choose <strong>Hidden </strong>from the dropdown menu for that ticket type, which means that the people who view your event page won\'t be able to see it.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"250\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/early_bird_pricing/hidden.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To make the ticket type appear again, you can choose <strong>Not Started</strong> (which means the <strong>Quantity</strong> column on your event page will show as <strong>N/A</strong>) or <strong>On Sale</strong>, depending on the <strong>Sale Starts</strong> date/time that you\'ve set for this ticket type in its <strong>Settings</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229225', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('44', '10', 'Tabled seating', 'tabled-seating', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Tabled seating</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Sell tickets for entire tables</li>\r\n                                    <li>\r\n                                        Collect information from each person at table or just ticket buyer</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> \r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> There are a couple ways to collect information from your attendees when setting up tabled seating. After setting up the ticket type, make sure to check out the <strong>Collecting information from each attendee</strong> section below if you\'d like to get information from <strong>each person</strong> who will be sitting at the table. To collect information from the <strong>ticket buyer only</strong>, look under the <strong>Collecting information from the ticket buyer </strong>section near the bottom.</p>\r\n                                <p>\r\n                                    Click <a href=\"http://www.eventbrite.com/event/2148243454\">here</a> to take a look at an example tabled-seating event that you\'ll learn to create here!</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Setting up the ticket type</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, <a href=\"https://www.eventbrite.com/login\">log in</a> to your Eventbrite account. To create a new event, click on the <a href=\"http://www.eventbrite.com/create\"><strong>Create Event</strong></a> tab found at the top of the page. To edit an existing event, click on the <strong>My Events</strong> tab at the top of the screen and then click on <strong>Edit</strong> to the right of the name of your event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket Information</strong>, click on the <strong>Create a Ticket</strong> button if this is your first ticket, or on the <strong>Add a New Ticket</strong> button if you have already created at least one ticket.</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"219\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/tabled_seating1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <strong>Ticket Name</strong>, <strong>Price</strong>, and <strong>Quantity Available</strong>.</p>\r\n                                <p>\r\n                                    As an example, let\'s say you\'d like to sell a table for $1000 dollars, have 10 seats available per table, and have a total of 20 tables available. In this case, you\'d set the <strong>Price</strong> to $100 (this is the per-seat price) and set the <strong>Quantity Available</strong> to 200 (this is the total number of seats that are available across all tables).</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"315\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/create_ticket.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once you\'ve entered the information above, click <strong>Show</strong> next to <strong>Advanced Options </strong>to set the minimum and maximum number of tickets per order.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the number of tickets (in terms of seats) available at the table in both the <strong>Min. Tickets per Order</strong> and the <strong>Max. Tickets per Order</strong> fields. (For our example, you\'d want to set both fields to 10.)</p>\r\n                                <p>\r\n                                    You may want to include a <strong>Ticket Description</strong> explaining what your attendee will receive by purchasing this ticket. Click the <strong>Save</strong> button to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"671\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/create_ticket2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The price that you set you for your ticket will be multiplied by the number of tickets ordered, equaling the total price of the table package. (In our example, the attendee will be required to purchase 10 of the \"Table for 10\" tickets at $100 a piece, which equals the $1000 that we\'re charging for the entire table.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Ready to check what your guests are going to see? Click on the <strong>Preview </strong>or <strong>View</strong> tab in the upper left.</p>\r\n                                <p>\r\n                                    When viewing your event page, go to the <strong>Ticket Information </strong>box and click on the <strong>Quantity</strong> dropdown menu for your \"Table for 10\" ticket type. You\'ll see that the guest can only choose to order the number of tickets that you set in the <strong>Min. Tickets per Order</strong> and the <strong>Max. Tickets per Order</strong> fields in step 4 above.</p>\r\n                                <p>\r\n                                    <img width=\"623\" height=\"349\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/tabedseating51.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong><a href=\"http://eventbrite.com/fees/\">Fees</a> will apply per ticket purchased.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p></p>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Collecting information from each attendee</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6a</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To collect information from each attendee at the table go to the <strong>Manage</strong> page of your event. You can get to the <strong>Manage</strong> page by clicking on <strong>My Events</strong> at the top of the page, and then clicking on the name of your event.</p>\r\n                                <p>\r\n                                    Once on the <strong>Manage </strong>page, select <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Customize Order Form</strong> page, click the radio button next to <strong>Collect Information below for each atende</strong><strong>e</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/collectinfotable6.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>When collecting information from each attendee, you\'re able to choose which ticket type the questions you are asking apply to. In this case, if you only want answers from those ordering a \"Table for 10\" ticket, you\'d check the box next that ticket type\'s name.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    An <strong>Information to Collect</strong> grid will appear and allow you to choose what information you\'d like to get from each of the attendees at the table. If you have a specific question you would like to ask the attendees that is not on the left side, you may create it by clicking on <a href=\"/en_us/t/custom-questions-for-attendees\"><strong>Add a Question</strong></a> on the right side.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/tabledseating71.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <br>\r\n                            <br>\r\n                            <br>\r\n                            &nbsp;</div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Collecting information from the ticket buyer</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you plan to use Eventbrite\'s <a href=\"/en_us/t/print-name-badges\">auto-generated name badge feature</a>, our <a href=\"/en_us/t/iphone-easy-entry-app\">Easy Entry iPhone check-in app</a> or our <a href=\"/en_us/t/laptop-for-checkin\">Entry Management laptop check-in tool</a> on the day of your event, you\'ll want to collect information from each attendee, as detailed above. Otherwise, if you collect information from just the ticket buyer, as detailed below, only the ticket buyer will show up on the name badges and/or check-in list.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6b</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you don\'t want the ticket buyer to have to input information for each person at the table and want them to simply register for the table as a whole, you can click on <strong><label for=\"collectinfo_ticketbuyer\">Collect information below for the ticket buyer only</label></strong>.</p>\r\n                                <p>\r\n                                    Then follow Step 8 from above.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/collectinfotable6b.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you want an idea of who\'ll be at each table, you can <a href=\"/en_us/t/custom-questions-for-attendees\"><strong>Add a Question</strong></a>--we recommend choosing the <strong>large text field</strong> answer type--that asks the ticket buyer to list the names of the other 9 guests at the table.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Selling tickets for an event with tabled seating</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Sell tickets for entire tables</li>\r\n                                        <li>\r\n                                            Collect information from each person at table or just ticket buyer</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> There are a couple ways to collect information from your attendees when setting up tabled seating. After setting up the ticket type, make sure to check out the <strong>Collecting information from each attendee </strong>section below if you\'d like to get information from <strong>each person</strong> who will be sitting at the table. To collect information from the <strong>ticket buyer only</strong>, look under the <strong>Collecting information from the ticket buyer </strong>section near the bottom.</p>\r\n                                <p>\r\n                                    Click <a href=\"http://www.eventbrite.com/event/2148243454\">here</a> to take a look at an example tabled-seating event that you\'ll learn to create here!</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Setting up the ticket type</strong></span></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"294\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Create Tickets</strong>, use the <strong>Add a Ticket</strong> dropdown menu to create one ticket type for individuals and one ticket type for an entire table.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"215\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/general.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    For the table ticket type--called \"Table for 10\" in our example here--enter the <strong>Ticket Name</strong>, <strong>Ticket Quantity</strong>, and<strong> Price</strong>.</p>\r\n                                <p>\r\n                                    (For our example, we\'ll say we\'d like to sell a table for $1000 dollars, have 10 seats available per table, and have a total of 20 tables available. In this case, you\'d set the <strong>Price</strong> to $100 [this is the per-seat price] and set the <strong>Ticket Quantity</strong> to 200 [this is the total number of seats that are available across all tables].)</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"250\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/table%20tix.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve entered the information above, click <strong>Settings</strong> to set the minimum and maximum number of tickets permitted per order for this table ticket type.</p>\r\n                                <p>\r\n                                    Enter the number of tickets (in terms of seats) available at the table in the <strong>Minimum</strong> and <strong>Maximum</strong> fields next to <strong>Tickets permitted per order</strong>. (For our example, you\'d want to set both fields to 10.)</p>\r\n                                <p>\r\n                                    You may also want to include a <strong>Ticket description</strong> explaining what your attendee will receive by purchasing this ticket. (This description will show as smaller text below the name of the ticket type on your event page.)</p>\r\n                                <p>\r\n                                    Click the <strong>Settings</strong> link again to close the <strong>Settings</strong> box. Be sure to click <strong>Save</strong> to save your changes before exiting the page.</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"281\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/minmax.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> The price that you set you for your ticket will be multiplied by the number of tickets ordered, equaling the total price of the table package. (In our example, the attendee will be required to purchase 10 of the \"Table for 10\" tickets at $100 a piece, which equals the $1000 that we\'re charging for the entire table.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Ready to take a look at what your guests are going to see? Click on the <strong>Colors &amp; Options</strong> tab in the upper left.</p>\r\n                                <p>\r\n                                    When visitors to your event page want to purchase a table, they will only be able to select the number of tickets that you set in the <strong>Tickets permitted per order</strong> fields, as explained in step 4 above.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"404\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/event%20page.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong><a href=\"http://eventbrite.com/fees/\">Fees</a> will apply per ticket purchased.</p>\r\n                                <p>\r\n                                    <br>\r\n                                    <br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p></p>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Collecting information from each attendee</strong></span></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6a</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To collect information from each attendee at the table, click <strong>Save</strong> or <strong>Make Event Live</strong> in the upper right. Then click <strong>Manage</strong> in the upper left to go to your event\'s <strong>Manage</strong> page, if you aren\'t taken there automatically.</p>\r\n                                <p>\r\n                                    <img width=\"604\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/tabledseating222.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, select <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once on the <strong>Customize Order Form</strong> page, select the option to<strong> Collect information below for each attendee</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/collectinfotable6.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>When collecting information from each attendee, you\'re able to choose which ticket type(s) the questions you are asking will apply to. In this case, if you only want answers from those ordering a \"Table for 10\" ticket, you\'d check the box next that ticket type\'s name.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll see an <strong>Information to collect</strong> grid that will allow you to choose which information you\'d like to collect from each of the attendees at the table. If you have a specific question you\'d like to ask the attendees that isn\'t on the left side, you can create your own question by clicking on the <a href=\"/en_us/t/custom-questions-for-attendees\"><strong>Add a Question</strong></a> button on the right side. (Any information that you collect can later be viewed in your <a href=\"/en_us/t/attendance-summary\"><strong>Attendee Summary</strong> report</a>).</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/tabledseating71.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <br>\r\n                            <br>\r\n                            <br>\r\n                            &nbsp;</div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Collecting information from the ticket buyer</strong></span></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you plan to use Eventbrite\'s <a href=\"/en_us/t/print-name-badges\">auto-generated name badge feature</a>, our <a href=\"/en_us/t/iphone-easy-entry-app\">Easy Entry iPhone check-in app</a> or our <a href=\"/en_us/t/laptop-for-checkin\">Entry Management laptop check-in tool</a> on the day of your event, you\'ll want to collect information from each attendee, as detailed above. Otherwise, if you collect information from just the ticket buyer, as detailed below, only the ticket buyer will show up on the name badges and/or check-in list.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6b</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you don\'t want the ticket buyer to have to input information for each person at the table and want them to simply register for the table as a whole, select <strong>ticket buyer</strong> <strong>only</strong> from the <strong>Customize Order Form </strong>page.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-setup-tabled-seating/collectinfotable6b.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then follow the instructions starting from step 7 from above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you you\'re capturing information from <strong>Just the ticket buyer</strong> and want an idea of who\'ll be at each table, you can <a href=\"/en_us/t/custom-questions-for-attendees\"><strong>Add a Question</strong></a>--we recommend choosing the <strong>large text field</strong> answer type--that asks the ticket buyer to list the names of the other 9 guests at the table. You can then access people answers in the <a href=\"/en_us/t/attendance-summary\"><strong>Attendee Summary</strong> report</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229230', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('45', '10', 'Daily deals for my event', 'daily-deals-for-my-event', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Daily deals for my event</h3>\r\n                            <ul>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Configure your event to work with vouchers from sites like Groupon or LivingSocial</li>\r\n                                    <li>\r\n                                        Use a custom question to collect voucher numbers</li>\r\n\r\n                                </ul>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> \r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Event organizers sometimes set up their Eventbrite event page to take registrations from people who purchased a voucher on a site like Groupon, LivingSocial, or HomeRun. Typically, organizers collect voucher codes from attendees using a custom question at checkout. See below for a look at how to set things up!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 259px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Add Ticket Information</strong> on the <strong>Edit</strong> page, click the <strong>Add a New Ticket</strong> button. (If you\'re creating your first ticket type for this new event, this button will say <strong>Create a Ticket</strong> instead.)</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"202\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/add_ticket.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>Edit Ticket</strong> window, enter the following information:</p>\r\n                                <p>\r\n                                    - <strong>Ticket Name</strong>: You want to choose a name that will indicate to the attendee that they will be using a voucher when registering for this ticket type. An example would be \"Using a Voucher.\"</p>\r\n                                <p>\r\n                                    - <strong>Ticket Price</strong>: Free, since they have already prepurchased the voucher.</p>\r\n                                <p>\r\n                                    - <strong>Quantity Available</strong>: This indicates the quantity available of this particular ticket type.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"317\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/edit_ticket.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can change the <strong>Quantity Available</strong> at any time, even after publishing your event, by going to <strong>Step 2: Add Ticket Information </strong>on the <strong>Edit</strong> page for your event and clicking on the name of the ticket.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Save</strong> to save your changes.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now head to the <strong>Manage</strong> page for your event by clicking the <strong>Manage </strong>tab in the upper left. On the <strong>Manage </strong>page, click <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the third of the three information-collection settings to \"Collect information from each attendee.\"</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/attendee.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Create a custom survey question by selecting <strong>Create a Question</strong> under <strong>Your Questions</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 608px; height: 86px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/add.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Input your question in the <strong>Question</strong> field. This can be something along the lines of \"Please enter your voucher number here.\" Then designate the <strong>Question Type</strong> to be <strong>Text</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 298px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/custom_q.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                9</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Under <strong>Optional Settings</strong>, choose to show this question only for the voucher ticket type you created. (You can learn more about creating ticket type-specific custom questions <a href=\"/en_us/t/ticket-typespecific-questions\">here</a>.) Be sure to select <strong>Save </strong>to save your changes.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 232px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/custom_q2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                10</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next screen, select that this question be <strong>Required</strong>. Remember to click <strong>Save Changes</strong> once you\'re finished.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 611px; height: 164px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/required.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> After your event, you\'ll want to run an Attendee Summary report to see who has used their voucher number. Click <a href=\"t/how-to-view-attendee-report\">here</a> to learn how to view and export the Attendee Summary report for your event, which includes the answers to all the custom questions you ask your attendees.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Integrate daily deals or vouchers with your Eventbrite event</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Configure your event to work with vouchers from sites like Groupon or LivingSocial</li>\r\n                                        <li>\r\n                                            Use a custom question to collect voucher numbers</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Event organizers sometimes set up their Eventbrite event page to take registrations from people who purchased a voucher on a site like Groupon, LivingSocial, or HomeRun. Typically, organizers collect voucher codes from attendees using a custom question at checkout. See below for a look at how to set things up!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 608px; height: 258px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/edit2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 2: Create Tickets</strong> on your <strong>Event Details</strong> page, click the&nbsp;<strong>Free</strong>&nbsp;button to the right of&nbsp;<strong>Add a Ticket</strong>&nbsp;to begin creating your free ticket.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 607px; height: 183px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/free.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Create a <strong>Ticket Name</strong> that indicates to the attendee they will use their voucher code when registering for this ticket type. An example is \"Using a Voucher.\" Once you\'ve named your ticket type, input the <strong>Ticket Quantity </strong>for this ticket type and leave the <strong>Ticket Price</strong> set to 0.00.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 607px; height: 201px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/voucher.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Then, click <strong>Make Event Live</strong> or <strong>Save</strong> in the upper right. Now click <strong>Manage</strong> in the upper left.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 611px; height: 43px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/save.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    On the <strong>Manage </strong>page, select <strong>Customize Order Form</strong>, found near the top of the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose the option to <strong>Collect information below for each attendee</strong>.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/attendee.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Next create a custom survey question by clicking <strong>Create a Question</strong>&nbsp;under <strong>Your Questions</strong>.&nbsp;</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 608px; height: 86px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/add.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Input your question in the <strong>Question</strong> field. This can be something along the lines of \"Please enter your voucher number here.\" Then, designate the <strong>Question Type</strong> to be a <strong>Text</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 298px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/custom_q.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Under <strong>Optional Settings</strong>, check the box to the left of <strong>Show this question for specific ticket types</strong> and check the box to the left of “Using a Voucher” to show this question only for the voucher ticket type you created.</p>\r\n                                <p>\r\n                                    If you have more than one ticket type, you can select which tickets to apply the custom question(s) to. (You can learn more about creating ticket type-specific custom questions <a href=\"/en_us/t/ticket-typespecific-questions\">here</a>.)</p>\r\n                                <p>\r\n                                    Be sure to click <strong>Save </strong>to save your changes!</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 232px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/custom_q2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next screen, select that this question be <strong>Required</strong>. Remember to click <strong>Save Changes</strong> once you\'re finished.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 611px; height: 164px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-use-vouchers-deals/required.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Before your event, you can compare the number of “Use a Voucher” tickets that have been “sold” or redeemed in Eventbrite with the number of deals or vouchers that you sold on the outside “daily deal” website to make sure that these two numbers sync.</p>\r\n                                <p>\r\n                                    If you’d like further details, you can run an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a> and export it to Excel. The Attendee Summary report will include the answers to all of the custom questions you asked your attendees, including the voucher numbers they redeemed. You can then cross reference the voucher numbers collected on Eventbrite with the numbers provided by the “daily deal” site.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229235', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('46', '12', 'Email invitations', 'email-invitations', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Email invitations</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Send up to 2000 free email invites per day</li>\r\n                                <li>\r\n                                    Customize the look/feel of your invites</li>\r\n                                <li>\r\n                                    Send to contacts from outside mail programs or upload contact lists</li>\r\n                                <li>\r\n                                    View performance of your invites (e.g., open and conversion rates)</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Looking for even more flexibility with your invitations? Go <a href=\"/en_us/t/paperless-post-and-eventbrite\">here</a> to learn about our integration with Paperless Post or <a href=\"/en_us/t/mailchimp-and-eventbrite\">here</a> to find out more about our MailChimp integration!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> You must first <strong>Publish</strong>/<strong>Make Live</strong> your event before you can use our <strong>Invitations</strong> feature.</p>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    1</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                    <p>\r\n                                        To send email invitations for your event event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                                    <p>\r\n                                        Click on <strong>Create Invitations</strong>, found on the features menu on the left side of your event\'s <strong>Manage</strong> page.</p>\r\n                                    <p>\r\n                                        <img width=\"252\" height=\"170\" border=\"0\" title=\"Send Email Invitations\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/features.png\" alt=\"Send email invitations\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <a name=\"editinvite\"></a>2</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        A preview is shown with the default invitation design, but you are free to customize from there!</p>\r\n                                    <p>\r\n                                        Click <strong>Customize</strong> to change the color scheme, select which social sharing links to include in your invites, choose a salutation or enable the RSVP option. Click <strong>Edit Message</strong> to change the default text included below the <strong>Attend Event</strong> button.</p>\r\n                                    <p>\r\n                                        You can also edit the <strong>Name of Sender</strong>, <strong>Reply-To</strong> email address and <strong>Subject Line</strong> of the email by clicking on the text next to each field.</p>\r\n                                    <p>\r\n                                        <img width=\"609\" height=\"793\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/invite22.png\" alt=\"\"></p>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p style=\"text-align: center;\">\r\n                                            <img width=\"548\" height=\"395\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/customize.png\" alt=\"\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        &nbsp;</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip:</strong> If you\'re trying to match a color from another web page, check out <a href=\"http://www.colorzilla.com/firefox/\">ColorZilla</a>, a handy Firefox add-on.</p>\r\n\r\n                                        <img width=\"555\" height=\"376\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/edit_message.png\" alt=\"\"><p></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Note:</strong> If you\'d like to save the changes that you\'ve made to the invitation you\'re working on make sure to click on the&nbsp;<strong>Save as Draft</strong> button found on top and bottom left of the page. You can retrieve and continue working on the draft by clicking on the <strong>Manage Invitations</strong> link found on the left side of your event\'s <strong>Manage</strong> page.&nbsp;</p>\r\n                                    </div>\r\n                                    <div class=\"step\">\r\n                                        <div class=\"stepnumber\">\r\n                                            3</div>\r\n                                        <div class=\"stepcontent\">\r\n                                            <p>\r\n                                                After you\'ve perfected the design, now choose whom to invite. Click <strong>Add Guests </strong>and select the recipients from a variety of different options. You can:</p>\r\n                                            <p>\r\n                                                - upload emails from a file (.csv or .txt);</p>\r\n                                            <p>\r\n                                                - import emails from an email address book;</p>\r\n                                            <p>\r\n                                                - import contacts from existing <a href=\"/en_us/t/import-or-create-contact-lists\">contact lists</a>;</p>\r\n                                            <p>\r\n                                                - create an email list from attendees of a past event;</p>\r\n                                            <p>\r\n                                                - or manually add email addresses.</p>\r\n                                            <p>\r\n                                                <img width=\"266\" height=\"136\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/addguests.png\" alt=\"\"></p>\r\n                                            <p>\r\n                                                <img width=\"384\" height=\"277\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/contacts.png\" alt=\"\"></p>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"step\">\r\n                                        <div class=\"stepnumber\">\r\n                                            <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                                        <div class=\"stepcontent\">\r\n                                            <p>\r\n                                                <strong>Note: </strong>Make sure to list individual, rather than listserv, email addresses as recipients to your invitation.</p>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"step\">\r\n                                        <div class=\"stepnumber\">\r\n                                            4</div>\r\n                                        <div class=\"stepcontent\">\r\n                                            <p>\r\n                                                Now select when you\'d like to send your email invitation. You have four options: send immediately, send on a specific date/time, send on a date/time in relation to the event start date, or you can save the email invitation as a draft and don\'t send.</p>\r\n                                            <p style=\"text-align: center;\">\r\n                                                <img width=\"464\" height=\"188\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/sendwhen.png\" alt=\"\"></p>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"step\">\r\n                                        <div class=\"stepnumber\">\r\n                                            5</div>\r\n                                        <div class=\"stepcontent\">\r\n                                            <p>\r\n                                                Once you\'ve sent your email invitations, you\'ll be taken to the <a href=\"/en_us/t/resend-and-manage-sent-invitations\"><strong>Manage Invitations</strong></a> page where you can view and manage any email invitations you have sent, including this one. Email invitations are saved here for easy viewing, copying, and editing.</p>\r\n                                            <p>\r\n                                                <img width=\"590\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/send_email_invites/viewstatus1.png\" alt=\"\"></p>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"step\">\r\n                                        <div class=\"stepnumber\">\r\n                                            <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                                        <div class=\"stepcontent\">\r\n                                            <p>\r\n                                                <strong>Note:</strong> Each recipient will receive an invitation to only their email address. They will <strong>not</strong> be able to view anyone else\'s email address. Click <a href=\"/en_us/t/import-or-create-contact-lists\">here</a> to learn how to create contact lists for easier invitation sending.</p>\r\n                                        </div>\r\n                                    </div>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229303', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('47', '12', 'Promotional tools overview', 'promotional-tools-overview', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Promotional tools overview</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Choose from a variety of promotional tools to sell out your event</li>\r\n                                <li>\r\n                                    Widgets, personalized links, email invites, tracking links and more</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To use Eventbrite\'s event promotion tools to get the word out about your event and <a href=\"/en_us/t/how-to-sell-tickets-online\">sell more tickets online</a>, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for Eventbrite or <a href=\"https://www.eventbrite.com/login\">log in</a> to your account. For a closer look at the numbers, check out our <a href=\"http://blog.eventbrite.com/social-commerce\">report </a>on the power of Social Commerce!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Promote your event on major search engines and the Eventbrite events directory</strong></p>\r\n                                <p>\r\n                                    By default, your event will be marked as Public, and so will be:</p>\r\n                                <p>\r\n                                    - be indexed by major search engines (like <a href=\"http://www.google.com/support/webmasters/bin/answer.py?hl=en&amp;amp;answer=1352276\">Google</a>, Yahoo!, and Bing);</p>\r\n                                <p>\r\n                                    - made available to our <a href=\"http://eventbrite.appstores.com/t/categories/distribution-partners\">distribution partners</a>, like Zvents and Eventful;</p>\r\n                                <p>\r\n                                    - listed on our public event-search directory, making it easy to find by event seekers.</p>\r\n                                <p>\r\n                                    Make sure that your event is marked as Public by going to <strong>Step 8: Set Privacy </strong>on the <strong>Edit </strong>page for your event. To better help your event\'s \'searchability\' by search engines, event seekers, and event-indexing sites, select one or two <strong>categories</strong> that best describe(s) your event and <strong>tag</strong> your event with relevant keywords.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"223\" border=\"0\" title=\"Promote your Events Online\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/step_8.png\"></p>\r\n                            </div>\r\n                            <div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        2</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Promote your event on Facebook</strong></p>\r\n                                        <p>\r\n                                            Use the power of the social graph to drive more visits to your event page and sell more tickets! Click <a href=\"/en_us/t/publish-event-to-facebook\">here </a>to learn more about how to easily publish your event to Facebook from within Eventbrite.</p>\r\n                                        <p>\r\n                                            <img border=\"0\" title=\"Promote your Events Online\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/facebook.png\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        3</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Insert a customized link or button</strong> <strong>in your website or email</strong></p>\r\n                                        <p>\r\n                                            You can choose a <strong>personalized link</strong> for your event page, to make it easy for your attendees to remember and find. <img width=\"614\" height=\"83\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/custom%20link.png\" alt=\"How to promote your events online\"></p>\r\n                                        <p>\r\n                                            You can also use our <a href=\"/en_us/t/custom-buttons-and-links\"><strong>Buttons</strong> and <strong>Links</strong> features</a> to promote your event in emails or on outside websites.</p>\r\n                                        <p>\r\n                                            <img title=\"Promote your Events Online\" style=\"border-width: 0px; border-style: solid;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/buttons3.png\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        4</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Publicize your event on your website using widgets</strong></p>\r\n                                        <p>\r\n                                            Choose from a variety of <a href=\"/en_us/t/sell-tickets-on-your-website\">customizable widgets</a> to include on your outside website. From the entire event registration page to a countdown timer to a calendar displaying all your live events, you can easily generate the HTML code to drop into your website.</p>\r\n                                        <p>\r\n                                            Here\'s an example of the <a href=\"/en_us/t/embed-ticket-form-into-your-website\">ticket form</a> integrated into an outside website:</p>\r\n                                        <p>\r\n                                            <img width=\"600\" height=\"399\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/page.png\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        &nbsp;</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip:</strong> To check out how you can easily send email invitations from within Eventbrite, go <a href=\"/en_us/t/email-invitations\">here</a>!</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <br>\r\n                                        <br>\r\n                                        &nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <hr/>\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Promotional tools overview</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Choose from a variety of promotional tools to sell out your event</li>\r\n                                    <li>\r\n                                        Widgets, personalized links, email invites, tracking links and more</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a> and make it live.</p>\r\n                                <p>\r\n                                    Then, check out some of our promotional tools listed below to get started promoting your event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Promote your event on major search engines and the Eventbrite events directory</strong></p>\r\n                                <p>\r\n                                    By default, your event will be marked as Public, and so will be:</p>\r\n                                <p>\r\n                                    - be indexed by major search engines (like <a href=\"http://www.google.com/support/webmasters/bin/answer.py?hl=en&amp;amp;answer=1352276\">Google</a>, Yahoo!, and Bing);</p>\r\n                                <p>\r\n                                    - made available to our <a href=\"http://eventbrite.appstores.com/t/categories/distribution-partners\">distribution partners</a>, like Zvents and Eventful;</p>\r\n                                <p>\r\n                                    - listed in our <a href=\"http://www.eventbrite.com/search\">public event-search directory</a>, making it easy to find by event seekers.</p>\r\n                                <p>\r\n                                    Make sure that your event is marked as <strong>Public</strong> by going to <strong>Step 3: Promote your Event Page </strong>on your event’s <strong>Event Details</strong> page. To better help your event\'s \'searchability\' by search engines, event seekers, and event-indexing sites, select one or two <strong>Search Categories</strong> that best describe(s) your event.</p>\r\n                                <p>\r\n                                    <img width=\"544\" height=\"180\" border=\"0\" title=\"Promote your Events Online\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/step3.png\"></p>\r\n                            </div>\r\n                            <div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        2</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Promote your event on Facebook</strong></p>\r\n                                        <p>\r\n                                            Use the power of the social graph to drive more visits to your event page and sell more tickets! Click <a href=\"/en_us/t/publish-event-to-facebook\">here </a>to learn more about how to easily publish your event to Facebook from within Eventbrite.</p>\r\n                                        <p>\r\n                                            <img border=\"0\" title=\"Promote your Events Online\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/facebook.png\"></p>\r\n                                        <p>\r\n                                            And go <a href=\"/en_us/t/twitter-promotion\">here</a> to learn more about using Twitter to promote your event!</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        &nbsp;</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip:</strong> For a closer look at the social-media numbers, check out our <a href=\"http://blog.eventbrite.com/social-commerce-2\">report</a> on the power of Social Commerce.</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        3</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong><strong>Send up to 2000 free email invitations per day</strong> </strong></p>\r\n                                        <p>\r\n                                            To check out how you can easily send email invitations from within Eventbrite, go <a href=\"/en_us/t/email-invitations\">here</a>! And you can always leverage our partnerships with <a href=\"/en_us/t/mailchimp-and-eventbrite\">MailChimp</a> and <a href=\"/en_us/t/paperless-post-and-eventbrite\">Paperless Post</a> for even more email-customization options and greater per-day email limits.</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        4</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Insert a customized link or button</strong> <strong>in your website or email</strong></p>\r\n                                        <p>\r\n                                            You can choose a <strong>customized web address/URL</strong> for your event page to make it easy for your attendees to remember and find your event.</p>\r\n                                        <p>\r\n                                            <img width=\"529\" height=\"200\" border=\"0\" title=\"Promote your Events Online\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/URL.png\"></p>\r\n                                        <p>\r\n                                            You can also use our <a href=\"/en_us/t/custom-buttons-and-links\"><strong>Buttons</strong> and <strong>Links</strong> features</a> to promote your event in emails or on outside websites.</p>\r\n                                        <p>\r\n                                            <img title=\"Promote your Events Online\" style=\"border-width: 0px; border-style: solid;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/buttons3.png\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        5</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Publicize your event on your website using widgets</strong></p>\r\n                                        <p>\r\n                                            Choose from a variety of <a href=\"/en_us/t/sell-tickets-on-your-website\">customizable widgets</a> to include on your outside website. From the entire event registration page to a countdown timer, to a calendar displaying all your live events, you can easily fine-tune your widget’s color scheme and then auto-generate the HTML code to drop into your website.</p>\r\n                                        <p>\r\n                                            Here\'s an example of the <a href=\"/en_us/t/embed-ticket-form-into-your-website\">ticket form</a> integrated into an outside website:</p>\r\n                                        <p>\r\n                                            <img width=\"600\" height=\"399\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_online/page.png\"></p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        &nbsp;</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip:</strong> To check out how you can easily send email invitations from within Eventbrite, go <a href=\"/en_us/t/email-invitations\">here</a>!</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        6</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong><strong>Monitor different sales channels using tracking links</strong> </strong></p>\r\n                                        <p>\r\n                                            Create as many individualized <a href=\"/en_us/t/track-my-promotional-activities\">tracking links</a> as you’d like for distribution to different promoters or marketing outlets. Then, track how each performs to refine your marketing plan for your next event!</p>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepnumber\">\r\n                                        &nbsp;</div>\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip:</strong> Check out the <strong><a href=\"http://eventbrite.appstores.com/t/im-looking-to/sell-more-tickets\">Reach New Markets</a></strong> section of our <strong>Partnerships and Resources</strong> page for a number of additional, third-party tools built for the Eventbrite platform! Also worth a look is the <strong><a href=\"http://blog.eventbrite.com/category/event-tips/event-marketing-tips\">Event Marketing</a></strong> section of our blog&mdash;another great resource for pro tips!</p>\r\n                                    </div>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229308', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('48', '12', 'Import or create contact lists', 'import-or-create-contact-lists', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Import or create contact lists</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Efficiently manage your contacts</li>\r\n                                <li>\r\n                                    Import contacts from outside databases and/or email services</li>\r\n                                <li>\r\n                                    Create contact lists from attendees of past event</li>\r\n                                <li>\r\n                                    Limit contact lists to fewer than 2000 contacts</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip: </strong>For more information about sending email invitations, click <a href=\"/en_us/t/email-invitations\">here</a>.</p>\r\n                                <p>\r\n                                    We recommend that you do <strong>not</strong> create any contact list with over 2000 contacts.&nbsp; Eventbrite has a limit of 2000 email invitations that can be sent per account per day, and sending email invitations to a contact list of more than 2000 email addresses can lead to irregularities in the delivery of the email invitations. If you have more than 2000 contacts, feel free to create another contact list!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <p>\r\n                                <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/GHem8ClMcqk?fs=1&amp;amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/GHem8ClMcqk?fs=1&amp;amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on the <strong>My Contacts</strong> tab located at the top of any page.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 45px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/my_contacts2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To create a new contact list, click <strong>Create a Contact List</strong>.</p>\r\n                                <img width=\"481\" height=\"224\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/create_button.png\" alt=\"How to create and save contact lists\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <strong>name of your new contact list</strong> and then choose from one of several <strong>import options</strong> to add contacts to your new list. See below for more details about these options.</p>\r\n                                <p>\r\n                                    <strong>Option 1:</strong> Import contacts from a text .txt or .csv file by uploading that file to Eventbrite.</p>\r\n                                <p>\r\n                                    <img width=\"421\" height=\"245\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/new_list.png\" alt=\"\"></p>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        If your contacts are in Excel format, you can convert that file in to .csv or .txt. Make sure your email addresses are in a column with the header <strong>email</strong>.</p>\r\n                                    <p>\r\n                                        OPTIONAL: <strong>Eventbrite</strong><strong> </strong>also imports the first and last names of your attendees on the list, if you have this information. You <strong>must </strong>include the headers <strong>firstname </strong>(no spaces)<strong> </strong>and <strong>lastname</strong> (no spaces) for the first name and last name columns, respectively.</p>\r\n                                    <p>\r\n                                        The <strong>email</strong>, <strong>firstname </strong>(no spaces), and <strong>lastname</strong> (no spaces) columns can be in any order. Any extra columns (\'age,\' for example) will be ignored. For text (.txt) files, a tab <strong>must</strong> be added after each column header and row entry--that is, press the <strong>Tab</strong> key on your keyboard after each column header and each row entry--even after the last column header and row entry.</p>\r\n                                    <p>\r\n                                        Here is an example of a text (.txt) file with <strong>email</strong> header:</p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/txt1.png\" alt=\"\">\r\n                                    <p>\r\n                                        Here is an example of a text (.txt.) file with <strong>email</strong>, <strong>firstname</strong>,<strong> </strong>and <strong>lastname </strong>headers:</p>\r\n                                    <img width=\"359\" height=\"78\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/txt2.png\" alt=\"\">\r\n                                    <p>\r\n                                        For .csv files, it is recommended to save as <strong>CSV (MS-DOS)</strong>.</p>\r\n                                    <p>\r\n                                        Here\'s an example of a .csv file with <strong>email</strong> header:</p>\r\n                                    <img width=\"199\" height=\"116\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/csv1.png\" alt=\"\">\r\n                                    <p>\r\n                                        Here\'s an example of a .csv file with <strong>email</strong>, <strong>firstname</strong>, and <strong>lastname</strong> headers:</p>\r\n                                    <img width=\"328\" height=\"116\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/csv2.png\" alt=\"\"></div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note: </strong>When importing a contact list, whether in .txt or .csv form, your list <strong>must</strong> contain <strong>email</strong>, <strong>firstname</strong> and <strong>lastname</strong> column headers, regardless of whether your list includes first and/or last names.</p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Option 2:</strong> Import contacts from your email address book by entering your email or user name and password and clicking <strong>Continue</strong>. You will have the option to include all contacts or pick specific contacts from each address book to include in your Contact List.</p>\r\n                                    <p>\r\n                                        <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/importer.png\" alt=\"\"></p>\r\n                                    <p>\r\n                                        <strong>Option 3:</strong> Import from the attendees of a past event in your Eventbrite account. Once you select an event (or multiple events) all attendees of the past event(s) will appear in the list of invitation recipients for this newest event, and you will have the option of removing any recipients before sending the invitation.</p>\r\n                                    <p>\r\n                                        <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/past_events.png\" alt=\"\"><br>\r\n                                        <strong>Option 4:</strong> Manually enter email addresses to include in your contact list.</p>\r\n                                    <p>\r\n                                        <img width=\"492\" height=\"229\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/manual_entry.png\" alt=\"\"></p>\r\n                                </div>\r\n                                <div class=\"step\">\r\n                                    <div class=\"stepcontent\">\r\n                                        <p>\r\n                                            <strong>Tip: </strong>Click <a href=\"/en_us/t/manage-contact-lists\">here</a> to learn how to manage your contact lists and send email invitations using contact lists.</p>\r\n                                    </div>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229312', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('49', '12', 'Sell tickets on your website', 'sell-tickets-on-your-website', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Sell tickets on your website</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Embed a widget in your site to drive ticket sales</li>\r\n                                <li>\r\n                                    Ticket form or your entire event page can be embedded</li>\r\n                                <li>\r\n                                    Calendar or countdown widgets are other options</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Your outside website must support iframes in order to embed these widgets. <strong>Wordpress users</strong>, for instance, will need install a <a href=\"http://wordpress.org/extend/plugins/search.php?q=embed+iframes&amp;amp;sort=\">plugin</a> allowing you to embed iframes in your site. Also, widgets cannot be used in conjunction with our <a href=\"/en_us/t/invitationonly-events\">invite-only feature</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the different widgets that are available, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> or <a href=\"https://www.eventbrite.com/login\">log in</a> to Eventbrite. From the <strong>My Events</strong> page, click on the name of your published event and then check out the <strong>Promote</strong> section of the features menu on the left side of the page.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The&nbsp;<strong>Promote</strong> section won\'t appear until you\'ve published your event or made it live.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Ticket form widget:</strong> To place a ticket order form for your published event on your website, first select <strong>Add Ticket Form to Website</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"232\" height=\"230\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/form.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You\'ll see a preview of the ticket form at the top. Scroll down, then copy and paste the ready-made HTML code into your own website.</p>\r\n                                <p>\r\n                                    Visitors to your website can now initiate a ticket sale right from your site!</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"315\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/ticket_form3.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Entire registration page:</strong> To integrate the entire event page into your own website, select <strong>Add Registration to Website</strong>.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/reg%20form%20manage.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You\'ll see a preview of the registration page at the top. Scroll down, then copy and paste the ready-made HTML code into your own website.</p>\r\n                                <p>\r\n                                    Visitors to your website can now see all your event details and initiate a ticket sale right from your site!</p>\r\n                                <p>\r\n                                    <img width=\"601\" height=\"539\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/event%20registration.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Event calendar widget:</strong> To display a calendar widget showing all your upcoming events, select <strong>Display Event Calendar on Site</strong>.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/calendar%20manage.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Choose from a variety of customization options, including selecting a color scheme to match that of your website. You can choose to display events by all organizers in your account, or just the organizer of the event you\'re currently accessing. You can also choose to display both public and private events, or only public events.</p>\r\n                                <p>\r\n                                    When you\'re finished customizing the widget, click the <strong>Create your widget</strong> button and the widget\'s HTML code will be displayed in the box below. Copy and paste the code into your website.</p>\r\n                                <p>\r\n                                    Visitors to your website can click on a highlighted date, read more about the event below the calendar and then click on the name of the event to go to the event page and register!</p>\r\n                                <p>\r\n                                    <img width=\"604\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/calendarwidget.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Event countdown widget:</strong> To display a countdown widget for your event, select <strong>Display Countdown on Site</strong>.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/countdown%20manage.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Choose from a variety of customization options, including selecting a color scheme to match that of your website. When you\'re finished customizing the widget, select the <strong>Create Widget</strong> button and the widget\'s HTML code will be displayed in the box below. Copy and paste the code into your website.</p>\r\n                                <p>\r\n                                    Visitors to your website can click on <strong>Register Now</strong> button to register for your event!</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"563\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/countdown2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To check out other handy tools you can use to promote your event, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229318', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('50', '12', 'Publish event to Facebook', 'publish-event-to-facebook', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Publish event to Facebook</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Market to your Facebook friends and community</li>\r\n                                <li>\r\n                                    Not available for <a href=\"/en_us/t/repeating-events\">repeating events</a></li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The <strong>Publish to Facebook</strong> option will not appear for <a href=\"/en_us/t/repeating-events\">repeating events</a>. If you would like to publicize your repeating event on Facebook, you will need to manually create an event directly in Facebook.</p>\r\n                                <p>\r\n                                    You can also check out the third-party Facebook apps <a href=\"http://appbistro.com/products/eventbrite#\">Eventbrite for Pages</a> (read more about it <a href=\"http://blog.eventbrite.com/sell-tickets-through-facebook-version-two-of-eventbrite-for-pages-launches/\">here</a>) and <a href=\"http://eventbrite.appstores.com/apps/sell-tickets-on-facebook\">GroSocial</a> (read more about it <a href=\"http://blog.eventbrite.com/sell-tickets-on-facebook/\">here</a>).</p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After publishing your event on Eventbrite, click on <strong>My Events </strong>on the top of the page. Clicking on the name of the event will redirect you to the <strong>Manage</strong> page for your event. Select <strong>Publish to Facebook</strong> from the features menu of the left side of the page.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/features%20menu.png\" border=\"0\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now click <strong>Connect with Facebook</strong>.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/connect.png\" border=\"0\" height=\"154\" width=\"610\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you\'re not already logged in to Facebook, a <strong>Connect with Facebook</strong> screen will appear. Log in using your Facebook log-in email and password.</p>\r\n                                <p>\r\n                                    (If you are already logged in to Facebook, skip to step 5 below.)</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/login.png\" border=\"0\" height=\"285\" width=\"612\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you\'ve never before accessed Facebook from within your Eventbrite account, you\'ll need to grant permission to Eventbrite to publish and update your event details on Facebook. (Don\'t worry, we\'ll never publish an event on Facebook unless you explicitly tell us to.)</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/permission.png\" border=\"0\" height=\"290\" width=\"612\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'re successfully logged in to Facebook, your basic event details--event name, date/time, location, category, description--will appear. You can edit or add new information to the event name and/or description fields to modify how the event will appear in Facebook. (Facebook controls which fields are editable, so if you would like to edit any other fields, you would need to do so directly from the <strong>Edit</strong> page of your event in Eventbrite.)</p>\r\n                                <p>\r\n                                    If you are the administrator of a fan or group page on Facebook, you can choose to publish to either of these pages, or to your personal page. (Facebook only allows you to publish to one of these pages directly from within Eventbrite. If you\'d like to publish to more than one page, you\'d need to manually create an event and publish to those additional pages directly from within Facebook.)</p>\r\n                                <p>\r\n                                    When you\'re finished, click <strong>Publish to Facebook</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Facebook requires that your event have a physical address in order to publish it to Facebook directly from within Eventbrite. This means that you must specify a physical address for your event on your Eventbrite event page. If you have marked your event as an \'online event\' in Eventbrite, you\'ll need to manually create the Facebook event in Facebook, and then include a link to your Eventbrite event page in the Facebook event\'s description.&nbsp;</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/publish.png\" border=\"0\" height=\"612\" width=\"608\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Screenshot --><!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Well done! You have now published your event as a Facebook event. A small window will appear to allow you to post a message to your wall on Facebook, if you wish.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/wall.png\" border=\"0\" height=\"336\" width=\"609\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You will see a message telling you that your event has been published to Facebook, with links at the top of the page to view and to edit the event in Facebook. At the bottom of the page are buttons to update/edit the Facebook event, as well as to cancel/remove the event in Facebook.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/published.png\" border=\"0\" height=\"614\" width=\"605\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!-- Step 1 -->		<div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Your Facebook friends and contacts will now be able to view details about your event and to register by clicking on the link to your Eventbrite event page. Success is yours!</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_facebook/facebook%20event.png\" border=\"0\" height=\"496\" width=\"607\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> For an interesting read, check out this blog <a href=\"http://blog.eventbrite.com/social-commerce\">post</a> breaking down the value of each Facebook share--versus other forms of social media sharing--in terms of ticket sales that we have seen here at Eventbrite.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>We recommend that your event\'s date and time are correct when publishing to Facebook as the&nbsp;<strong>Time</strong> field on a Facebook event is not able to be changed if the event was created more than a week from the date you are attempting to edit it or if more than 5,000 people have been invited to your event through Facebook.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229323', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('51', '12', 'Twitter promotion', 'twitter-promotion', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Twitter promotion</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Create brand awareness and social-media buzz around your event</li>\r\n                                <li>\r\n                                    Give your followers opportunity to discuss your event</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you do not have a Twitter account, follow this link to <a title=\"Signup for Twitter\" target=\"_blank\" href=\"https://www.twitter.com/signup\">sign up now!</a> We recommend creating a Twitter handle that includes your event or organization\'s name to help build up your event\'s brand.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    By default, each publicly listed event page will have a Twitter share button. If your event is private, you can display this information by selecting \'Attendees can share the event with their friends on sites like Facebook, Twitter and LinkedIn\' from Step 8 on the Edit tab of the event.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"280\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/twitter_twittershare.png\" alt=\"Twitter share\"></p>\r\n                                <p style=\"text-align: left;\">\r\n                                    When an attendee clicks on this link, a preloaded tweet will appear with a link to your event. The attendee can then customize this message and post it to their Twitter feed!</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/twitter_preloadedtweet.png\" alt=\"Preloaded tweet\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, you will want to add your own Twitter information to your event page. When you reach <strong>Step 3: Add Event Details</strong>, we recommend including a link to your Twitter profile to encourage people to follow your Twitter account.</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"208\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/twitter_eventdetails.png\" alt=\"Event details\"></p>\r\n                                <p style=\"text-align: left;\">\r\n                                    This way, you can post event updates as well as any relevant news. Some organizers will even run special promotions through their Twitter account for free tickets, giveaways, and other interactive goodies.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also create a custom survey to collect Twitter handles from your attendees so you can start following them. If you would like, you can display the attendee list on the registration page so attendees can find each other on Twitter! (<a target=\"_blank\" href=\"/en_us/t/custom-questions-for-attendees\">Learn how to collect custom information</a>)</p>\r\n                                <p style=\"text-align: center;\">\r\n                                    <img width=\"512\" height=\"164\" border=\"0\" title=\"Twitter attendee list\" src=\"http://i531.photobucket.com/albums/dd353/eventbrite/twitter_tutorial_attendee_list.jpg\" alt=\"Twitter attendee list\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Now promote your event Twitter handle by including it in all your email communications to your attendees as a way to find out the latest updates about your event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The best way to promote your event on Twitter is to engage your followers! You can do this by asking questions, introducing speakers, or offering special deals. Have a conversation with your followers!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Create a hashtag for your event and use it often! Effective, memorable hashtags are one of the best ways to promote your event. Attendees can use this hashtag to reference your event in their tweets. An effective hashtag will gain popularity and attendees will want to be involved in the discussion!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Tweet about the key moments in your event life cycle: <i>early bird specials</i>, <i>discount code offerings</i>, <i>24 hour notice before registration closes, 10 tickets left, etc.</i></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                9</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After the event, continue to use Twitter not only to engage your current followers, but also to attract more followers interested in coming to your next event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <br>\r\n                                <br>\r\n                                &nbsp;</p>\r\n                        </div>\r\n\r\n                        <hr/>\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Twitter promotion</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Create brand awareness and social-media buzz around your event</li>\r\n                                    <li>\r\n                                        Give your followers opportunity to discuss your event</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you don’t have a Twitter account, follow this link to <a href=\"https://twitter.com/signup\">sign up now</a>!</p>\r\n                                <p>\r\n                                    We recommend creating a Twitter handle that includes your event’s or organization\'s name to help build up your event\'s brand.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    By default, each public event page will have multiple Twitter share buttons.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"304\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/event%20pagea.png\"></p>\r\n                                <p>\r\n                                    A Twitter share button will also appear on the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation page and order confirmation email</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Note: </b> If your event is private, you can display the Twitter share buttons by going to the <b>Event Details</b> page and selecting the option to <b>Allow sharing on Facebook, Twitter and LinkedIn</b> in <b>Step 3: Privacy Settings</b>. (To get to the <b>Event Details</b> page, click on <b>My Events </b>at the top of the screen, and then click on <b>Edit</b>, under <b>Quick Links</b>, to the right of the name of your event.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When an attendee clicks on this link, a preloaded tweet will appear with a link to your event. The attendee can then customize this message and post it to their Twitter feed!</p>\r\n                                <p>\r\n                                    <img width=\"400\" height=\"188\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/share.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, you’ll want to add a link to your Twitter profile in the event details on your event page to encourage people to follow your Twitter account. This way, you can post event updates as well as any relevant news. Some organizers will even run special promotions through their Twitter account for free tickets, giveaways, and other interactive goodies.</p>\r\n                                <p>\r\n                                    We also recommend that you include your Twitter feed on your <a href=\"/en_us/t/organizer-profile-pages\">organizer profile page</a>.</p>\r\n                                <p>\r\n                                    <img width=\"618\" height=\"317\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/organizer%20profile.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can create <a href=\"/en_us/t/custom-questions-for-attendees\">custom registration questions</a> to collect Twitter handles from your attendees so you can start following them. You can also <a href=\"/en_us/t/list-attendees-on-event-page\">display the attendee list</a> on your event page so attendees can find one another on Twitter!</p>\r\n                                <p>\r\n                                    <img width=\"623\" height=\"393\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/promote_on_twitter/attendee%20list.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Create a hashtag for your event and use it often! Effective, memorable hashtags are one of the best ways to promote your event. Attendees can use this hashtag to reference your event in their tweets. An effective hashtag will gain popularity, and attendees will want to be involved in the discussion!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Promote your Twitter handle and/or hashtag by including it in all your <a href=\"/en_us/t/email-invitations\">email invitations</a> and all your <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">email communications</a> to your attendees as a way to find out the latest updates about your event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Make sure to engage your Twitter followers! You can do this by asking questions, introducing speakers, or offering special deals.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                9</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Tweet about the key moments in your event life cycle: <em>early-bird specials,</em> <i><a href=\"/en_us/t/discount-codes-access-codes\">discount code</a> offerings</i>, <i>24-hour notice before registration closes, 10 tickets left, etc.</i></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                10</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After the event, continue to use Twitter not only to engage your current followers, but also to attract more followers interested in coming to your next event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229330', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('52', '12', 'Resend and manage sent invitations', 'resend-and-manage-sent-invitations', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Resend and manage sent invitations</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Track invite stats, such as opens, clicks, and registrations</li>\r\n                                <li>\r\n                                    Resend invites--e.g., to only those who opened the invite but didn\'t respond</li>\r\n                                <li>\r\n                                    You can edit the invite before resending</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> The <strong>Manage Invitations</strong> will not appear in your event\'s <strong>Manage</strong> page until you have scheduled or sent out email invites. In <strong>Manage Invitations</strong>, you\'ll see a <strong>status </strong>shown for each of your recipients. Click <a href=\"/en_us/t/invitation-statuses-explained\">here </a>to learn more about what each of these statuses means.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To view the recipients of your sent invites, go to your event\'s <strong>Manage</strong> page by clicking on <strong>My Events</strong> at the top of the page, and then clicking on the name of your event. Once on the <strong>Manage </strong>page, select <strong>Manage Invitations</strong> on the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_status_of_invites/manage%20status%281%29.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Each batch of invitations you send will show as a separate line item. You\'ll see the subject of the email invite, the number of recipients, and the date it was sent. If you click <strong>Copy</strong>, you\'ll be able to edit your invite before sending it out to more invitees.</p>\r\n                                <p>\r\n                                    If you\'ve sent multiple rounds of invitations, click <strong>View all invited guests </strong>in the lower right to show the <a href=\"/en_us/t/invitation-statuses-explained\">statuses</a> for all recipients of all rounds of invites.</p>\r\n                                <p>\r\n                                    To resend an invite to all recipients or a subset of recipients--e.g., those who haven\'t responded--first use the <strong>Filter invites</strong> dropdown menu and then click the <strong>Edit and Resend Invites</strong> link. At this point, you\'ll have the opportunity to edit the invite and recipient list before sending it out.</p>\r\n                                <p>\r\n                                    <img width=\"609\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_status_of_invites/resendinvites.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also track invitations that you have scheduled to be sent at a future date by clicking the <strong>Scheduled </strong>tab.&nbsp; You can preview, edit, copy, or delete any scheduled invites.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"275\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_status_of_invites/managestatus33.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Click <a href=\"/en_us/t/rsvp-tracking\">here</a> to learn more about using the RSVP feature to give your invitees a few options to respond to your email invitation.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229336', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('53', '12', 'Invitation statuses explained', 'invitation-statuses-explained', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Invitation statuses explained</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Definitions of all email statuses and RSVP labels</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> \'Complaint\' means that an invitee has marked the email invitation to your event as spam. Make sure to remove this attendee from your address book and/or contact lists before sending out any more email invitations to any of your events!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To learn about how to access <b>Manage Invitations</b>, check out our tutorial <a href=\"/en_us/t/resend-and-manage-sent-invitations\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once on the <b>Manage Invitations</b> page, you will see one of a number of statuses next to each of the email addresses to which you have sent an invitation.</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"513\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/status-of-invitations_definitions/managestatus202.png\"></p>\r\n                                <p>\r\n                                    Here are the definitions to help decode each status:</p>\r\n                                <p>\r\n                                    <b>Opened</b>: The recipient opened, loaded the images in the invitation email, and viewed your invitation. Note that the invitation will continue to show as <b>Sent</b> if the recipient has not loaded the images in the email invitation. Email-tracking technology requires that images be loaded in an email in order to activate the tracking pixel that communicates with our mail servers that the email has been opened.</p>\r\n                                <p>\r\n                                    <b>Clicked</b>: Your guest clicked on the invitation and viewed your event page.</p>\r\n                                <p>\r\n                                    <b>Bounced</b>: The invite wasn\'t delivered, as the email address was invalid. Eventbrite won\'t send invites for your events to this email address again.</p>\r\n                                <p>\r\n                                    <b>Unsubscribed</b>: The recipient unsubscribed from receiving your invitations. Eventbrite won\'t send invites for your events to this email address again.</p>\r\n                                <p>\r\n                                    <b>Complaint</b>: The recipient has marked the invite as spam. <b>Be sure to remove any of these email addresses marked as \'Complaint\' from your address book and/or <a href=\"/en_us/t/import-or-create-contact-lists\">contact list</a> before sending out any more invitations to any of your events!</b></p>\r\n                                <p>\r\n                                    <b>Undelivered/Blocked</b>: The attempt to deliver the email invite was unsuccessful. However, Eventbrite will continue to send email invites for your events to this email address.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you\'re using the <b>RSVP </b>feature, then you may see some of these labels:</p>\r\n                                <p>\r\n                                    <b>Responded</b>: Your invitee has responded yes or no to the invitation.</p>\r\n                                <p>\r\n                                    <b>Not Responded: </b>Those who haven\'t responded yet.</p>\r\n                                <p>\r\n                                    <b>Yes</b>: Success! They\'re coming.</p>\r\n                                <p>\r\n                                    <b>No</b>: They\'re not able to make it.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229341', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('54', '12', 'RSVP tracking', 'rsvp-tracking', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>RSVP tracking</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Invitees can respond Yes, No or Undecided</li>\r\n                                <li>\r\n                                    View responses on Manage Invitations screen in Manage page</li>\r\n                                <li>\r\n                                    You must enable the RSVP option for each round of invites you send</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>By default, the RSVP option will be turned <strong>off</strong>, and your invitees will only see an <strong>Attend </strong>button when they open your email invite. You must enable the RSVP option for each round of email invites that you send out in order to give your invitees the option to respond <strong>Yes</strong>, <strong>No </strong>or <strong>Maybe</strong>. See below for instructions on how to enable the RSVP option!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve created and published your event page, click on the title of your event on the <strong>My Events</strong> page. Then select <strong>Create Invitations</strong> from the features menu on the left side of the <strong>Manage</strong> page for that event.&nbsp;</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_status_of_invites/manage%20status(1).png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on <strong>Customize</strong> at the top of the preview invite.&nbsp;</p>\r\n                                <img width=\"608\" height=\"419\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/rsvp_responses/editmessage2.png\" alt=\"\"><br><p>\r\n                                    In the <strong>Customize Invitations</strong> window, select the <strong>Track Responses with RSVP</strong> option and click <strong>Save</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"507\" height=\"367\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/rsvp_responses/option.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    After you\'ve enabled the RSVP feature as described above, you can further customize, then send, your <a href=\"/en_us/t/email-invitations\">email invites</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When your invitee receives their email invite, they will have the option to respond <strong>Yes</strong>, <strong>No</strong> or <strong>Maybe.&nbsp;</strong></p>\r\n                                <img width=\"576\" height=\"504\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/rsvp_responses/invite2.png\" alt=\"\"><p>\r\n                                    Once the invitee clicks on their response in the email, they will be taken to your event page, where they will then make their selection--<strong>Yes</strong>, <strong>No</strong> or <strong>Maybe</strong>--and either click <strong>Register</strong> (for free events) or <strong>Order Now </strong>(for paid events) if the response is <strong>Yes</strong>, or <strong>RSVP Maybe </strong>or <strong>RSVP No</strong>. If the attendee chooses No or Maybe, they will have the option to include a message that will be visible only to you.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"601\" height=\"346\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/rsvp_responses/event_page.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To view the RSVP responses for your event, as well as any messages your invitees have included with an RSVP of No or Maybe, click on <strong><a href=\"/en_us/t/resend-and-manage-sent-invitations\"><strong>Manage Invitations</strong></a></strong> on the left side of the <strong>Manage</strong> page for your event.&nbsp;</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_status_of_invites/manage%20status(1).png\" alt=\"\"></p>\r\n                                <p>\r\n                                    You will see all of your RSVP responses for your event.</p>\r\n                                <img width=\"602\" height=\"336\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/rsvp_responses/track.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229346', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('55', '12', 'Track my promotional activities', 'track-my-promotional-activities', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Track my promotional activities</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Create unique tracking link for each promoter or marketing outlet</li>\r\n                                <li>\r\n                                    Access visits, tickets sold and sales for each link</li>\r\n                                <li>\r\n                                    Use data to strategically apply your promotional muscle</li>\r\n                                <li>\r\n                                    See benefits of promotion Eventbrite does for you automatically</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> For more information about promoting your published event, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, go to your event\'s <strong>Manage</strong> page by clicking on <strong>My Events</strong> at the top of the page, and then clicking on the name of your event. Once on the <strong>Manage </strong>page, select<strong> Track Attendee Activity</strong> on the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img alt=\"Use Tracking Links\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_affiliate_links/promocode3.png\" title=\"Use Tracking Links\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can create a new tracking link by first entering a <strong>Name</strong> for your tracking link. As you type, you\'ll see that a unique tracking link is created in the box below. Copy this link to your clipboard, and distribute it to the relevant promoter or marketing outlet. Then click <strong>Create</strong>.&nbsp;</p>\r\n                                <img alt=\"How to create and manage affiliate tracking links\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_affiliate_links/create_link.png\" border=\"0\"><p>\r\n                                    When the tracking link is used for the first time, a statistical box will appear on the <strong>Attendee Tracking Activity</strong> page in the <strong>Your tracking links</strong> section that will track the <strong>number of visits</strong>, <strong>tickets sold</strong> and <strong>amount collected</strong> for that particular tracking link.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'ve created a <a href=\"/en_us/t/custom-urlweb-address-for-event-page\">custom URL</a> for your event, you\'ll see that two boxes appear when you are creating a tracking link. You may copy either link and distribute it to your promoter since both links will lead visitors to your event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can check on the progress of the existing tracking links in your event by clicking on <strong>Track Attendee Activity</strong> from the&nbsp;<strong>Manage</strong> page of your event. There, you can view a list of all the tracking links in your event and the number of <strong>visits</strong>, <strong>tickets sold</strong>, and total <strong>sales</strong>, that each has generated. You can view an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a> by clicking on <strong>Attendee Report </strong>from the <strong>Quick Actions</strong> dropdown menu to the right of the name of the tracking link.</p>\r\n                                <p>\r\n                                    At the top are links that have been automatically created by Eventbrite to generate even more sales without additional effort from you. Below this are any tracking links that you have created, as well as the button to create a new link.</p>\r\n                                <p>\r\n                                    <img alt=\"How to create and manage affiliate tracking links\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_affiliate_links/links.png\" height=\"575\" width=\"610\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Here\'s a guide to the codes that may appear in your list of Eventbrite-generated links. If you see any of these codes listed for your event, you\'ll know the visits and revenue that Eventbrite drove to your event without any additional effort from you!</p>\r\n                                <p>\r\n                                    - <strong>atom</strong>: Eventbrite RSS feeds</p>\r\n                                <p>\r\n                                    - <strong>efblike</strong>: Facebook news feed from an attendee liking an event</p>\r\n                                <p>\r\n                                    - <strong>efbnen</strong>: Facebook news feed event name link</p>\r\n                                <p>\r\n                                    - <strong>efbnreg</strong>: Facebook news feed register link</p>\r\n                                <p>\r\n                                    - <strong>eorg</strong>: Eventbrite <a href=\"/en_us/t/organizer-profile-pages\">organizer page</a></p>\r\n                                <p>\r\n                                    - <strong>eorgf</strong>: featured event on your Eventbrite <a href=\"/en_us/t/organizer-profile-pages\">organizer page</a></p>\r\n                                <p>\r\n                                    - <strong>erec</strong>: Eventbrite recommended your event to attendees in their Eventbrite account</p>\r\n                                <p>\r\n                                    - <strong>esli</strong>: LinkedIn share link on your event page</p>\r\n                                <p>\r\n                                    - <strong>estw</strong>: Twitter share link on your event page</p>\r\n                                <p>\r\n                                    - <strong>etckt</strong>: <a href=\"/en_us/t/embed-ticket-form-into-your-website\">ticket widget </a></p>\r\n                                <p>\r\n                                    - <strong>eweb</strong>: Eventbrite <a href=\"/en_us/t/embed-registration-form-into-your-website\">website integration</a></p>\r\n                                <p>\r\n                                    - <strong>incompleteregistration</strong>: automatic email sent when a registration isn\'t completed</p>\r\n                                <p>\r\n                                    - <strong>rss</strong>: Eventbrite RSS feeds</p>\r\n                                <p>\r\n                                    - <strong>auto</strong>: event search auto-suggest</p>\r\n                                <p>\r\n                                    - <strong>autohome</strong>: event search auto-suggest on our homepage</p>\r\n                                <p>\r\n                                    - <strong>connect</strong>: <a href=\"/en_us/t/publish-event-to-facebook\">Facebook connect</a></p>\r\n                                <p>\r\n                                    - <strong>eivtefrnd</strong>: \'invite friend\' link on your event page</p>\r\n                                <p>\r\n                                    - <strong>estwbreg</strong>: Twitter tweet button link</p>\r\n                                <p>\r\n                                    - <strong>zvents</strong>: Zvents events auto listing</p>\r\n                                <p>\r\n                                    - <strong>eotw</strong>: links from Twitter to your Eventbrite <a href=\"t/organizer-profile-pages-and-the-my-profile-tab\">organizer page</a></p>\r\n                                <p>\r\n                                    - <strong>eoln</strong>: links from LinkedIn to your Eventbrite <a href=\"t/organizer-profile-pages-and-the-my-profile-tab\">organizer page</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To take a quick look at the progress of the Eventbrite-generated Tracking Links, you can simply scroll down in your event\'s <strong>Event Summary </strong>page until you see the <strong>Eventbrite Promotion Sources</strong> box. There you\'ll see the list of tracking links that we\'ve generated and how much money they\'ve helped you make in gross ticket sales.</p>\r\n                                <p>\r\n                                    <img alt=\"How to create and manage affiliate tracking links\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/track_affiliate_links/eventsumm.png\" width=\"600\"></p>\r\n                                <p>\r\n                                    You can always view the entire list of tracking links by clicking <strong>Track attendee activity</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To learn how you can use the <strong>Eventbrite Promotion Sources</strong> summary to fine-tune your future marketing efforts, check out <a href=\"http://blog.eventbrite.com/eventbrite-promotion-tools-pay-off-part-2-of-3\">this post</a> on our blog!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229353', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('56', '12', 'Organizer profile pages', 'organizer-profile-pages', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Organizer profile pages</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    List all your public events on one page and drive customers there</li>\r\n                                <li>\r\n                                    Establish credibility and a strong brand presence</li>\r\n                                <li>\r\n                                    Match your brand\'s color scheme</li>\r\n                                <li>\r\n                                    Include Facebook and/or Twitter feed</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Customizing and publicizing your organizer profile page is a great way to drive all your potential attendees to a single page listing all your public events. Be sure to blast out its link via email and your online social networks, post it on your own website, and use it on any flyers or advertising materials! Read more below about all the different features and options you can customize in the <strong>My Profile</strong> tab.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">The organizer profile page</span></strong>\r\n                        </p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Here\'s an example organizer profile page:</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"568\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/page.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    - Notice the social-sharing options at the top of the page, useful so visitors can blast your page to their friends and contacts on Facebook, Twitter and LinkedIn.</p>\r\n                                <p style=\"text-align: left;\">\r\n                                    - A logo and short bio show up at the top of the page.</p>\r\n                                <p style=\"text-align: left;\">\r\n                                    - All your current and past public events are displayed below that, as well as a featured event module. You can also display a clickable link to your outside website, the number of public events you\'ve held, the top categories and primary locations of your public events.</p>\r\n                                <p style=\"text-align: left;\">\r\n                                    - Each event with tickets on sale has a prominent <strong>Attend</strong> button next to it, to drive even more ticket sales your way!</p>\r\n                                <p style=\"text-align: left;\">\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <strong><span style=\"font-size: large;\">Editing your organizer profile page via the My Profile tab</span></strong>\r\n                        </p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To edit your organizer profile, click on the <strong>My Profile</strong> tab at the top of the screen.</p>\r\n                                <p>\r\n                                    <img width=\"604\" height=\"374\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/top.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the top section of the <strong>My Profile </strong>page, you\'ll see:</p>\r\n                                <p>\r\n                                    - A <strong>dropdown menu</strong> showing all existing organizers in your account. (You\'ve most likely created these organizers on the <strong>Edit </strong>page of your events, in <strong>Step 6: Add Host</strong>, if you\'re using our old design, or <strong>Step 1: Add your Event Details</strong>, if you\'re using our <a href=\"/en_us/t/more-info-about-our-new-design\">new design</a>.) You can edit the profile page for an existing organizer, or create a new organizer and related profile page from this dropdown.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To delete an unused organizer, first save your changes in <strong>My Profile</strong>. Then click on the <strong>Account </strong>tab in the upper right and click on <strong>Unused Venues &amp; Organizers</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    - An <strong>organizer name</strong> field that will display at the top of the profile page. (If you leave this field blank, \"About\" will show at the top of the profile page.)</p>\r\n                                <p>\r\n                                    - You can upload an <strong>organizer logo</strong> by clicking <strong>Browse</strong>, selecting an image file from your computer, and then clicking <strong>Upload</strong>, which turns green once you\'ve chosen a valid file. Parameters governing the image file you choose are displaying in this section.</p>\r\n                                <p>\r\n                                    - Throw up a short bio or description in the <strong>about the organizer</strong> section that will display under the organizer name at the top of the profile page. (You can click the <strong>Also use this description for event pages </strong>option to have this description show in the <strong>Hosted by</strong> section on all event pages that have this organizer selected as the host, overriding any description that was previously there.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>organizer settings </strong>section you can choose to:&nbsp;</p>\r\n                                <p>\r\n                                    - Display a clickable URL for your <strong>external website</strong>.</p>\r\n                                <p>\r\n                                    - Show the <strong>number of past events</strong> that have been held by this organizer. (Private events are not included in this tally, as only public events are displayed on the organizer profile page.)</p>\r\n                                <p>\r\n                                    - Display your one or two <strong>top event categories</strong>.</p>\r\n                                <p>\r\n                                    - Show your one or two <strong>top event locations</strong>.</p>\r\n                                <p>\r\n                                    - Display only events by this organizer, or by all organizers in your account. (You match an event to an organizer on the <strong>Edit </strong>page for an event, in <strong>Step 6: Add Host</strong>, if you\'re using our old design, or <strong>Step 1: Add your Event Details</strong>, if you\'re using our <a href=\"/en_us/t/more-info-about-our-new-design\">new design</a>.)</p>\r\n                                <p>\r\n                                    - Feature a particular event. (By default, the next public event is shown.)</p>\r\n                                <p>\r\n                                    - Customize the organizer profile page URL. Choose a memorable URL, which you can then publicize to your potential attendees! Optimizing your URL is also a great way to rank better in search engines, so choose a URL that utilizes <a href=\"http://www.google.com/insights/search/\">targeted keywords</a>.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"272\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/organizer_settings2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Promote your organizer profile page with an Eventbrite button! You can apply an Evenbrite button to your website or blog so that visitors to the page can click on it to view your organizer profile. First, choose the size of the button that you\'d like to display on your webiste. Then click <strong>Copy to Clipboard</strong> and paste the HTML code into your website or blog.&nbsp;&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"609\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/org-profile-button.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Include your <strong>Facebook Page and/or Twitter feeds</strong> on your profile page to incorporate your wider social media presence, foster greater conversation about your brand, and provide your attendees with the latest info about your events.</p>\r\n                                <p>\r\n                                    - Facebook Page: You can incorporate the feed for your organization\'s Facebook Page by entering its URL (e.g., http://www.facebook.com/<strong>Eventbrite</strong> or http://www.facebook.com/<strong>pages/Eventbrite/14829466723</strong>).</p>\r\n                                <p>\r\n                                    - Twitter feed: You can enter either a Twitter username to display the feed for that username (e.g., @Eventbrite) or a search query (e.g., #sfevents) to display the real-time feed of search results.</p>\r\n                                <p>\r\n                                    - If you choose to display both a Facebook and a Twitter feed, the Facebook feed will always be displayed by default. Also, due to a limitation with Facebook\'s API, the default color scheme for the Facebook feed will always show, regardless of whether you customize the color scheme for your profile page (as described below).</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"179\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/communicate.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Customize the <strong>organizer profile page colors</strong> using either a preset template--our example above uses the \"Sand\" palette--or by designing your own custom color scheme. Older users will see an <strong>Advanced </strong>tab that controls whether:</p>\r\n                                <p>\r\n                                    - The selected colors will always be applied to the organizer profile page, or</p>\r\n                                <p>\r\n                                    - The colors of a given event page will be applied to the organizer profile page when an attendee clicks on the \"View more events\" link in the <strong>Hosted by </strong>section on that event page.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"196\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/organizer-profile-pages-and-the-my-profile-tab/colors.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve tricked out the page to your liking, make sure to click <strong>Save</strong>. You can click <strong>View Profile</strong> to take a look at the finished product. Now take the <strong>organizer page URL</strong> and publicize it to the world!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229459', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('57', '12', 'MailChimp and Eventbrite', 'mailchimp-and-eventbrite', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>MailChimp and Eventbrite</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Greater flexibility with invitation design using MailChimp-Eventbrite integration</li>\r\n                                <li>\r\n                                    Send more than 2000 email invites per day</li>\r\n                                <li>\r\n                                    Benefit from MailChimp\'s full-service email-marketing expertise</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re using our <a href=\"/en_us/t/invitationonly-events\">invite-only feature</a> for your event, you will need to send <a href=\"/en_us/t/email-invitations\">email invitations</a> from within Eventbrite, as the invite-only feature is incompatible with our integration with MailChimp.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To use MailChimp to send invites for an event, first <a href=\"/en_us/t/event-page-creation\">create and publish</a> your event in Eventbrite. Now click on <strong>My Events</strong> at the top of the page, and then click on the name of your published event to go to its <strong>Manage</strong> page. Select <strong>Create Invitations</strong> from the feature menu on the left side of the <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/integrate-eventbrite-and-mailchimp/create_invitations.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on the <strong>Use MailChimp!</strong> button on the right side of the next screen. A new tab or window will open, and you will be taken to MailChimp and prompted to log in to your MailChimp account (or to sign up for one if you haven\'t already).&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"313\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/integrate-eventbrite-and-mailchimp/use_mailchimp.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Go <a target=\"_blank\" href=\"http://kb.mailchimp.com/article/how-do-i-send-my-eventbrite-event-invitation-through-mailchimp\">here </a>to learn what to do in MailChimp from this point on in order to send your invites.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You may be prompted by MailChimp to enter your Eventbrite API user key if you haven\'t already.</p>\r\n                                <p>\r\n                                    To find your key, go back to Eventbrite, make sure you\'re logged in, and click <strong>Account</strong> in the upper right. Then click on <strong>API User Key</strong> on the vertical menu on the left side of the page. You will find your key in the box labeled <strong>Your API User Key </strong>at the top of the next page.</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"332\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/integrate-eventbrite-and-mailchimp/API_user_key.png\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'re wanting to connect your Eventbrite and MailChimp accounts without sending out email invitations right now, click <a target=\"_blank\" href=\"http://kb.mailchimp.com/article/how-do-i-connect-to-eventbrite-in-mailchimp/\">here</a> to learn how. Go <a target=\"_blank\" href=\"http://kb.mailchimp.com/article/how-do-i-import-my-list-from-eventbrite/\">here </a>to learn more about importing a contact list into MailChimp from Eventbrite. If you\'re having problems integrating MailChimp and Eventbrite, go <a target=\"_blank\" href=\"http://kb.mailchimp.com/article/my-integration-isnt-working-no-one-is-being-added-to-my-list/\">here </a>to troubleshoot.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229464', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('58', '12', 'Paperless Post and Eventbrite', 'paperless-post-and-eventbrite', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Paperless Post and Eventbrite</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Send designer event invitations using Paperless Post</li>\r\n                                <li>\r\n                                    Invitations fully integrated with your Eventbrite event</li>\r\n                                <li>\r\n                                    Greater range of customization options than with standard invitations</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re using our <a href=\"/en_us/t/invitationonly-events\">invite-only feature</a> for your event, you will need to send <a href=\"/en_us/t/email-invitations\">email invitations</a> from within Eventbrite, as the invite-only feature is incompatible with our Paperless Post integration.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To use Paperless Post to send invites for an event, first <a href=\"/en_us/t/event-page-creation\">create and publish</a> your event in Eventbrite. Now click on <strong>My Events</strong> at the top of the page, and then click on the name of your published event to go to its <strong>Manage</strong> section. Select <strong>Create Invitations</strong> from the feature menu on the left side of the <strong>Manage</strong> section.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/create_invites1.png\" border=\"0\" height=\"123\" width=\"230\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click on the <strong>Use Paperless Post!</strong> button on the right side of the next screen. A new tab or window will open, and you will be taken to Paperless Post and prompted to sign up for a Paperless Post account.&nbsp;&nbsp;</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless1.png\" border=\"0\" height=\"338\" width=\"605\"></p>\r\n                                <p>\r\n                                    If you are already a member of Paperless Post, go ahead and click on <strong>Already a Member?</strong> and enter you email and password for your Paperless Post account.&nbsp;</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless2.png\" border=\"0\" height=\"478\" width=\"604\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you are new to Paperless Post, you will receive an email from them once you sign up for an account, asking you to confirm your Paperless Post account.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once logged in to Paperless Post, you can look through the available designs and select and customize the one you like best right away!</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless3.png\" border=\"0\" height=\"482\" width=\"609\"></p>\r\n                                <p>\r\n                                    Go <a href=\"http://www.paperlesspost.com/info/help\">here</a> to learn more about how to customize your Paperless Post invitation or get answers to frequently asked questions about Paperless Post\'s service.&nbsp;&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After selecting and customizing the card of your choice, you are able to verify and enter the details of your event as you would like them to be&nbsp; presented in the invitation that you are sending to your guests.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless42.png\" border=\"0\" height=\"680\" width=\"599\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After updating and confirming the details of your event, you may preview and send your invitations to the guests of your choice. Paperless Post offers various ways to add recipients, such as importing contacts or adding individual emails.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperlesscontacts.png\" border=\"0\" height=\"464\" width=\"599\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Once you send the customized invitations to your contacts, you will be able to track the invitations in your Paperless Post account, not in Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When your guests receive the invitation from Paperless Post in their email inbox, they will be able to open the email and watch your beautiful invitation slide out of its envelope so that they may read the details of your event!</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless5.png\" border=\"0\" height=\"567\" width=\"598\"></p>\r\n                                <p>\r\n                                    The guest can click on the <strong>Click to Register</strong> link or on the arrow pointing to the right in order to see the ticket form and register for your event.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless6.png\" border=\"0\" height=\"522\" width=\"610\"></p>\r\n                                <p>\r\n                                    Once the guest selects the quantity of tickets that they would like to order and clicks on the <strong>Order Now </strong>button (or the <strong>Register</strong> button if your event is free) they will be sent to your event page on Eventbrite. There, they can enter their information and complete their order for your event.&nbsp;</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/paperless-post/paperless7.png\" border=\"0\" height=\"772\" width=\"377\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Congratulations! You\'ve successfully sent out some great-looking invitaitons to your guests!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>When you sign up for an account with Paperless Post, they do offer you a limited quantity of stamps and coins to use to send your invitations. You can go <a href=\"http://www.paperlesspost.com/info/help/payment_orders\">here</a> for more information on Paperless Post\'s pricing.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349229475', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('59', '12', 'Email reminders for already-registered attendees', 'email-reminders-for-already-registered-attendees', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Email reminders for already-registered attendees</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Remind all or some of your attendees of your upcoming event</li>\r\n                                <li>\r\n                                    Great for FAQs like restrictions and transit info</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You are able to email only those who have already registered for your event. If you would like to send an email to those invited, but not yet registered, feel free to send out additional <a href=\"/en_us/t/email-invitations\">email invitations</a>.</p>\r\n                                <p>\r\n                                    By default, Eventbrite sends a 48-hour reminder email to your attendees. While you cannot edit its text, you can delete this automated reminder and replace it with a reminder email of your own design, as detailed below. To disable these automated 48-hour reminder emails for all events in your account, please <a href=\"http://www.eventbrite.com/contact-us/\">contact us</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the name of an event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage</strong> page, select <strong>Email Attendees</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/howtoemailattendees/sidebar.png\" border=\"0\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click the <strong>Create New Attendee Email</strong> button to create a new message.<img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/howtoemailattendees/e-mail_attendee_step_2.png\" border=\"0\" height=\"168\" width=\"493\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Customize the<strong> Name</strong> and <strong>Reply-To Email</strong> fields to control who will be shown as the sender of your email and to which email address any replies to your email will go. Then select the recipients of this email using the <strong>To</strong> dropdown menu.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/howtoemailattendees/e-mail_attendee_step_3.png\" border=\"0\" height=\"349\" width=\"509\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Customize the <strong>Subject</strong> and <strong>Message</strong> fields of the email. You can include a <strong>link to print tickets</strong> in your email (if <a href=\"/en_us/t/order-confirmation-customization\">PDF tickets are enabled</a> for the event), as well as include a <strong>link to invite friends to the event</strong>, if your event is public.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you have any files--e.g., slides or a presentation--to send to your recipients, you can upload these files to an outside site and then include in your email a link to download the files. Unfortunately, it isn\'t possible to attach files directly to an email to your attendees.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-schedule-and-send-email-reminders/message.png\" border=\"0\" height=\"569\" width=\"607\"></p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/howtoemailattendees/e-mail_attendee_step_4.png\" border=\"0\" height=\"685\" width=\"493\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose <strong>when </strong>you would like to send the email. Your options are to send now, at a specific date/time, or a certain number of days/hours/minutes before the start of your event. Then click <strong>Send Now</strong> or <strong>Save</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>More Options:</strong></p>\r\n                                <p>\r\n                                    You can easily copy and resend a scheduled or sent email by clicking <strong>Copy</strong> next to the subject of that email and following the steps above, making any edits you would like along the way.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/howtoemailattendees/e-mail_attendees_step_5.png\" border=\"0\" height=\"179\" width=\"468\"></p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229480', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('60', '12', 'Custom buttons and links', 'custom-buttons-and-links', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Custom buttons and links</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Use custom buttons and links to send people to your event page</li>\r\n                                <li>\r\n                                    Match your outside website\'s color scheme</li>\r\n                                <li>\r\n                                    Auto-generate HTML to drop into a rich-text email or outside website</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the different widgets that are available, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> or <a href=\"https://www.eventbrite.com/login\">log in</a> to Eventbrite. From the <strong>My Events</strong> page, click on the name of your published event and then check out the <strong>Promote</strong> section of the features menu on the left side of the page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The <strong>Promote</strong> section won\'t appear until you\'ve published your event or made it live.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Create a custom button: </strong>To get started, first click <strong>Create Buttons for Event</strong>.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/buttons_links/manage.png\" border=\"0\"></p>\r\n                                <p>\r\n                                    Select or input the button text you\'d like. Then select the size of the button so that it fits into your website, blog or email. Then select the style of Eventbrite logo (if any) you\'d like shown in the button.</p>\r\n                                <p>\r\n                                    Click <strong>Generate</strong> and you\'ll see the preview of the button update to reflect your changes. Your button\'s HTML code will be displayed in the box below. Click <strong>Copy to Clipboard</strong> so that you can paste the code into your website, blog or email.</p>\r\n                                <p>\r\n                                    When someone clicks the button, they\'ll be taken to your event page to register!</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/buttons_links/createbuttons2.png\" style=\"border-width: 0px; border-style: solid; width: 610px; height: 666px; \"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Create a custom text link: </strong>To get started, first click <strong>Create Links for Event</strong>.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/buttons_links/create_link.png\" border=\"0\" height=\"277\" width=\"233\"></p>\r\n                                <p>\r\n                                    Enter in the link text and choose a color for the link. Click <strong>Create your widget</strong> and your link\'s HTML code will be displayed in the box below. Copy and paste the code that is displayed into your website.</p>\r\n                                <p>\r\n                                    When visitors to your website click the link, they will be taken to your event page to register!</p>\r\n                                <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/buttons_links/create_link2.png\" border=\"0\" height=\"295\" width=\"606\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> For more information on how you can use other tools to promote your event online, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229485', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('61', '12', 'Embed ticket form into your website', 'embed-ticket-form-into-your-website', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Embed ticket form into your website</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Show all ticket types for sale on your outside website</li>\r\n                                <li>\r\n                                    People initiate their orders while on your site</li>\r\n                                <li>\r\n                                    Auto-generate the HTML to drop into your website, Wordpress or Tumblr</li>\r\n                                <li>\r\n                                    Your website must support iframes to embed this widget</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use this feature to easily add event registration to your own website, content management system (like Drupal and Joomla), or Wordpress or Tumblr blog. Your website must support iframes in order to embed these widgets. If your website was built with Wordpress, for instance, you\'d need to install a <a href=\"http://wordpress.org/extend/plugins/search.php?q=embed+iframes&amp;sort=\">plugin </a>(website in English), which will&nbsp;allow you to embed iframes in your site.</p>\r\n                                <p>\r\n                                    Widgets like the ticket widget cannot be used in conjunction with our <a href=\"/en_us/t/invitationonly-events\">invite-only feature</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    For an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage</strong> page, select <strong>Add Ticket Form to Website</strong>, found in the <strong>Promote</strong> section of the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img alt=\"Sell tickets from your site\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/ticket_form.png\" title=\"Sell tickets from your site\" border=\"0\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The <strong>Promote</strong> section won\'t appear until you\'ve published your event or made it live..</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll&nbsp; see a preview of the ticket form at the top of the next page as it will appear when inserted into your website. Scroll down to the HTML code<strong> </strong>for the widget, in the section labelled <strong>Copy and paste this code for use on a webpage</strong>. Click the <strong>Copy to Clipboard </strong>button. Now all you have to do is paste the code into your own website or blog.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/ticket_form2.png\" border=\"0\" height=\"408\" width=\"596\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To learn more about pasting the entire registration page into your own website, click <a href=\"/en_us/t/embed-registration-form-into-your-website\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The ticket order form will appear on your website and attendees can see all the different ticket types that you\'ve created in Eventbrite, choose the quantity of each that they want, and click to register. Well done!</p>\r\n                                <p>\r\n                                    Here\'s a good example of a website with the ticket widget inserted:</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/ticket_widget/page.png\" border=\"0\" height=\"408\" width=\"613\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> For more information on how you can use other tools to promote your event online, please click <a href=\"/en_us/t/promotional-tools-overview\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229492', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('62', '12', 'Embed registration form into your website', 'embed-registration-form-into-your-website', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Embed registration form into your website</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Show your entire event page on your outside website</li>\r\n                                <li>\r\n                                    People initiate their orders while on your site</li>\r\n                                <li>\r\n                                    Auto-generate HTML to drop into your website, Wordpress or Tumblr</li>\r\n                                <li>\r\n                                    Your website must support iframes to embed the registration form</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <p>&nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use this feature to easily add event registration to your own website, content management system (like Drupal and Joomla), or Wordpress or Tumblr blog. Your website must support iframes in order to embed these widgets. If your website was built with Wordpress, for instance, you\'d need install a <a href=\"http://wordpress.org/extend/plugins/search.php?q=embed+iframes&amp;amp;sort=\">plugin </a>(website in English)&nbsp;allowing you to embed iframes in your site.</p>\r\n                                <p>\r\n                                    Widgets like the ticket widget cannot be used in conjunction with our <a href=\"/en_us/t/invitationonly-events\">invite-only feature</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    For an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage</strong> page, select <strong>Add Registration to Website</strong>, found in the&nbsp;<strong>Promote</strong> section of the features menu on the left side of the&nbsp;page.</p>\r\n                                <p>\r\n                                    <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/use_website_int/reg_page.png\" border=\"0\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>The&nbsp;<strong>Promote</strong> section won\'t appear until you\'ve published your event or made it live..</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll see the preview of the event page that you\'ve created and customized. Scroll down to the HTML code<strong> </strong>for the widget, in the section labelled <strong>Copy and paste this code for use on a webpage</strong>. Click the <strong>Copy to Clipboard </strong>button. Now all you have to do is paste the code into your own website or blog.</p>\r\n                                <img alt=\"How to use Website Integration\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/use_website_int/reg_page_2.png\" border=\"0\" height=\"260\" width=\"608\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The registration page will appear on your website. Attendees can see your entire event page, choose the quantity of each ticket type(s) that they want, and click to register. Way to go!</p>\r\n                                <p>\r\n                                    Here\'s an example of a website displaying an Eventbrite registration page:</p>\r\n                                <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/use_website_int/page2.png\" border=\"0\" height=\"665\" width=\"615\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> For more information on how you can use other tools to promote your event online, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229499', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('63', '12', 'Show who\'s going on event page', 'show-who-s-going-on-event-page', 'Show who\'s going on event page', '1349229524', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('64', '12', 'Event affiliate programs', 'event-affiliate-programs', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Event affiliate programs</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Turn attendees into promoters</li>\r\n                                <li>\r\n                                    Each affiliate gets a unique link</li>\r\n                                <li>\r\n                                    You and your affiliate can see their performance</li>\r\n                                <li>\r\n                                    Handle payment of your affiliates separate from Eventbrite</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> While you can use both <a href=\"/en_us/t/track-my-promotional-activities\">tracking links</a> and affiliate programs to track your promoters\' performance, the two features differ in a number of ways. If you use tracking links, you will need to create a unique tracking link for each of your promoters. However, the affiliate program tool allows you to create one program, which all of your promoters can sign up for at a single link Eventbrite provides you after you create the affiliate program. Moreover, an affiliate program also allows your promoters to log in to Eventbrite and track the ticket sales and revenue they\'ve generated for your event, while the performance of a given tracking link is visible only to you, the organizer.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, go to your event\'s <strong>Manage</strong> page by clicking on <strong>My Events</strong> at the top of the page, and then clicking on the name of your event. Once on the <strong>Manage </strong>page, select <strong>Create Affiliate Programs</strong> on the features menu on the left side of the page.</p>\r\n                                <img alt=\"Create an Affiliate Program\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_affiliates/affiliate.png\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <strong>name of your affiliate program</strong>, the <strong>referal fee</strong> you\'ll pay to an affiliate for each attendee they refer to your event, and any <strong>additional notes</strong> you would like potential affiliates to see in the program details. If you\'d like to list your affiliate program in our <a href=\"http://www.eventbrite.com/publicaffiliates\">directory of public affiliate programs</a>, make sure the <strong>Make Public</strong> box is checked. Click <strong>Save</strong> to create your program!</p>\r\n                                <img alt=\"Create an Affiliate Program\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_affiliates/create_affiliate.png\" border=\"0\" height=\"288\" width=\"590\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To promote your affiliate program and get affiliates to sign up, select <strong>Invite Affiliates</strong> from the <strong>Quick Actions</strong> menu next to the name of your affiliate program. You\'ll then be able to send a customized email invitation for your affiliate program to your contacts.</p>\r\n                                <img alt=\"Create an Affiliate Program\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_affiliates/invite_affilliates.png\" border=\"0\" height=\"471\" width=\"603\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When your contacts receive the affiliate-program invitation and click on the link to sign up, they will be prompted to log in to their Eventbrite account (if they already have an account) or to sign up for an Eventbrite account (if they haven\'t yet created one). Once they log in, they will be on the <a href=\"https://www.eventbrite.com/referrals-attendees\"><strong>Event Affiliate Program</strong></a> screen, which they can access by clicking the <strong>Account</strong> tab in the upper right at any time.</p>\r\n                                <p>\r\n                                    On this screen, they will find their unique <strong>referral link</strong> (shown in the red box below) to send out to their contacts. The affiliate will receive credit for any ticket sales that come through their unique referral link, and can track the performance of their link in the stats shown on this <a href=\"https://www.eventbrite.com/referrals-attendees\"><strong>Event Affiliate Program</strong></a> screen. The affiliate can change their PayPal email address for payout by clicking on the <strong><a href=\"https://www.eventbrite.com/payment-information\"><strong>Payment Accounts</strong></a> </strong>link in the <strong>Account</strong> tab.</p>\r\n                                <img alt=\"\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_affiliates/account.png\" border=\"0\" height=\"417\" width=\"618\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you, the organizer, would like to view each of your affiliates\' performance, select <strong>View Users</strong> from the <strong>Quick Actions</strong> menu located next to any affiliate program listed in the <strong>Create Affiliate Programs</strong> screen for your event. You can also click <strong>Attendee Report</strong> on this <strong>Quick Actions</strong> dropdown menu to view an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a> for more details about any attendees who registered for your event through this affiliate program.</p>\r\n                                <img alt=\"Create an Affiliate Program\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_affiliates/created_affiliate.png\" border=\"0\" height=\"190\" width=\"590\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" border=\"0\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> You, the event organizer and affiliate-program owner, are responsible for making payment to your affiliates. Payment can be handled via PayPal, but is conducted entirely outside of Eventbrite. We leave the management of your affiliate program(s) up to you!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229528', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('65', '12', 'Join an event affiliate program', 'join-an-event-affiliate-program', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Join an event affiliate program</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Earn money for driving ticket sales to an event</li>\r\n                                <li>\r\n                                    You are paid by event organizer, not Eventbrite</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> When you join an event-specific affiliate program you are entering into an affiliate relationship with the event organizer, <span style=\"text-decoration: underline;\">not with Eventbrite</span>. We recommend getting in touch with the event organizer before promoting any event-specific affiliate program, especially those listed on the <a href=\"http://www.eventbrite.com/publicaffiliates\">public event affiliate programs</a><a href=\"http://www.eventbrite.com/publicaffiliates\"> page</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>More on event-specific affiliate programs</b></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    With the <a href=\"/en_us/t/event-affiliate-programs\">affiliate program</a> tool, event organizers can create an event-specific affiliate program for their event. This enables event organizers to increase attendance to their event by offering a referral commission to third-party promoters. The payment can either be a fixed dollar amount or a percentage of the ticket price--it\'s entirely up to the event organizer. Eventbrite gives event organizers and their affiliates all the tools they need to send, receive and track traffic, as well as keep track of commissions. When you join an event-specific affiliate program, you will receive a unique URL which you must use to refer attendees to that event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>There are two ways to join an event-specific affiliate program:</b></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You may receive an invitation via email from the event organizer. In this case, simply click on the embedded link to join the program.</p>\r\n                            </div>\r\n                            <p style=\"text-align: center;\">\r\n                                <img alt=\"Attendee Referral Program Invitation\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_affiliate_program/Email_link.png\" border=\"0\" height=\"248\" width=\"479\"></p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You may also find an event affiliate program that interests you listed on the <a href=\"http://www.eventbrite.com/publicaffiliates\">public event affiliate programs</a><a href=\"http://www.eventbrite.com/publicaffiliates\"> page</a>. In this case, simply click the <b>Join </b>button. If you don\'t already have an Eventbrite account, you\'ll be directed to create one.</p>\r\n                                <img alt=\"Join Attendee Referral Program\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_affiliate_program/public_list.png\" border=\"0\" height=\"116\" width=\"559\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Once you\'ve joined an affiliate program:</b></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Keep track of your earnings on the <b><a href=\"http://www.eventbrite.com/referrals-attendees\">Event Affiliate Program</a></b> page, which you can access by clicking <b>Account</b> in the upper right. All of your event affiliate programs will be listed here, along with basic stats and earnings. Direct any questions you have about earnings or payments to the event organizer. Eventbrite provides a convenient <b>Contact the Organizer</b> link under the event name.</p>\r\n                                <img alt=\"Attendee Referral Program\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_affiliate_program/account_tab.png\" border=\"0\" height=\"196\" width=\"588\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Note:</b> Your PayPal account email address is listed at the bottom of the <a href=\"https://www.eventbrite.com/payment-information\"><b>Payment Accounts</b></a> page, which is accessed by clicking the <b>Account </b>tab in the upper right. Make sure this account email is accurate! If you don\'t have a PayPal account, <a href=\"https://www.paypal.com/us/mrb/pal=W9QW5TKUA246E\">open an account now</a>. Affiliate program owners are responsible for making payments to your PayPal account unless otherwise indicated in their affiliate program description. Eventbrite is in no way responsible for any earnings owed to you by event organizers.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229533', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('66', '12', 'Google Analytics integration', 'google-analytics-integration', 'Google Analytics integration', '1349229541', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('67', '12', 'Why does an old photo show when event shared on Facebook?', 'why-does-an-old-photo-show-when-event-shared-on-facebook-', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Why does an old photo show when event shared on Facebook?</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Force Facebook to crawl your updated event page</li>\r\n                                <li>\r\n                                    Updated images will then show in Facebook</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, you\'ll need to copy the URL for your event page, so that later you\'ll be able to paste that URL into Facebook\'s Debug tool (which we explain how to do in step 2 below).</p>\r\n                                <p>\r\n                                    You can find the URL for your event page by logging in to your Eventbrite account, clicking on <b>My Events</b> at the top of the page, then clicking <b>View</b> to the right of your event\'s name (under <b>Quick Links</b>) to go to your event page. Copy the URL for your event page from your browser\'s address bar.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To force Facebook to crawl your event page, go to Facebook\'s Debug tool by clicking <a href=\"http://developers.facebook.com/tools/debug\">here</a>. Paste in your event page\'s URL and then click the <b>Debug</b> button.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img alt=\"Note\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Note: </b>This Debug tool is built primarily to assist web developers in configuring their sites to work with Facebook, so don\'t worry about any error messages that might display after you\'ve clicked <b>Debug</b>. All you care about here is that you\'ve forced Facebook to re-index your event page with all the updates you\'ve made.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Anytime someone shares your event page on a Facebook wall from this point on, the updated event information and images will display! Unfortunately, there is no way to update existing wall posts, so you\'ll want to delete any existing posts and then re-share your event page to display the updated info.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229546', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('68', '12', 'Manage contact lists', 'manage-contact-lists', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Manage contact lists</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Edit, copy and delete existing contact lists</li>\r\n                                <li>\r\n                                    Copy contacts from one list to another</li>\r\n                                <li>\r\n                                    Export contact list to a .csv file for use with another program</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip: </strong>To first learn how to create / import a contact list into Eventbrite, click <a href=\"/en_us/t/import-contact-lists\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To view and manage your contact lists, click on the <strong>My Contacts</strong> tab located at the top of any page when you\'re logged in to Eventbrite.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/importing_contacts/my_contacts2.png\" border=\"0\" height=\"60\" width=\"619\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>My Contacts</strong> page, you\'ll see all your contact lists, the number of contacts in each list, the date each was created, and the last time that you sent an email invitation from the <strong>My Contacts</strong> page to each list.</p>\r\n                                <p>\r\n                                    In the far right column are four action icons (from left to right): <strong>edit contact list</strong>,<strong> copy contact list</strong>,<strong> delete contact list</strong>,<strong> </strong>and <strong>send invites to this contact list. </strong>You can click the name of a contact list to view all the email addresses in that list and see more options for that list. To search across all your contact lists for a specific contact, use the search box on the far right, labeled <strong>Search across all contacts</strong>.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_contacts/my_contacts.png\" border=\"0\" height=\"182\" width=\"611\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To send an email invite to one or more contact lists </strong></p>\r\n                                <p>\r\n                                    Click the check box next to those contact lists and then click <strong>Send Invitations to Selected.</strong> In the <strong>Create Invite</strong> box, select the event for which you would like to send invitations to the contact list(s), then press <strong>Create Invite</strong>.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_contacts/send_invites.png\" border=\"0\" height=\"296\" width=\"614\"></p>\r\n                                <p>\r\n                                    You will be taken to the <strong>Create Invitations</strong> page for the event you selected and your contact list will be auto-loaded into the invite\'s recipient list.&nbsp; Here you can preview and edit your email invitation, add or remove contacts from the recipient list, and then send the invitation or schedule it to go out in the future.</p>\r\n                                <p>\r\n                                    To learn more about customizing and scheduling / sending your email invitation, go <a href=\"/en_us/t/email-invitations\">here</a>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To view and/or edit the entries in a contact list </strong>click the name of the contact list, which will show you all contacts in that list, as seen below.&nbsp;</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/manage_contacts/contact_list.png\" border=\"0\" height=\"361\" width=\"606\"></p>\r\n                                <p>\r\n                                    When looking at an individual contact list, you can:</p>\r\n                                <p>\r\n                                    - <strong>Search for a contact</strong> using the <strong>search box</strong> in the upper right.&nbsp;</p>\r\n                                <p>\r\n                                    - <strong>Add more contacts</strong> by clicking <strong>add more</strong> in the upper left. You can import contacts from a .csv or .txt file, from an outside email account such as Hotmail or Gmail, from attendees of a past event in your Eventbrite account, or by manually entering email addresses.</p>\r\n                                <p>\r\n                                    - <strong>Export contacts to a .csv file </strong>by selecting the contact(s) you want to export, then clicking <strong>Export Selected</strong> near the bottom. You can then import these contacts from the .csv file into an outside email client, suck as Outlook.</p>\r\n                                <p>\r\n                                    - <strong>Delete contacts from the contact list</strong> by selecting the contact(s) you want to delete, then clicking <strong>Delete Selected</strong> near the bottom.</p>\r\n                                <p>\r\n                                    - <strong>Send email invitations to specific contacts</strong> by selecting the contact(s) you want to send invites to, then clicking <strong>Send Invitations to Selected</strong> near the bottom.</p>\r\n                                <p>\r\n                                    - <strong>Copy contacts to another contact list</strong> by selecting the contact(s) you want to copy, then clicking <strong>Copy Selected to Another List</strong>. A <strong>Copy Contacts</strong> window will appear, and you can select the contact list you want to copy to or else create a new contact list with your selected contacts.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349229551', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('69', '12', 'Import contact lists', 'import-contact-lists', 'Import contact lists', '1349229556', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('70', '12', 'Event Tracking FAQ', 'event-tracking-faq', 'Event Tracking FAQ', '1349229561', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('71', '9', 'Fee information', 'fee-information-1', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Fee information</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Frequently asked questions about fees</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>How much does it cost to use Eventbrite?</strong></p>\r\n                                <p>\r\n                                    First off, if your event is free--or you\'re just wanting to create a test event to try us on for size--Eventbrite is free to use! There are no monthly charges, enrollment or setup fees.</p>\r\n                                <p>\r\n                                    If you\'re charging for your event, you can learn more about the small per-ticket fee to use Eventbrite by going <a href=\"http://www.eventbrite.com/fees/\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>I\'m charging for my event. Can I have my attendees pay Eventbrite\'s fee?</strong></p>\r\n                                <p>\r\n                                    Yes! By default, the per-ticket Eventbrite fee for paid events is added to the ticket price, and so is paid by the attendee. However, you can choose to pay the fees instead of your attendees.</p>\r\n                                <p>\r\n                                    Go <a href=\"/en_us/t/feehandling-options\">here</a> to learn more about the fee-handling options that are available in Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>What payment-processing options do I have, and how much does each cost?</strong></p>\r\n                                <p>\r\n                                    You can choose from a number of different payment processors for your event, though we recommend our standard <a href=\"/en_us/t/credit-card-processing-faq\">credit card processing</a> as the smoothest option for both you and your attendees.</p>\r\n                                <p>\r\n                                    For a comparison of the different payment-processing options, go <a href=\"/en_us/t/payment-processor-options\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>How can I offer offline (non-credit card) payment methods to my attendees? Does Eventbrite still charge a fee for offline payments? </strong></p>\r\n                                <p class=\"stepcontent\">\r\n                                    To learn how to offer your attendees the option to pay offline, go <a href=\"/en_us/t/offline-payment-options\">here</a>. The Eventbrite ticketing fee--though not the payment processing fee--is still charged for orders with offline payment. Go <a href=\"http://www.eventbrite.com/fees/\">here </a>to learn more about Eventbrite\'s fees.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Does Eventbrite still charge a fee for orders that I refund?</strong></p>\r\n                                <p>\r\n                                    No. For an order that you completely refund, Eventbrite refunds all fees associated with that order.</p>\r\n                                <p>\r\n                                    Go <a href=\"/en_us/t/refund-an-order\">here </a>to learn more about refunding an order in Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229639', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('72', '9', 'Payment processor options', 'payment-processor-options', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Payment processor options</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    More information about each of the credit card processing options</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> We recommend our standard credit card processing, as it\'s the smoothest experience for both organizer and attendees. Click <a href=\"/en_us/t/how-will-i-be-paid\">here </a>to see how to select a payment processor in Eventbrite. And go <a href=\"http://www.eventbrite.com/fees/\">here</a> for more information about our fees.<br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Credit card processing&nbsp;</strong></p>\r\n                                <p>\r\n                                    - Fee: 3% per transaction</p>\r\n                                <p>\r\n                                    - Significantly higher attendee purchase conversion rate than PayPal or Google Checkout</p>\r\n                                <p>\r\n                                    - Attendees stay on the Eventbrite site for payment</p>\r\n                                <p>\r\n                                    - No set-up time or cost</p>\r\n                                <p>\r\n                                    - PCI compliant (<a href=\"http://en.wikipedia.org/wiki/Payment_Card_Industry_Data_Security_Standard\">learn more</a>)</p>\r\n                                <p>\r\n                                    - Funds sent via mailed check (in the US) or bank transfer five days after the event ends</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>PayPal</strong></p>\r\n                                <p>\r\n                                    - Fee: usually 2.9% + $.30 per transaction</p>\r\n                                <p>\r\n                                    - Attendees redirected to PayPal website for payment</p>\r\n                                <p>\r\n                                    - Attendees are often prompted to log in to PayPal to pay with a credit card</p>\r\n                                <p>\r\n                                    - Receive funds immediately into PayPal account</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Google Checkout</strong></p>\r\n                                <p>\r\n                                    - Fee: usually 2.9% + $.30 per transaction</p>\r\n                                <p>\r\n                                    - Attendees redirected to Google Checkout website for payment</p>\r\n                                <p>\r\n                                    - Receive funds immediately into Google Checkout account</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Authorize.net</strong></p>\r\n                                <p>\r\n                                    - Fee: varies</p>\r\n                                <p>\r\n                                    - Use your existing merchant account</p>\r\n                                <p>\r\n                                    - To activate, call: 888.541.9753</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Click <a href=\"/en_us/t/how-will-i-be-paid\">here </a>to see how to select a payment processor in Eventbrite. And to learn how to offer your attendees offline (non-credit-card) payment options, click <a href=\"/en_us/t/offline-payment-options\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229645', null, '0', '0');
