INSERT INTO `etk_faqs` VALUES ('92', '9', 'Add tax to ticket price', 'add-tax-to-ticket-price', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Add tax to ticket price</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    &nbsp;Manually include tax in the ticket price</li>\r\n                                <li>\r\n                                    Indicate to attendees that tax is included</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re in the UK, Ireland, Canada, Australia, New Zealand, France, Spain, Germany or the Netherlands and would like to charge tax, go <a href=\"/en_us/t/charge-tax-on-tickets-limited-availability\">here</a> to learn how.</p>\r\n                                <p>\r\n                                    We plan to gradually widen the availability of our dedicated tax tool--keep an eye on our <a href=\"http://blog.eventbrite.com/\">blog</a> for announcements as we expand our dedicated tax support! In the meantime, see below to learn how to add tax to your ticket price if you are not in the abovementioned countries.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>As an example, we\'ll use a $50 ticket, with 10% tax to be added on to the price</strong></p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When on the <strong>Create an Event </strong>page for your event, in <strong>Step 2: Add Tickets</strong>, click the <strong>Create a Ticket</strong> button. (If you\'ve already created a ticket, and want to create an additional ticket type, this button will say <strong>Add a New Ticket</strong>.)</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/create_ticket.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To add tax to an existing ticket for an existing event, go to the event\'s <strong>Edit </strong>page and click the name of the ticket type to edit the information for that ticket.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"202\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/existing_ticket.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>Edit Ticket</strong> window that appears, indicate in the <strong>Ticket Name</strong> that the tax is included in the price. Make sure to include the sum of your ticket price and any taxes in the <strong>Price</strong> field. Then enter the <strong>Quantity Available</strong> of this ticket type.</p>\r\n                                <p>\r\n                                    In our example, the name of the ticket is \'General Admission,\' and we\'ve shown that 10% tax is included. (We\'ve included a line break between the name of the ticket and the tax information by adding in the HTML tag &amp;lt;br/&amp;gt; so that this information will appear on separate lines.) We\'ve then entered $55 as the <strong>Price</strong>, as this is the sum of our original $50 ticket price and the additional $5 in taxes.</p>\r\n                                <p>\r\n                                    <img width=\"598\" height=\"315\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/edit_ticket2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, click on <strong>Show</strong> to view the <strong>Advanced</strong> <strong>Options</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"317\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/advanced_options2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the <strong>Ticket Description</strong> field, include a message reiterating that the tax is included in the ticket price. This message will appear below the name of your ticket type on your event page. Uncheck the <strong>Auto Hide Description</strong> box, so that this message will always be visible. Make sure to click <strong>Save</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"433\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/ticket_description.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>By default, the service fee is added on top of any value you include in the <strong>Price</strong> field and so is paid by the attendee. To learn more about the option to absorb the fee yourself, go <a href=\"/en_us/t/feehandling-options\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Back on the main <strong>Edit </strong>or <strong>Create an Event</strong> page, click on <strong>View</strong> or <strong>Preview </strong>in the upper left to view your event page and see how the ticket information is displayed to your attendees. Well done!</p>\r\n                                <p>\r\n                                    <img width=\"603\" height=\"281\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/event_page.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> We recommend also adding information about taxes into <strong>Step 3: Add Event Details</strong> on your event\'s <strong>Edit </strong>page. The more messaging the better, we say!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also customize the order confirmation email that your attendees will receive after they order to include the fact that tax is included in the ticket price. Go <a href=\"/en_us/t/order-confirmation-customization\">here</a> to learn how.<br>\r\n                                    <br>\r\n                                    <br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Add tax to ticket price</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Manually include tax in the ticket price</li>\r\n                                    <li>\r\n                                        Indicate to attendees that tax is included</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <br>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re in the UK, Ireland, Canada, Australia or New Zealand and would like to charge tax, go <a href=\"/en_us/t/charge-tax-on-tickets-limited-availability\">here</a> to learn how.</p>\r\n                                <p>\r\n                                    We plan to gradually widen the availability of our dedicated tax tool--keep an eye on our <a href=\"http://blog.eventbrite.com/\">blog</a> for announcements as we expand our dedicated tax support! In the meantime, see below to learn how to add tax to your ticket price if you are not in the abovementioned countries.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>As an example, we\'ll use a $50 ticket, with 10% tax to be added on to the price</strong></p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, <a href=\"http://www.eventbrite.com/login\">log in</a> to your Eventbrite account and click <strong>My Events</strong> at the top of the screen.Then, click on <strong>Edit</strong> to the right of your event’s name.<br>\r\n                                    <img width=\"592\" height=\"177\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket/myevents1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Go to <strong>Step 2: Create Tickets</strong> and click into the <strong>Ticket Price</strong> field to the right of the <strong>Ticket Name</strong> that you want to add tax to. (If you want to create a new ticket type that will have tax added to its price, use the <strong>Add Ticket Type</strong> dropdown menu to choose the type of ticket you\'d like to create.)</p>\r\n                                <p>\r\n                                    <img width=\"588\" height=\"208\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/addtax-new2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve clicked into the <strong>Ticket Price</strong> field, enter the sum of your ticket and any taxes that are included. You may also want to rename the name of your ticket in the <strong>Ticket Name</strong> field, indicating that tax is included in the price.</p>\r\n                                <p>\r\n                                    In our example, the name of the ticket is \'General Admission,\' and we\'ve shown that 10% tax is included. We\'ve then entered $55 as the <strong>Ticket</strong> <strong>Price</strong>, as this is the sum of our original $50 ticket price and the additional $5 in taxes.</p>\r\n                                <p>\r\n                                    <img width=\"595\" height=\"191\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/addtax-new3.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, click on <strong>Settings</strong> to the right of the <strong>Ticket Name</strong> to view more options.</p>\r\n                                <p>\r\n                                    <img width=\"599\" height=\"206\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/addtax-new4.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the <strong>Ticket description</strong> field, include a message reiterating that the tax is included in the ticket price. This message will appear below the name of your ticket type on your event page. Uncheck the <strong>Hide description on event pages</strong> box, so that this message will always be visible. Make sure to click <strong>Save</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"300\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/addtax43.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>By default, the service fee is added on top of any value you include in the <strong>Ticket Price</strong> field and so is paid by the attendee. To learn more about the option to absorb the fees yourself, go <a href=\"/en_us/t/feehandling-options\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You did it! You can click on <strong>Colors &amp; Options</strong> in the upper left to view your event page and see how the ticket information is displayed to your attendees.</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"302\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-charge-tax-on-the-ticket-price/addtax52.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> We recommend also adding information about taxes in the <strong>Event Details</strong> box in <strong>Step 1: Add your Event Details</strong> on your event\'s <strong>Event Details</strong> page. The more messaging the better, we say!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can also customize the order confirmation email that your attendees will receive after they order to include the fact that tax is included in the ticket price. Go <a href=\"/en_us/t/order-confirmation-customization\">here</a> to learn how.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229845', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('93', '9', 'Charge tax on tickets (limited availability)', 'charge-tax-on-tickets--limited-availability-', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Charge tax on tickets (limited availability)</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Available in the UK, Ireland, Canada, Australia, New Zealand, France, Spain, Germany, Netherlands</li>\r\n                                <li>\r\n                                    Tickets must be priced in the local currency</li>\r\n                                <li>\r\n                                    Useful for VAT, GST and HST</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you are located in a country in which this feature is not available, click <a href=\"/en_us/t/add-tax-to-ticket-price\">here</a> to learn how to configure your tickets to include tax. (And to learn more about our local UK, IE, CA, AU, NZ, FR, ES, DE, or NL sites, click <a href=\"/en_us/t/language-and-region-settings\">here</a>.) We plan to gradually widen our dedicated tax support, however!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> or <a href=\"https://www.eventbrite.com/login\">log in</a> to Eventbrite and <a href=\"/en_us/t/event-page-creation\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit</strong> to the right of your event’s name.<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"318\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/edit2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'ve already sold a ticket for your event, you will be unable to start charging tax for the event. If you\'ve sold a ticket and want to charge tax, you can <a href=\"/en_us/t/copy-an-event-page\">copy your existing event page</a> and then enable tax support for the new event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event’s <strong>Edit</strong> page, in <strong>Step 2: Add Ticket Information</strong>, <a href=\"/en_us/t/ticket-type-creation\">create a ticket</a> priced in your local currency, if you haven’t already.</p>\r\n                                <p>\r\n                                    (<strong>We\'ll use a ticket priced in GBP for an event on our UK [.co.uk] site for our example. If you\'re on a non-UK site, what you see may be a bit different.</strong>)</p>\r\n                                <p>\r\n                                    Next click <strong>+ Add VAT</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"281\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/add%20VAT.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the <strong>Tax Options</strong> window, select <strong>Yes</strong> to show all fields and options to fill out.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    First complete the three required fields:</p>\r\n                                <p>\r\n                                    - The name of the tax, as it will appear on the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email and PDF ticket</a> that your attendees will receive after ordering. See below for how this will display. (We\'ll use \"VAT\" for our example.)</p>\r\n                                <p>\r\n                                    - The percentage tax that you would like to charge.</p>\r\n                                <p>\r\n                                    - Your tax registration ID.</p>\r\n                                <p>\r\n                                    Then choose the ticket type(s) to which you want to apply this tax.</p>\r\n                                <p>\r\n                                    Last, choose whether to include the tax in the ticket price, then click <strong>Save</strong>.<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"573\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/taxoptions22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Tax is charged on the total price the attendee will pay at checkout, inclusive of <a href=\"/en_us/t/feehandling-options\">fees</a>. By default, tax is not included in the ticket price and is added on top of the ticket price, though you can choose to include it in the ticket price instead.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Back on the <strong>Edit</strong> page, make sure to save your changes. To take a look at how your event page will display, click <strong>View</strong> or <strong>Preview</strong> in the upper left.</p>\r\n                                <p>\r\n                                    Here’s how your <strong>Ticket Information</strong> box on your event page will look if you&nbsp;<strong>do not</strong> <strong>include</strong> tax in the ticket price:</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"300\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/add.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    And here’s how your <strong>Ticket Information</strong> box on your event page will look if you<strong> include</strong> tax in the ticket price:</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"322\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/include.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>In both of the examples above, the fees are added on top of the ticket price. (Click <a href=\"/en_us/t/feehandling-options\">here</a> to learn more about how to include the fees in the ticket price.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Here’s a look at the order confirmation email and PDF ticket that your attendees will receive after ordering. Both include a reference to the tax.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"1024\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/order%20conf.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Here\'s the PDF ticket:</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"736\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/PDF.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you’d like your tax to show as a separate line item in the order confirmation email and the PDF ticket&mdash;as seen here&mdash;you’ll need to leave the <strong>Include it in the ticket price?</strong> option unchecked when setting up the tax, as described in step 3 above.</p>\r\n                                <p>\r\n                                    Go <a href=\"/en_us/t/order-confirmation-customization\">here</a> to learn how to edit the order confirmation email and PDF ticket!</p>\r\n                            </div>\r\n                        </div>\r\n                        <br/>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Charge tax on your ticket sales (limited availability)</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Available in the UK, Ireland, Canada, Australia, New Zealand, France, Spain, Germany, Netherlands</li>\r\n                                    <li>\r\n                                        Tickets must be priced in the local currency</li>\r\n                                    <li>\r\n                                        Useful for VAT, GST and HST</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you are located in a country in which this feature is not available, click <a href=\"/en_us/t/add-tax-to-ticket-price\">here</a> to learn how to configure your tickets to include tax. (And to learn more about our local UK, IE, CA, AU, NZ, FR, ES, DE, NL sites, click <a href=\"/en_us/t/language-and-region-settings\">here</a>.) We plan to gradually widen our dedicated tax support, however!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> or <a href=\"https://www.eventbrite.com/login\">log in</a> to Eventbrite and <a href=\"/en_us/t/event-page-creation\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit</strong> to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"318\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/edit2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'ve already sold a ticket for your event, you will be unable to start charging tax for the event. If you\'ve sold a ticket and want to charge tax, you can <a href=\"/en_us/t/copy-an-event-page\">copy your existing event page</a> and then enable tax support for the new event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event’s&nbsp;<strong>Event Details</strong> page, in <strong>Step 2: Create Tickets</strong>, <a href=\"/en_us/t/ticket-type-creation\">create a ticket</a> priced in your local currency, if you haven’t already.</p>\r\n                                <p>\r\n                                    (<strong>We\'ll use a ticket priced in GBP for an event on our UK [.co.uk] site for our example. If you\'re on a non-UK site, what you see may be a bit different.</strong>)</p>\r\n                                <p>\r\n                                    Next click <strong>VAT </strong>on the right.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"250\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/VAT222.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    In the <strong>Tax Options</strong> window, select <strong>Yes</strong> to show all fields and options to fill out.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    First complete the three required fields:</p>\r\n                                <p>\r\n                                    - The name of the tax, as it will appear on the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email and PDF ticket</a> that your attendees will receive after ordering. See below for how this will display. (We\'ll use \"VAT\" for our example.)</p>\r\n                                <p>\r\n                                    - The percentage tax that you would like to charge.</p>\r\n                                <p>\r\n                                    - Your tax registration ID.</p>\r\n                                <p>\r\n                                    Then choose the ticket type(s) to which you want to apply this tax.</p>\r\n                                <p>\r\n                                    Last, choose whether to include the tax in the ticket price, then click <strong>Save</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"476\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/taxoptions32.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>Tax is charged on the total price the attendee will pay at checkout, inclusive of <a href=\"/en_us/t/feehandling-options\">fees</a>. By default, tax is not included in the ticket price and is added on top of the ticket price, though you can choose to include it in the ticket price instead.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Back on the&nbsp;<strong>Event Details</strong> page, make sure to save your changes. To take a look at how your event page will display, click <strong>Colors &amp; Options</strong> in the upper left.</p>\r\n                                <p>\r\n                                    Here’s how your <strong>Ticket Information</strong> box on your event page will look if you <strong>do not include</strong> tax in the ticket price:</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"300\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/add.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    And here’s how your <strong>Ticket Information</strong> box on your event page will look if you <strong>include</strong> tax in the ticket price:</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"322\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/include.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>In both of the examples above, the fees are added on top of the ticket price. (Click <a href=\"/en_us/t/feehandling-options\">here</a> to learn more about how to include the fees in the ticket price.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Here’s a look at the order confirmation email and PDF ticket that your attendees will receive after ordering. Both include a reference to the tax.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"1024\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/order%20conf.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Here\'s the PDF ticket:</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"736\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/charge-VAT-on-your-ticket-sales/PDF.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you’d like your tax to show as a separate line item in the order confirmation email and the PDF ticket&mdash;as seen here&mdash;you’ll need to leave the <strong>Include it in the ticket price?</strong> option unchecked when setting up the tax, as described in step 3 above.</p>\r\n                                <p>\r\n                                    Go <a href=\"/en_us/t/order-confirmation-customization\">here</a> to learn how to edit the order confirmation email and PDF ticket!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229851', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('94', '9', 'Our referral program', 'our-referral-program', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Our referral program</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Promote Eventbrite using customized a link</li>\r\n                                <li>\r\n                                    Earn 25% of all ticket fees generated by people you refer for one year</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you have an Eventbrite account, log in to your account and select the <strong>Account tab</strong> at the top right side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"603\" height=\"58\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/referral_program/account-tab.png\" alt=\"Eventbrite\'s Referral Program:\"></p>\r\n                                <p>\r\n                                    If you don\'t have an Eventbrite account, <a href=\"http://www.eventbrite.com/signup\">click here to sign up</a>. You do not have to create an event to join our Referral Program.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong><a href=\"http://www.eventbrite.com/referrals\">Referral program</a></strong> link, create a unique link to promote Eventbrite.</p>\r\n                                <p>\r\n                                    <img width=\"598\" height=\"257\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/referral_program/referral-program.png\" alt=\"Eventbrite\'s Referral Program:\"></p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Promote your Eventbrite link(s) by putting it on your own website or sending to prospective event holders.</p>\r\n                                <p>\r\n                                    When a new event organizer signs up for the Eventbrite service from that link, you\'ll earn <strong>2</strong><strong>5%</strong> of our base fees (e.g., our 2.5% + $0.99 fee when normal rates are applicable) collected and retained by Eventbrite from your referred event organizers for a full year following their signup!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> After you\'ve accrued more than $5 in referral payments, we\'ll send you a monthly payment via PayPal to the account you\'ve indicated on your <strong>Referral program</strong> page. <em><strong>You must have a valid PayPal account to receive any payouts</strong></em>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229855', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('95', '9', 'IBAN information', 'iban-information', 'IBAN information', '1349229860', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('96', '9', 'D&B DUNS number for Eventbrite', 'd-b-duns-number-for-eventbrite', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>D&B DUNS number for Eventbrite</h3>\r\n                        </div>\r\n                        <br/>\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            Our DUNS number is <strong>026894447</strong>.</p>\r\n                        <!--step-->', '1349229866', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('97', '9', 'W-9 for Eventbrite', 'w-9-for-eventbrite', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>W-9 for Eventbrite</h3>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            To download our current Form W-9, click <a href=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/w-9-for-eventbrite/Eventbrite%20W9%202012.pdf\">here</a>.<br>\r\n                            <br/>\r\n                        </p>\r\n                        <!--step-->', '1349229872', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('98', '9', 'TIN for Eventbrite', 'tin-for-eventbrite', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>TIN for Eventbrite</h3>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            Our Taxpayer Identification Number is <strong>141888467</strong>.</p>\r\n                        <!--step-->', '1349229877', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('99', '11', 'Collect information from attendees', 'collect-information-from-attendees', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Collect information from attendees</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Collect address, work information, age, and more</li>\r\n                                <li>\r\n                                    Create your own custom questions</li>\r\n                                <li>\r\n                                    Questions can be optional or required</li>\r\n                                <li>\r\n                                    Attendees answer questions when registering</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Your survey will appear after your attendees click to register/buy one or more tickets on your event page. To create a post-event survey, we recommend using <a href=\"http://www.surveymonkey.com/\">SurveyMonkey</a> to create a survey and then including a link to that survey in an <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">attendee email</a> sent from within Eventbrite after your event has ended.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                            <p>\r\n                                <strong>How to collect custom information from your attendees:</strong></p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the name of an event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage</strong> page, select <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"228\" height=\"172\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select 1 of 3 information-collection settings:</p>\r\n                                <p>\r\n                                    1. Collect just the <strong>name &amp; email</strong> of the person buying the ticket(s) or registering for the event (<strong>basic information</strong>).</p>\r\n                                <p>\r\n                                    2. Collect information from just the <strong>ticket buyer</strong> (one survey per order).</p>\r\n                                <p>\r\n                                    3. Collect information from <strong>each attendee</strong> (one survey per ticket).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you select option 1 or 2, the same name will appear on all tickets in a multiple-ticket order.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose from the default list of questions to ask by checking the <strong>Included </strong>box. You can choose to make a question required by checking the <strong>Required</strong> box.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customform2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can create your own custom survey questions by selecting <strong>Create a Question</strong> under <strong>Your Questions</strong> (optional). <a href=\"/en_us/t/custom-questions-for-attendees\">Learn More</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    There are a number of <strong>Optional Settings</strong> at the bottom that include:</p>\r\n                                <p>\r\n                                    - Title of, and instructions concerning, the page where you will be collecting information from your attendees;</p>\r\n                                <p>\r\n                                    - The time limit to complete an order before the tickets in that order revert from a \"pending\" state and again become available to other attendees (the default is 15 minutes);</p>\r\n                                <p>\r\n                                    - The message displayed on your event page after sales have ended;</p>\r\n                                <p>\r\n                                    - The option to allow attendees to update their information after they have registered by logging in to Eventbrite;</p>\r\n                                <p>\r\n                                    - Allowing attendees to request a refund.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/optionalsettingssurvey.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Save Changes</strong> to activate the questions/changes you\'ve made for the attendee registration process, as outlined above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> To learn how to view the information you collect from your attendees by running/exporting an Attendee Summary report to Excel, click <a href=\"/en_us/t/attendance-summary\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Collecting attendee info</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Collect address, work information, age, and more</li>\r\n                                        <li>\r\n                                            Create your own custom questions</li>\r\n                                        <li>\r\n                                            Questions can be optional or required</li>\r\n                                        <li>\r\n                                            Attendees answer questions when registering</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Your survey will appear after your attendees click to register/buy one or more tickets on your event page. To create a post-event survey, we recommend using <a href=\"http://www.surveymonkey.com/\">SurveyMonkey</a> to create a survey and then including a link to that survey in an <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">attendee email</a> sent from within Eventbrite after your event has ended.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                            <p>\r\n                                <strong>How to collect custom information from your attendees:</strong></p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’re working on an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to get to the <strong>Manage</strong> page for that event.</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"198\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, select <strong>Customize Order Form</strong> from the features menu on the left side of the page under the <strong>Registration</strong> subheading.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select 1 of 3 information-collection settings:</p>\r\n                                <p>\r\n                                    1. Collect just the <strong>name &amp; email</strong> of the person buying the ticket(s) or registering for the event (<strong>basic information</strong>).</p>\r\n                                <p>\r\n                                    2. Collect information from just the <strong>ticket buyer</strong> (one survey per order).</p>\r\n                                <p>\r\n                                    3. Collect information from <strong>each attendee</strong> (one survey per ticket).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you select option 1 or 2, the same name will appear on all tickets in a multiple-ticket order.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose from the default list of questions to ask by checking the <strong>Included </strong>box. You can choose to make a question required by checking the <strong>Required</strong> box.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customform2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can create your own custom survey questions by selecting <strong>Create&nbsp;a Question</strong> under <strong>Your Questions</strong> (optional). Learn more about this <a href=\"/en_us/t/custom-questions-for-attendees\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    There are a number of <strong>Optional Settings</strong> at the bottom that include:</p>\r\n                                <p>\r\n                                    - Title of, and instructions concerning, the page where you will be collecting information from your attendees;</p>\r\n                                <p>\r\n                                    - The time limit to complete an order before the tickets in that order revert from a \"pending\" state and again become available to other attendees (the default is 15 minutes);</p>\r\n                                <p>\r\n                                    - The message displayed on your event page after sales have ended;</p>\r\n                                <p>\r\n                                    - The option to allow attendees to update the information in their order after they have registered by logging in to their Eventbrite account;</p>\r\n                                <p>\r\n                                    - Allowing attendees to request a refund.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/optionalsettingssurvey.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Save Changes</strong> to activate the questions/changes you\'ve made for the attendee registration process, as outlined above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> To learn how to view the information you collect from your attendees by running/exporting an Attendee Summary report to Excel, click <a href=\"/en_us/t/attendance-summary\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229918', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('100', '11', 'Order confirmation customization', 'order-confirmation-customization', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Order confirmation customization</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Provide answers to FAQ\'s like parking info, age restrictions and more</li>\r\n                                <li>\r\n                                    Disable PDF tickets for ticketless events</li>\r\n                                <li>\r\n                                    Send attendees to your website after completing an order</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use the <strong>Edit Order Confirmations</strong> feature to add custom information to the order confirmation page and the order confirmation email that attendees receive after registering for your event. This could include special instructions or <a href=\"/en_us/t/webinar-registration\">webinar</a> log-in details. You can also turn off PDF tickets here--<strong><span style=\"color: #008000;\">think green!</span></strong>--if your event doesn\'t require physical tickets.</p>\r\n                                <p>\r\n                                    We recommend including the following info in your order confirmations: your refund policy, age restrictions, lost ticket policy, parking information, door times, dress code, prohibited items, and/or your contact information.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To edit your order confirmations, from the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Edit Order Confirmations</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img title=\"Edit order confirmations\" style=\"border-width: 0px; border-style: solid; width: 222px; height: 192px;\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/edit_order_conf.png\" alt=\"Edit order confirmations\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Disable PDF tickets if your event does not require physical tickets:</strong></p>\r\n                                <p>\r\n                                    Un-check the two checkboxes at the top of the page. Turning off unnecessary PDF tickets can reduce both attendee confusion and paper waste. You can always print out a <a href=\"/en_us/t/print-checkin-list\">check-in list</a>, use the <a href=\"/en_us/t/laptop-for-checkin\">Entry Management tool</a> located in the Manage page for your event, or download and use our app for <a href=\"/en_us/t/iphone-for-checkin\">iPhone</a> or <a href=\"/en_us/t/android-for-checkin\">Android</a> to check in attendees at the door to your event.</p>\r\n                                <p>\r\n                                    <strong>Make sure to advise your attendees in the order confirmation email and/or page that tickets are not included and so not required. </strong></p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"110\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/disable%20tickets.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Here\'s an example of the default PDF ticket that is attached to--and linked to in--the order confirmation email:</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"781\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/ticket.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Customize the order confirmation email:</strong></p>\r\n                                <p>\r\n                                    If you <strong>haven\'t </strong>disabled PDF tickets (see step 2 above), any text that you enter will appear on the PDF tickets that your attendees will receive and print <strong>and</strong> in the order confirmation email.</p>\r\n                                <p>\r\n                                    If you <strong>have </strong>disabled PDF tickets (see step 2 above), any text that you enter will appear in the <strong>Event Details</strong> section at the bottom of the order confirmation email. With tickets disabled, you have more formatting options for your text, and you can also enter HTML into your order confirmation email.</p>\r\n                                <p>\r\n                                    <img width=\"616\" height=\"411\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/edit_order_conf_email.png\" alt=\"How to customize the order confirmation email &amp;amp; page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> You can also change the default <strong>reply-to email address</strong> that will appear in the order confirmation email.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Below is an example of a customized confirmation email that is sent to your attendees after they register. Notice the information we\'ve added to the <strong>Event Details</strong> section at the bottom of the email.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/order_conf_email2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Customize the order confirmation page:</strong></p>\r\n                                <p>\r\n                                    Enter any information that you would like to appear in the <strong>Message </strong>section of the order confirmation page, which is the page where an attendee lands after successfully completing an order. This section reads HTML, allowing you to add links and images to your order confirmation page.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"398\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/edit_order_confirmation_page.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Below is an example of the default order confirmation page that your attendees are taken to after successfully completing their order. You can customize this page, as described above.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"594\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/custom_order_conf/order_conf_page3.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div id=\"redirect\" class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you want the page to just automatically redirect to your own website once an attendee is finished registering, clear all of the text and just enter your website URL (including the <strong>http://</strong>).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Make sure to click <strong>Save Changes</strong> when you\'re done!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Feel free to click <a target=\"_blank\" href=\"http://www.eventbrite.com/event/2152744918\">here</a> to register for a test event so you can see what the order confirmation page and email, and PDF ticket, look like.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229923', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('101', '11', 'Disable paper tickets', 'disable-paper-tickets', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Disable paper tickets</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Save paper and hassle for your attendees</li>\r\n                                <li>\r\n                                    Advise attendees that tickets aren\'t needed</li>\r\n                                <li>\r\n                                    Electronic check-in options available</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: small;\"><strong>DISABLE PDF TICKETS FOR YOUR EVENT</strong></span><br>\r\n                            &nbsp;</p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    To disable PDF tickets for an already-created event, go to your event\'s <strong>Manage</strong> page. You can get there by clicking on <strong>My Events</strong> at the top of the screen, and then clicking on the name of your event.</p>\r\n                                <p>\r\n                                    <img width=\"621\" height=\"279\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/best-practices-for-ticketless-events/manage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve published or made your event live, go to your event\'s <strong>Manage</strong> page.&nbsp;Click on&nbsp;<strong>Edit Order Confirmations</strong>, found on the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"229\" height=\"129\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/best-practices-for-ticketless-events/features.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    On the next screen, uncheck the two checkboxes near the top of the page: <strong>Attach PDF tickets in confirmation email</strong> and <strong>Include Print Tickets link in confirmation email</strong>.</p>\r\n                                <p>\r\n                                    In the <strong>Customize the order confirmation email </strong>text box, make sure to advise your attendees that tickets won\'t be issued for the event. We recommend that you also copy and paste this message into the <strong>Customize the order confirmation page</strong> text box below that.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://i392.photobucket.com/albums/pp8/postdesign/eventbrite/icon_note.gif\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re holding a webinar or other online event, you\'ll want to include all the log-in instructions for your attendees here. Be sure to include a phone number where you can be reached, in case your attendees have trouble logging in.</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"726\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/best-practices-for-ticketless-events/order%20conf.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Make sure to click <strong>Save Changes</strong> when you\'re finished!<br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: small;\"><strong>SCHEDULE A REMINDER EMAIL FOR YOUR ATTENDEES</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve&nbsp;<a href=\"/en_us/t/event-page-creation\">created an event</a> and published or made it live, create and schedule a reminder email for your attendees. Be sure to include all the information you\'ve provided in the order confirmation page and email--as detailed in step 2 above--so your attendees are fully informed!</p>\r\n                                <p>\r\n                                    Click <a href=\"/en_us/t/email-reminders-for-attendees\">here</a> to learn how to schedule or send a reminder email.<br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: small;\"><strong>USE A CHECK-IN LIST OR APP AT THE DOOR OF YOUR EVENT</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    No physical tickets? No problem! You have a number of options for checking people in at the door (click on each for more info):</p>\r\n                                <p>\r\n                                    - A printed <a href=\"/en_us/t/print-checkin-list\">check-in list</a></p>\r\n                                <p>\r\n                                    - Our laptop-based <a href=\"/en_us/t/laptop-for-checkin\">Entry Management</a> check-in tool</p>\r\n                                <p>\r\n                                    - Our Easy Entry <a href=\"/en_us/t/iphone-for-checkin\">iPhone</a> or <a href=\"/en_us/t/android-for-checkin\">Android</a> app</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229928', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('102', '11', 'Time limit for registration', 'time-limit-for-registration', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Time limit for registration</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Default time limit is 15 minutes</li>\r\n                                <li>\r\n                                    Give your attendees plenty of time to register</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'ve created a lot of <a href=\"/en_us/t/collect-information-from-attendees\">custom questions</a> for your attendees to answer at registration, it\'s a good idea to extend the registration time limit beyond the default 15 minutes. See below to learn how to do that.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, from your event\'s <strong>Manage</strong> page, select <strong>Customize Order Form </strong>from the features menu on the left side of the page. (To get to the <strong>Manage</strong> page, click on <strong>My Events </strong>at the top of the screen, then click on the name of your event.)</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div>\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Customize Order Form </strong>screen, scroll down to the<strong> Optional Settings</strong> section. You can edit the <strong>Registration time limit</strong> as you\'d like.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/timer/regtimelimit2.png\" alt=\"How to customize the registration time limit\"></p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When attendees register for your event, they will see a time limit countdown clock on the registration page. When that time limit expires, the tickets they put on hold when they initiated the order will be released and they will need to start the order process from the beginning.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/timer/regtimelimit3.png\" alt=\"How to customize the registration time limit\"></p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <!--step-->', '1349229934', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('103', '11', 'Post-event survey options', 'post-event-survey-options', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Post-event survey options</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    We recommend SurveyMonkey, Google Docs, or <a href=\"http://eventbrite.appstores.com/apps/survey-event-attendees\">Survey Gizmo</a>\r\n                                </li>\r\n\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> While we don\'t currently have a tool to create a post-event attendee survey within Eventbrite, we recommend creating a survey or form using an outside website and then using the <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Email Attendees</a> feature in Eventbrite to easily email all your attendees the link to the survey or form. See below for more info!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The first step in creating and sending a post-event attendee survey is to choose an outside survey- or form-creation tool. We recommend using <a href=\"http://www.surveymonkey.com/\">SurveyMonkey</a> or <a href=\"http://www.google.com/google-d-s/forms/\">Google Docs</a>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve selected your survey- or form-creation tool and created your survey, it\'s time to send your attendees the URL or link to the survey. The easiest way to do this is to create and send a new attendee email from within Eventbrite using the <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">Email Attendees</a> feature. Don\'t forget to paste the URL or link to the survey into the body of the email before sending!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229938', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('104', '11', 'Show custom question answers on order confirmation', 'show-custom-question-answers-on-order-confirmation', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Show custom question answers on order confirmation</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Include questions and answers on order confirmation page and email</li>\r\n                                <li>\r\n                                    Great for breakout sessions, entry gates, seating sections, starting sequences and more</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">Create custom questions</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first create the custom questions that you want your attendees to answer at registration. (Click <a href=\"/en_us/t/custom-questions-for-attendees\">here</a> to learn how to do this.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When creating a custom question, select the option to&nbsp;<strong>Show the attendee the answer to this question on the order confirmation</strong> in the&nbsp;<strong>Optional Settings</strong> box near the bottom of the page.&nbsp;</p>\r\n                                <p>\r\n                                    Click <strong>Save Changes</strong> once you\'re done.</p>\r\n                                <img width=\"604\" height=\"634\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show-answers-to-custom-questions-on-the-order-confirmation/show1.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">What your attendees will see</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After registering for your event, attendees will be taken to the order confirmation page. The answers they\'ve given to your custom questions, along with the questions themselves, will appear here. See below for an example order confirmation page!</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"903\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show-answers-to-custom-questions-on-the-order-confirmation/confpage.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    These questions and answers will also appear near the bottom of the order confirmation email they receive. Here\'s a look at a sample order confirmation email:</p>\r\n                                <p>\r\n                                    <img width=\"513\" height=\"949\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show-answers-to-custom-questions-on-the-order-confirmation/confemail.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'ve customized the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email</a>--to include instructions or answers to FAQ\'s, for example--that information will appear <strong>below</strong> the custom questions and answers in the email.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229943', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('105', '11', 'Custom questions for attendees', 'custom-questions-for-attendees', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Custom questions for attendees</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Collect whatever information you need</li>\r\n                                <li>\r\n                                    T-shirt size, meal preference, class or session selection and more</li>\r\n                                <li>\r\n                                    Require attendees to accept a waiver</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>Tip: </strong>Many event organizers use this feature to get t-shirt sizes, track class or meal-type selections, monitor referrals, require acknowledgement of a waiver, and much more. Get creative!</p>\r\n                            <p>\r\n                                If you\'re looking to create a post-event survey, we recommend using <a href=\"http://www.surveymonkey.com/\">SurveyMonkey</a> to create a survey and then including a link to that survey in an <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">attendee email</a> sent from within Eventbrite after your event has ended.</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, from the <strong>My Events</strong> page, click on the name of an event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage</strong> page, select <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 228px; height: 190px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose one of the following options (either of the last two of the three options shown in the box labeled <strong>What type of information do you want to collect?</strong>):</p>\r\n                                <p>\r\n                                    1. <strong>Collect information below for the ticket buyer only</strong><br>\r\n                                    OR<br>\r\n                                    2. <strong>Collect information below for each attendee</strong></p>\r\n                                <p>\r\n                                    <img width=\"599\" height=\"337\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectcustominfo1.png\" alt=\"How to create custom survey questions\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Create a Question </strong>under&nbsp;<strong>Your questions</strong>.</p>\r\n                                <p style=\"text-align: left; \">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 513px; height: 225px; \" src=\"/customer/portal/attachments/51842\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Input your question in the <strong>Question</strong> field. Then choose from a variety of answer formats: <strong>text</strong>, <strong>paragraph Text</strong>, <strong>multiple choice</strong> (check boxes, radio buttons, or dropdown menu), or <strong>waiver</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 358px; height: 349px; \" src=\"/customer/portal/attachments/51859\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you choose the <strong>multiple choice</strong> answer format, input the different choices that will be available to your attendees. If you wish, you can enable a <a href=\"/en_us/t/choice-limits-for-custom-questions\">choice limit</a> for each of the choices you give your attendees.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 600px; height: 539px; \" src=\"/customer/portal/attachments/51837\" alt=\"\"></p>\r\n                                <p>\r\n                                    If you choose the <strong>waiver</strong> answer format, make sure to include the request in the <strong>Question</strong> field and then input the text of the waiver in the <strong>Waiver</strong> text field below. The attendee will be required to accept the waiver in order to complete registration for your event.</p>\r\n                                <p style=\"text-align: center; \">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 600px; height: 336px; \" src=\"/customer/portal/attachments/51856\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Under <strong>Optional Settings</strong>, you can choose to show this question only for specific ticket types as well as show the attendee the answer to this question on the order confirmation. <a href=\"/en_us/t/ticket-typespecific-questions\">Learn More</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Save</strong>. On the next screen, choose whether you want this question to be <strong>Included</strong> or <strong>Required</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 685px; height: 216px; \" src=\"/customer/portal/attachments/51839\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To view the information that you\'ve collected, you\'ll want to run an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>, which you can easily export to Excel.<br>\r\n                                    <br>\r\n                                    <br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Custom questions for attendees</h3>\r\n                                <ul>\r\n                                    <ul>\r\n                                        <li>\r\n                                            Collect whatever information you need</li>\r\n                                        <li>\r\n                                            T-shirt size, meal preference, class or session selection and more</li>\r\n                                        <li>\r\n                                            Require attendees to accept a waiver</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                <strong>Tip: </strong>Many event organizers use this feature to get t-shirt sizes, track class or meal-type selections, monitor referrals, require acknowledgement of a waiver, and much more. Get creative!</p>\r\n                            <p>\r\n                                If you\'re looking to create a post-event survey, we recommend using <a href=\"http://www.surveymonkey.com/\">SurveyMonkey</a> to create a survey and then including a link to that survey in an <a href=\"/en_us/t/email-reminders-for-alreadyregistered-attendees\">attendee email</a> sent from within Eventbrite after your event has ended.</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’re working an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to get to the <strong>Manage</strong> page of that event<strong>.</strong><br>\r\n                                    <br>\r\n                                    <img width=\"574\" height=\"193\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, click <strong>Customize Order Form</strong>&nbsp;from the features list on the left side under the <strong>Registration </strong>subheading.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 228px; height: 190px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose one of the following options (either of the last two of the three options shown in the box labeled <strong>What type of information do you want to collect?</strong>):</p>\r\n                                <p>\r\n                                    1. <strong>Collect information below for the ticket buyer only</strong><br>\r\n                                    OR<br>\r\n                                    2. <strong>Collect information below for each attendee</strong></p>\r\n                                <p>\r\n                                    <img width=\"599\" height=\"337\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectcustominfo1.png\" alt=\"How to create custom survey questions\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Create a Question </strong>under&nbsp;<strong>Your questions</strong>.</p>\r\n                                <p style=\"text-align: left; \">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 513px; height: 225px; \" src=\"/customer/portal/attachments/51842\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Input your question in the <strong>Question</strong> field. Then choose from a variety of answer formats: <strong>text</strong>, <strong>paragraph Text</strong>, <strong>multiple choice</strong> (check boxes, radio buttons, or dropdown menu), or <strong>waiver</strong>.</p>\r\n                                <p style=\"text-align: left; \">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 358px; height: 349px; \" src=\"/customer/portal/attachments/51859\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If you choose the <strong>multiple choice</strong> answer format, input the different choices that will be available to your attendees. If you wish, you can enable a <a href=\"/en_us/t/choice-limits-for-custom-questions\">choice limit</a> for each of the choices you give your attendees.</p>\r\n                                <p style=\"text-align: left; \">\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 600px; height: 539px; \" src=\"/customer/portal/attachments/51837\" alt=\"\"></p>\r\n                                <p>\r\n                                    If you choose the <strong>waiver</strong> answer format, make sure to include the request in the <strong>Question</strong> field and then input the text of the waiver in the <strong>Waiver</strong> text field below. The attendee will be required to accept the waiver in order to complete registration for your event.</p>\r\n                                <p style=\"text-align: left; \">\r\n                                    <img style=\"text-align: center; border-width: 0px; border-style: solid; width: 600px; height: 336px; \" src=\"/customer/portal/attachments/51856\" alt=\"\"><br style=\"text-align: center; \">\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Under <strong>Optional Settings</strong>, you can choose to show this question only for specific ticket types as well as show the attendee the answer to this question on the order confirmation. <a href=\"/en_us/t/ticket-typespecific-questions\">Learn More</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Save</strong>. On the next screen, choose whether you want this question to be <strong>Included</strong> or <strong>Required</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 685px; height: 216px; \" src=\"/customer/portal/attachments/51839\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To view the information that you\'ve collected, you\'ll want to run an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>, which you can easily export to Excel.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229949', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('106', '11', 'Conditional logic for custom questions', 'conditional-logic-for-custom-questions', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Conditional logic for custom questions</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Ask specific questions based on answers to earlier questions</li>\r\n                                <li>\r\n                                    Get more specific information from your attendees</li>\r\n                                <li>\r\n                                    Great for seminars, classes, breakout sessions . . . and more!</li>\r\n\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p></p>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">Create your custom questions and sub-questions</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"http://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and go to your event\'s <strong>Manage</strong> page by clicking on the name of your event.</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"253\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/manage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage</strong> page, click <strong>Customize Order Form</strong>, found on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"227\" height=\"224\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/features-menu.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, choose to&nbsp;<strong>Collect information below for the ticket buyer only</strong> or&nbsp;<strong>Collect information below for each attendee</strong> in the box titled&nbsp;<strong>What type of information do you want to collect?</strong></p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"421\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/collect-info-for-buyer-or-each-attendee.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then click <strong>Create a question</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"538\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/create-a-question.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter your <strong>question</strong>, choose the <strong>question type</strong> (you can use conditional logic with checkboxes, radio buttons and dropdowns), and input the answer <strong>options available</strong> for your attendees (along with any associated choice limits, if applicable, in the <strong>Quantity</strong> field).</p>\r\n                                <p>\r\n                                    To create a sub-question, select <strong>Surface a secondary question based on answers to this question</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"312\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/conditional-questions-option.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose which answer option will trigger this sub-question, then enter the sub-question. Select the <strong>question type</strong>, and set the <strong>options available</strong> (along with any associated choice limits, if applicable, by clicking <strong>Add choice limit</strong>).</p>\r\n                                <p>\r\n                                    (In our example, an attendee will be asked \"At which time would you like to attend?\" if they select \"Hip hop\" as the answer to the original custom question \"Which style of dance are you interested in?\")</p>\r\n                                <p>\r\n                                    If you\'d like to create a second sub-question to your custom question, click <strong>Add another rule</strong>. Otherwise, click <strong>Save</strong> to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"449\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/sub-question-one-and-save.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>At this time, you can only create a sub-question for a custom question, not for another sub-question. However, you can create as many sub-questions for a custom question as you\'d like!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Back on the main&nbsp;<strong>Customize order form</strong> page, you\'ll see your custom questions, along with any sub-questions (and their answers) that you\'ve created. Here\'s where you can choose to make a custom question or sub-question required, or delete a custom question or sub-question.</p>\r\n                                <p>\r\n                                    To add another custom question, simply click <strong>Add another question</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"353\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/your-questions.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <br/>\r\n                        <br/>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">What attendees see at checkout</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <br>\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once an attendee has initiated their order, they\'ll see any custom questions you\'ve created in the <strong>Other Information</strong> section--either for each ticket in the order (if you chose to collect info from each attendee--see step 2 above) or for the entire order (if you chose to collect info from the ticket buyer only--see step 2 above).</p>\r\n                                <p>\r\n                                    The attendee can complete their order after answering all questions you\'ve made required (if any).</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"571\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/conditional-logic-for-custom-questions/registration-example.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <br/>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">View/export attendees\' answers to your questions</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Check out your event\'s <a href=\"/en_us/t/attendance-summary\"><strong>Attendee Summary report</strong></a> to see all your attendees\' answers, as well as to export them to Excel.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229954', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('107', '11', 'Collect info from each attendee', 'collect-info-from-each-attendee', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Collect info from each attendee</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Collect info from each person in a multi-ticket order</li>\r\n                                <li>\r\n                                    Request address, work info, age and more</li>\r\n                                <li>\r\n                                    Create your own custom questions</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\">\r\n                                <param value=\"always\" name=\"allowscriptaccess\">\r\n                                <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\">\r\n                                <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/>\r\n                        <p>\r\n                            <strong>How to collect information from each attendee at registration:</strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, from your event\'s <strong>Manage</strong> page, choose <strong>Customize Order Form</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"228\" height=\"172\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the top section, select the third option--<strong>Collect information below for each attendee</strong>--and indicate for which ticket types you would like to collect this information.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_attendee_info/collecteachattendee2.png\" alt=\"How to collect information from each attendee\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the next section, you can select from a number of preexisting fields to include for your attendees to answer (on the left) or create your own questions (on the right). <a href=\"/en_us/t/collect-information-from-attendees\">Learn More</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Your survey questions will appear once for each attendee in the order (i.e., if 3 tickets are purchased, then 3 sets of survey questions will appear on the checkout page). All survey answers are recorded separately for each attendee in the <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>.<br><br><br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Collect info from each attendee</h3>\r\n                                <ul>\r\n\r\n                                    <li>\r\n                                        Collect info from each person in a multi-ticket order</li>\r\n                                    <li>\r\n                                        Request address, work info, age and more</li>\r\n                                    <li>\r\n                                        Create your own custom questions</li>\r\n\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\">\r\n                                <param value=\"always\" name=\"allowscriptaccess\">\r\n                                <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\">\r\n                                <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’re working an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to get to the <strong>Manage</strong> page of that event<strong>.</strong></p>\r\n                                <p>\r\n                                    <img width=\"574\" height=\"193\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, click <strong>Customize Order Form</strong> from the features list on the left side under the <strong>Registration </strong>subheading.</p>\r\n                                <p>\r\n                                    <img width=\"228\" height=\"195\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the top section, select the third option--<strong>Collect information below for each attendee</strong>--and indicate for which ticket types you would like to collect this information.</p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_attendee_info/collecteachattendee2.png\" alt=\"How to collect information from each attendee\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the next section, you can select from a number of preexisting fields to include for your attendees to answer (on the left) or create your own questions (on the right). <a href=\"/en_us/t/collect-information-from-attendees\">Learn More</a></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Your survey questions will appear once for each attendee in the order (i.e., if 3 tickets are purchased, then 3 sets of survey questions will appear on the checkout page). All survey answers are recorded separately for each attendee in the <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229959', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('108', '11', 'Ticket type-specific questions', 'ticket-type-specific-questions', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Ticket type-specific questions</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Apply custom questions to certain ticket types</li>\r\n                                <li>\r\n                                    Collect different information based on ticket type</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click the <strong>title</strong> of your event. From the <strong>Manage</strong> event page, select <strong>Customize Order Form</strong> from the left hand feature menu.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>CREATE YOUR OWN SURVEY QUESTIONS:</strong></p>\r\n                                <p>\r\n                                    <strong>Choose one of these options:</strong></p>\r\n                                <p>\r\n                                    1. <strong>Collect information below for the ticket buyer only</strong></p>\r\n                                <p>\r\n                                    OR</p>\r\n                                <p>\r\n                                    2. <strong>Collect information below for each attendee</strong></p>\r\n                                <p>\r\n                                    <img width=\"502\" height=\"98\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/show_info_for.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Click <strong>ADD A QUESTION</strong></p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/select_add_a_question.png\" alt=\"How to create ticket type specific questions\"></p>\r\n                                <p>\r\n                                    <strong>Enter the question in the text field.</strong> You can choose from a variety of answer formats: <strong>text</strong>, <strong>paragraph text</strong>, or <strong>multiple choice</strong> (drop down, check box, or radio button), or <strong>waiver</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 608px; height: 461px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/create_a_new_question.png\" alt=\"How to create ticket type specific questions\"></p>\r\n                                <p>\r\n                                    Select <strong>Show this question for specific ticket types</strong> to select which ticket types you would like the question to apply. This question will only display for the ticket types you select.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 611px; height: 232px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/optional_settings.png\" alt=\"How to create ticket type specific questions\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the <strong>Save</strong>&nbsp;button at the bottom of the page and your settings will be saved.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can view your attendees\' answers to your questions at any time by viewing and/or exporting an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Create ticket type-specific questions for your attendees</h3>\r\n                                <ul>\r\n                                    <ul>\r\n\r\n                                        <li>\r\n                                            Apply custom questions to certain ticket types</li>\r\n                                        <li>\r\n                                            Collect different information based on ticket type</li>\r\n\r\n                                    </ul>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p>\r\n                            <object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’re working on an already-created event, click <strong>My Events</strong> at the top of the screen and then, click on the name of your event to get to the <strong>Manage</strong> page of that event<strong>.</strong></p>\r\n                                <p>\r\n                                    <img title=\"How to create ticket types\" style=\"border-width: 0px; border-style: solid; width: 574px; height: 251px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, click <strong>Customize Order Form</strong> from the features list on the left side under the <strong>Registration </strong>subheading.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorder.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose one of the following options (either of the last two of the three options shown in the box labeled <strong>What type of information do you want to collect?</strong>):</p>\r\n                                <p>\r\n                                    1. <strong>Collect information below for the ticket buyer only</strong><br>\r\n                                    <br>\r\n                                    OR<br>\r\n                                    <br>\r\n                                    2. <strong>Collect information below for each attendee</strong></p>\r\n                                <p>\r\n                                    <img width=\"611\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create-custom-questions/addquestion3.png\" alt=\"How to create custom survey questions\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'d like to collect answers to your question(s) from everyone joining you at the event, make sure to select the option to <strong>Collect information below for each attendee</strong>. This way, the ticket buyer will be able to input information for all of the people who will be accompanying them.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click the&nbsp;<strong>Create a Question&nbsp;</strong>button<strong>&nbsp;</strong>below&nbsp;<strong>Your questions</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"width: 611px; height: 268px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create-custom-questions/addquestion4.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Input your question in the <strong>Question</strong> field. Then, choose from a variety of answer formats: <strong>text</strong>, <strong>paragraph text</strong>, <strong>multiple choice</strong> (radio buttons, check boxes, or dropdown menu), or <strong>waiver</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"589\" height=\"417\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/specificticket-new5.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To apply this quesiton to those ordering a specific ticket type, make sure to check the <strong>Show this quesiton for specific ticket types</strong> box. Then, check the box next to the ticket type that the question applies to . Click the<strong> Save</strong>&nbsp;button at the bottom of the page so your settings will be saved.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 584px; height: 222px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_ticket_type_specific_questions/specificticket-new6.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can view your attendees\' answers to your questions at any time by viewing and/or exporting an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229964', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('109', '11', 'Choice limits for custom questions', 'choice-limits-for-custom-questions', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Choice limits for custom questions</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Cap the number of people that can select a given option</li>\r\n                                <li>\r\n                                    Useful for managing a limited number of t-shirt sizes or meal types</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p><object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\">\r\n                                <param value=\"always\" name=\"allowscriptaccess\">\r\n                                <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;amp;hl=en_US\" name=\"src\">\r\n                                <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the title of your event. On the <strong>Manage</strong> page, select <strong>Collect Registration Info</strong> from the left hand feature list.</p>\r\n                                <p>\r\n                                    <img width=\"228\" height=\"117\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/manage_bar.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>CREATE YOUR OWN SURVEY QUESTIONS:</strong><br>\r\n                                    Choose one of these options (note: this feature does <span style=\"text-decoration: underline;\">not</span> work when you are collecting only basic information):</p>\r\n                                <p>\r\n                                    1. Collect information below for the ticket buyer only<br>\r\n                                    OR<br>\r\n                                    2. Collect information below for each attendee</p>\r\n                                <img width=\"597\" height=\"223\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/information_to_collect.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    Click <strong>ADD A QUESTION</strong></p>\r\n                                <img width=\"385\" height=\"148\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/select_add_a_question.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    <strong>Enter the question</strong> in the text field. You can choose from a variety of answer formats: small text field, large text field or multiple choice (drop down, check box, or radio button).</p>\r\n                                <img width=\"576\" height=\"352\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/create_a_new_question.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    Select <strong>\'Set a choice limit\'</strong> and enter the inventory of options (note: applies only to Multiple Choice questions) in the field titled \'# Available\'.</p>\r\n                                <img width=\"580\" height=\"220\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/choice_limit_enabled.png\" alt=\"How to create a limit for survey choices\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the <strong>SAVE CHANGES</strong> button at the bottom of the page and your settings will be saved.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Once the inventory of a choice has been reached, that option will appear as \'not available\' on your event survey page.</p>\r\n                                <img width=\"456\" height=\"110\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/not_available.png\" alt=\"How to create a limit for survey choices\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>You can view your attendees\' answers at any time by running and/or exporting an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>.<br><br><br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Choice limits for custom questions</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Cap the number of people that can select a given option</li>\r\n                                    <li>\r\n                                        Useful for managing a limited number of t-shirt sizes or meal types</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p><object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\">\r\n                                <param value=\"always\" name=\"allowscriptaccess\">\r\n                                <param value=\"http://www.youtube.com/v/hAIXEmuV6kE?fs=1&amp;amp;hl=en_US\" name=\"src\">\r\n                                <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"http://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or <a href=\"http://www.eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://www.eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you’re working an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to get to the <strong>Manage</strong> page of that event<strong>.</strong><br><img width=\"574\" height=\"193\" border=\"0\" title=\"How to create ticket types\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new1.png\" alt=\"How to create ticket types\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage</strong> page, click <strong>Collect Cusotm Information</strong> from the features list on the left side under the <strong>Registration </strong>subheading.</p>\r\n                                <p>\r\n                                    <img width=\"238\" height=\"205\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/collectinfo-new2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>CREATE YOUR OWN SURVEY QUESTIONS:</strong><br>\r\n                                    Choose one of these options (note: this feature does <span style=\"text-decoration: underline;\">not</span> work when you are collecting only basic information):</p>\r\n                                <p>\r\n                                    1. Collect information below for the ticket buyer only<br>\r\n                                    OR<br>\r\n                                    2. Collect information below for each attendee</p>\r\n                                <img width=\"590\" height=\"489\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/collect_attendee_info/eachattendee-new4.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    Click <strong>Add a Quesiton</strong></p>\r\n                                <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/addchoice-new3.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    Enter the question in the <strong>Title of your question</strong> text field . You can choose from a variety of answer formats: small text field, large text field or multiple choice (drop down, check box, or radio button). Select <strong>\'Set a choice limit\'</strong> and enter the inventory of options (note: applies only to Multiple Choice questions) in the field titled \'# Available\'.</p>\r\n                                <img width=\"586\" height=\"442\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/addchoice-new41.png\" alt=\"How to create a limit for survey choices\"><p>\r\n                                    To add another answer choice you can click on the <strong>Add more choices</strong> link. You may also delete a choice or disable the choice limit while editing your quesiton.</p>\r\n                                <img width=\"592\" height=\"261\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/addchoice-new44.png\" alt=\"How to create a limit for survey choices\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'re done! Now just click the <strong>Save Changes</strong> button at the bottom of the page and your settings will be saved!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Once the inventory of a choice has been reached, that option will appear as <strong>N/A</strong> when someone is registering for your event and will not be able to select that option anymore.</p>\r\n                                <img width=\"583\" height=\"383\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/limit_for_survey_choices/addchoice-new6.png\" alt=\"How to create a limit for survey choices\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can view your attendees\' answers at any time by running and/or exporting an <a href=\"/en_us/t/attendance-summary\">Attendee Summary report</a>.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349229973', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('110', '11', 'Group or team registration', 'group-or-team-registration', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Group or team registration</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Attendees can create new group or join existing group at registration</li>\r\n                                <li>\r\n                                    Manage groups and view/export group assignments</li>\r\n                                <li>\r\n                                    Great option for races, group competitions, fundraising events</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">Enable and configure group registration</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a> with at least one <a href=\"/en_us/t/ticket-type-creation\">ticket type</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    <img style=\"width: 609px; height: 262px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/manage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the&nbsp;<strong>Manage</strong> page for your event, click <strong>Colle</strong><strong>ct Custom Information </strong>on the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    &nbsp;<img width=\"227\" height=\"198\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/collect.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Select the third information-collection option, <strong>Collect information below for each attendee</strong>. (You can learn more about collecting information from your attendees by going <a href=\"/en_us/t/collect-info-from-each-attendee\">here</a>.)</p>\r\n                                <p>\r\n                                    <img style=\"width: 607px; height: 438px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/eachattendee.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then scroll down to the <strong>Group Registration</strong> section and click the <strong>Activate group registration</strong> button.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"202\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/activate.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the&nbsp;<strong>Activate group registration</strong> box, first select the&nbsp;<strong>Type</strong> of group registration you\'ll be accepting. (This determines the wording that is used when an attendee is registering.)</p>\r\n                                <p>\r\n                                    Then select which of your ticket types that you\'d like the group registrations to apply to, and whether group registration is <strong>optional </strong>or <strong>required</strong>. (In our example here, we\'ve allowed group registrations for only the Community Member ticket type and made this optional.)</p>\r\n                                <p>\r\n                                    Finally, specify the <strong>maximum number of attendees per group</strong>, if you\'d like. Make sure to click <strong>Save Changes</strong> when you\'re finished.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"354\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/settings.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can change any of these settings--or disable group registration entirely--at any time before or after you make your event live by clicking <strong>Edit Team Registration</strong> in the <strong>Group Registration</strong> summary box.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"159\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/edit.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">What your attendees will see</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When registering for a ticket type that has group registration enabled, the ticket buyer can choose to create a new group or join an existing group (if one has already been created) for each of the attendees in the order.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"318\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/group-or-team-registration/team2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    The group name will appear on the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation page and order confirmation email</a> that each attendee sees and receives after registering.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">Get a list of attendees and groups</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can run (and export to Excel) an <a href=\"/en_us/t/attendance-summary\"><strong>Attendee Summary</strong> report</a> to get a list of your attendees. If an attendee is part of a group, the group name will be shown in the <strong>Group</strong> column in the report.</p>\r\n                                <p>\r\n                                    If you\'d like to get a list of attendees by group, just export the report to Excel and sort by the <strong>Group </strong>column.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229978', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('111', '11', 'Sell additional items and merchandise', 'sell-additional-items-and-merchandise', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Sell additional items and merchandise</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Attendees can add additional items when checking out</li>\r\n                                <li>\r\n                                    Offer up to 6 items, with up to 20 options (e.g., size, color) for each item</li>\r\n                                <li>\r\n                                    Great for physical merchandise, meals, classes, campsites . . . and more!</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>We recommend increasing the registration time limit if you will be including a number of additional items for purchase. As we at Eventbrite don\'t handle order fulfillment, we recommend collecting your attendees\' <a href=\"/en_us/t/collect-info-from-each-attendee\">mailing addresses</a> if you plan to ship them the items they ordered.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <br/>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">Input your additional items</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and go to your event\'s <strong>Manage</strong> page by clicking on the name of your event.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"253\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/manage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Only people ordering <strong>paid</strong> tickets or registrations will have the option to add merchandise to their order. So, make sure to create at least one paid <a href=\"/en_us/t/ticket-type-creation\">ticket type</a> for your event!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage</strong> page, click <strong>Customize Order Form</strong>, found on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"231\" height=\"175\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/customize-order-form.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Then click the&nbsp;<strong>Add additional item</strong> button.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"234\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/add-merchandise.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter a name for your item, as well as an optional&nbsp;description. Upload an&nbsp;image, or leave the default image to display at checkout.&nbsp;</p>\r\n                                <p>\r\n                                    In the <strong>Options Available</strong> section, enter the different types of your item that are available, along with the <strong>Quantity</strong> and <strong>Price</strong>--options can be free or paid--of each. Clicking the <strong>Hidden</strong> checkbox will hide that particular option from attendees.</p>\r\n                                <p>\r\n                                    The <strong>Order Restrictions</strong> option lets you set a maximum number of this item <strong>per order</strong>.</p>\r\n                                <p>\r\n                                    Make sure to click <strong>Save</strong> to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"705\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/options.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Once an item has had one sale, it cannot be deleted and its price cannot be changed--though you can hide the item. Every other field can be changed at any time.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To add additional items, just click the <strong>Add additional item</strong> button on the <strong>Customize Order Form </strong>screen and repeat the process described above.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"378\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/additional-item.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can add a maximum of <strong>six (6) items</strong>--each with up to <strong>twenty (20) options</strong>--to an event.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <br/><br/>\r\n\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">What attendees see at checkout</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once an attendee has initiated their paid order, they will see an&nbsp;<strong>Event Items (optional)</strong> box just above the&nbsp;<strong>Pay Now&nbsp;</strong>button.</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"340\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/event-merchandise.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Clicking the item will open a box showing the (optional) item description and image you entered. The attendee can specify the item and the quantity they would like, and then click <strong>Add to Order</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"313\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/item-detail.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    A confirmation message will display.</p>\r\n                                <p>\r\n                                    <img width=\"618\" height=\"165\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/item-added.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    And the <strong>Order Summary</strong> box at the top of the page will update to include the selected item(s). The attendee can click the <strong>X </strong>next to any item they wish to remove from their order.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"344\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/order-summary.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    At any point, the attendee can click <strong>Pay Now</strong> to complete the order.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <strong><span style=\"font-size: large;\">View/export merchandise-order information</span></strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access a report showing who bought what, first go to your event\'s&nbsp;<strong>Manage</strong> page by clicking <strong>My Events</strong> at the top of the screen and then clicking on the name of your event.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"254\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/manage.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Then click on <strong>Event Reports</strong>, found on the left side of your event\'s <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    <img width=\"230\" height=\"199\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/event-reports.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Merchandise Summary</strong> from the&nbsp;<strong>Report Type</strong> dropdown menu.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"434\" height=\"391\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/report-type.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Here you\'ll see which orders included merchandise, and how much was paid for the merchandise.</p>\r\n                                <p>\r\n                                    <img width=\"613\" height=\"237\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/sell-merchandise-and-additional-options/merchandise-summary.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    If you\'d like to export the report, click the <strong>File Type</strong> button next to <strong>Export</strong>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349229983', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('112', '8', 'Images for event page', 'images-for-event-page-1', '<div class=\"block-heading\">\r\n                            <h3>Images for event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Paint a picture of the event experience</li>\r\n                                <li>\r\n                                    Showcase sponsor logos</li>\r\n                                <li>\r\n                                    Include one or more images</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> The maximum file size of an image to be uploaded into the<strong> Event Details </strong>section is 1 megabyte. If your image is larger than this, first use an outside image editor to decrease its size, then follow the steps below to insert it into your event page. The <strong>Event Details </strong>section is 600 pixels wide, so you\'ll want to make sure that any image you upload is no wider than this (though it can be any height).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit </strong>next to the name of an event to go to the <strong>Edit </strong>page for that event. (If you are setting up a new event page--that is, you just pressed <strong>Create Event</strong> and the page you are on says <strong>Create an Event</strong>--you can skip this step and go directly to step 2 below.)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 3: Add Event Details</strong>, click the button on the toolbar that has a picture of a tree. (It will say \'Insert/edit image\' when you position your cursor over it.)</p>\r\n                                <p>\r\n                                    <img width=\"576\" height=\"145\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-images-to-event-page/Step3%20header.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the pop-up window labeled \'Insert/edit image,\' click the orange <strong>Browse</strong> button.</p>\r\n                                <p>\r\n                                    <img width=\"406\" height=\"314\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-images-to-event-page/firstimageuploader.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the next pop-up window, labeled \'Tiny Browser,\' click the gray <strong>Browse</strong> button<strong>. </strong></p>\r\n                                <p>\r\n                                    <strong><img width=\"513\" height=\"332\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-images-to-event-page/tinybrowser.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Find the image on your computer that you want to add, select it, and click <strong>Open</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The name of your image file and its size and type should now be showing in the \'Tiny Browser\' window. Click <strong>Upload Files</strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                7</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next screen, find your image among those that you have uploaded and click on it. Your image will appear in the <strong>Preview</strong> section of the \'Insert/edit image\' window. Click <strong>Insert</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"403\" height=\"341\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-images-to-event-page/insert.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                8</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Your image is now in the <strong>Event Details</strong> box. Well done! You can move the image around by cutting/pasting it within the box. You can also center or align the image by using the text-alignment buttons in the HTML toolbar after highlighting the image.</p>\r\n                                <p>\r\n                                    <img width=\"390\" height=\"380\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-images-to-event-page/editimage.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img style=\"border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; \" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you want to insert an image that you have already uploaded from your computer into Eventbrite, click on the tree icon in the<strong> Event Details</strong> box, press the orange <strong>Browse </strong>button, then select the <strong>Insert</strong> tab at the top of the \'Tiny Browser\' pop-up window. Now continue from step 7 above to insert the image.</p>\r\n                                <p>\r\n                                    For more information about inserting an image into the custom header and/or footer on your event page, go <a href=\"/en_us/t/custom-header-or-footer-image\">here</a>.<br>\r\n                                    <br>\r\n                                    <br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230018', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('113', '8', 'Privacy settings for event', 'privacy-settings-for-event', '<div class=\"block-heading\">\r\n                            <h3>Privacy settings for event</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Overview of options for private events</li>\r\n                                <li>\r\n                                    Invite-only, password-protected and private event</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"<?php echo Yii::app()->request->baseUrl ?>/images/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>By default, your event will be marked as public, and so will be made available for listing on the major search engines and in the Eventbrite directory.</p>\r\n                                <p>\r\n                                    If you\'d like to make your event private, go to <strong>Step 8: Set Privacy</strong> on the <strong>Edit </strong>page for your event, select <strong>This event is private and should not be listed in the directory or on search engines</strong>, and check out the options below, which are listed in order from most to least private.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: medium;\"><strong>Here\'s a quick tutorial video (or find written information below):</strong></span><br><br>\r\n                            &nbsp;</p>\r\n                        <p style=\"text-align: center;\">\r\n                        <div bginactive=\"url(chrome://flashblock/content/flash.png) no-repeat center\" bgactive=\"url(chrome://flashblock/content/flashplay.png) no-repeat center\" style=\"background: url(\'chrome://flashblock/content/flash.png\') no-repeat scroll center center transparent ! important; min-width: 32px ! important; min-height: 32px ! important; width: 555px; height: 360px; border: 1px solid rgb(223, 223, 223); cursor: pointer; overflow: hidden; display: inline-block; visibility: visible ! important; -moz-box-sizing: border-box; margin-right: auto; margin-left: auto;\" title=\"http://www.youtube.com/v/zF7SnKtGAFw?version=3&amp;amp;hl=en_US\" dataattribute=\"http://www.youtube.com/v/zF7SnKtGAFw?version=3&amp;amp;hl=en_US\" tabindex=\"0\" role=\"button\"></div></p>\r\n                        <p style=\"text-align: center;\">\r\n                            <br><br><span style=\"font-size: medium;\"><strong>Some more info about optional settings for private events:</strong></span></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Most private: invite-only private event page</strong></p>\r\n                                <p>\r\n                                    Only those email addresses to which you send out email invitations using Eventbrite will be able to register for your event. The invitee\'s email address will be hard-coded in at registration and cannot be changed. Each invitation is good for one order, and cannot be used more than once.&nbsp;</p>\r\n                                <p>\r\n                                    For more information on the invite-only feature and instructions on how to enable it, check out <a href=\"/en_us/t/invitationonly-events\">this tutorial</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Password-protected private event page</strong></p>\r\n                                <p>\r\n                                    If you select this option, your attendees will be required to enter a password that you create in order view the event details and ticket information and then register for your event.</p>\r\n                                <p>\r\n                                    To enable this feature, go to <strong>Step 8: Set Privacy </strong>on your event\'s <strong>Edit</strong> page, select the option <strong>Require a password for people to view and register for your event</strong>, and input your password. Click <strong>Save Changes</strong> in the upper or lower right area of the page.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"284\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/options-for-a-private-event/password.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    See below to see how a password-protected event page appears to someone visiting it.</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"288\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/options-for-a-private-event/private.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"<?php echo Yii::app()->request->baseUrl ?>/images/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Don\'t forget to send your attendees the password, otherwise they won\'t be able to view your event details and register!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Private event page </strong></p>\r\n                                <p>\r\n                                    Anyone can view a private event page, though it will not be made available to the major search engines or listed in the Eventbrite public event directory. To make an event private, go to <strong>Step 8: Set Privacy </strong>on your event\'s <strong>Edit </strong>page and select <strong>This event is private and should not be listed in the directory or on search engines</strong>.</p>\r\n                                <p>\r\n                                    You have the option of allowing your attendees to share your private event page social media sites or by email. To enable this option for a private event, select <strong>Attendees can share the event with their friends on sites like Facebook, Twitter and LinkedIn</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"605\" height=\"233\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/options-for-a-private-event/share.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    If this option is enabled, the sharing buttons shown at the top of all public event pages (indicated below) will remain on your private event page. If this option is <em>not</em> enabled, then those share buttons will not appear on your event page.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"248\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/options-for-a-private-event/buttons.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349230023', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('114', '8', 'Copy an event page', 'copy-an-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Copy an event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Quick and convenient event-page creation</li>\r\n                                <li>\r\n                                    Create event \'templates\' for easy setup</li>\r\n                                <li>\r\n                                    Good for events that happen more than once</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the title of your event that you want to copy. This will take you to the <strong>Manage </strong>page for that event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage </strong>page, click the <strong>Copy</strong> button in the upper right.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 525px; height: 186px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/copy.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next screen, choose a new name for your new event page. Then click the <strong>Copy Event</strong> button.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 357px; height: 389px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/copy_event.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll now be taken to the <strong>Edit </strong>page for the new draft event that you\'ve created. Here you can make any changes you\'d like to the new event page. Be sure to click <strong>Save As Draft </strong>or <strong>Save &amp; Publish</strong> once you\'ve made your changes.</p>\r\n                                <p>\r\n                                    <img style=\"width: 525px; height: 263px; border-width: 0px; border-style: solid; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/draft.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img style=\"border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; \" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'d like to reuse the personalized link/URL from the old event that you copied, so that anyone with that link can register for your new event, go <a href=\"/en_us/t/reuse-a-custom-urlweb-address\">here</a> to learn how.<br>\r\n                                    <br>\r\n                                    <br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Copy an event page</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Quick and convenient event-page creation</li>\r\n                                    <li>\r\n                                        Create event \'templates\' for easy setup</li>\r\n                                    <li>\r\n                                        Good for events that happen more than once</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, click on <strong>My Events</strong> at the top of the screen. From the <strong>My Events</strong> page, click on the title of your event that you want to copy. This will take you to the <strong>Manage </strong>page for that event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Manage </strong>page, click the <strong>Copy</strong> button in the upper right.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 525px; height: 204px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/copyevent_1.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next screen, choose a new name for your new event page. Then click the <strong>Copy Event</strong> button.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 372px; height: 285px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/copyevent_2.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll now be taken to the <strong>Event Details </strong>page for the new draft event that you\'ve created. Here you can make any changes you\'d like to the new event page. Be sure to click <strong>Save </strong>or <strong>Make Event Live</strong> once you\'ve made your changes.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 525px; height: 329px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/copy_an_event/copyevent_3.png\" alt=\"How to copy an event registration page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img style=\"border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; width: 22px; height: 28px; \" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong> If you\'d like to reuse the personalized URL/customized web address from the old event that you copied, so that anyone with that link can register for your new event, go <a href=\"/en_us/t/reuse-a-custom-urlweb-address\">here</a> to learn how.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349230028', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('115', '8', 'Custom URL/web address for event page', 'custom-url-web-address-for-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Custom URL/web address for event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Brand the URL for your event page</li>\r\n                                <li>\r\n                                    Choose an easy-to-remember web address</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'d like to reuse the personalized link/URL that you used for a past event, click <a href=\"/en_us/t/reuse-a-custom-urlweb-address\">here</a> to learn how!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to create or edit the customized web address for an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"270\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Edit</strong> page, scroll to the bottom of <strong>Step 3: Add Event Details</strong> to the shaded box that says <strong>Personalize the link for your event</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"327\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/personalize.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Enter any combination of numbers and/or lower-case letters to create a personalized link for your event page. If you\'ve chosen a link that isn\'t already in use, you\'ll see a green check mark with <strong>Available!</strong> next to it. Make sure to save your changes!</p>\r\n                                <p>\r\n                                    <img width=\"617\" height=\"95\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/personalized.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> A personalized link can be an important part of a branding and marketing strategy for your event. We recommend creating a link that is short, easy to remember, and that directly&nbsp;relates to your event or organization.</p>\r\n                                <p>\r\n                                    An example of a personalized link is <a href=\"http://productdemo.eventbrite.com\"><strong>http://productdemo.eventbrite.com</strong></a> (which links to our regularly-scheduled live online demos of Eventbrite that you can sign up for).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve published your event page with your personalized link set and saved, you can publicize that link to lead people to your event page!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Creating a personalized link that is relevant to your event or organization can help the <a href=\"http://en.wikipedia.org/wiki/Search_engine_optimization\"><span style=\"background-color: #ffffff;\">SEO</span></a> of your public event page.&nbsp;<span style=\"background-color: #ffffff;\"><a href=\"http://www.google.com/insights/search/\">Google Insights for Search</a></span> is a free, handy tool that can help you choose which popular, relevant keywords to incorporate into your personalized link. And for more information about promoting your public event page, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Customize and promote the web address of your event page</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Brand the URL for your event page</li>\r\n                                    <li>\r\n                                        Choose an easy-to-remember web address</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'d like to reuse the customized web address/URL that you used for a past event, click <a href=\"/en_us/t/reuse-a-custom-urlweb-address\">here</a> to learn how!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to create or edit the customized web address for an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name to go to your event\'s <strong>Event Details</strong> page.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"270\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once on your event\'s&nbsp;<strong>Event Details</strong> page, go to <strong>Step 3: </strong><strong>Privacy Settings</strong>. Find the <strong>Customize Web Address </strong>field and enter any combination of numbers and/or lower-case letters to create a personalized link for your event page. Click <strong>Save</strong> at the top or bottom right of the page to ensure the change has been made.</p>\r\n                                <p>\r\n                                    <img width=\"609\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/customurlnew1.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    If you\'re notified that you\'ve chosen a link that\'s already in use, feel free to choose another!</p>\r\n                                <p>\r\n                                    <img width=\"609\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/customize-the-web-address-of-your-event-page/customurlnew2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> A personalized link can be an important part of a branding and marketing strategy for your event. We recommend creating a link that is short, easy to remember, and that directly relates to your event or organization.</p>\r\n                                <p>\r\n                                    An example of a personalized link is <a href=\"http://productdemo.eventbrite.com\"><strong>http://productdemo.eventbrite.com</strong></a> (which links to our regularly-scheduled live online demos of Eventbrite that you can sign up for).</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you have your personalized link set and saved and have made your event live, you can publicize that link to lead people to your event page!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Creating a personalized link that is relevant to your event or organization can help the <a href=\"http://en.wikipedia.org/wiki/Search_engine_optimization\"><span style=\"background-color: #ffffff;\">SEO</span></a> of your public event pa<span style=\"background-color: #ffffff;\">ge. <a href=\"http://www.google.com/insights/search/\">Google Insights for Search </a></span>is a free, handy tool that can help you choose which popular, relevant keywords to incorporate into your personalized link. And for more information about promoting your public event page, click <a href=\"/en_us/t/promotional-tools-overview\">here</a>!</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349230033', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('116', '8', '\"Registration\" vs. \"ticketing\" terminology', '-registration--vs---ticketing--terminology', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>\"Registration\" vs. \"ticketing\" terminology</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Use \'registration\' instead of \'ticketing\' terminology</li>\r\n                                <li>\r\n                                    Great for non-ticketed events like conferences, classes, and endurance sports</li>\r\n                                <li>\r\n                                    Changes the wording on entire attendee-facing registration flow</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on the name of your event to go to that event\'s <strong>Manage</strong> page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Manage</strong> page, select <strong>Event Type &amp; Language</strong> from the features menu on the left side of the&nbsp;page.</p>\r\n                                <img width=\"217\" height=\"139\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-type/terminology1.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the <strong>Event Type</strong> for your event.&nbsp;<strong>Ticketed Event</strong> will apply terminology referencing \'tickets\' to your event; <strong>Registration Event</strong> will apply terminology referencing \'registrations\' to your event.</p>\r\n                                <p>\r\n                                    Your selection will affect the terminology on your event page and registration flow, and in all attendee-facing emails (e.g., the <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email</a>) and <a href=\"/en_us/t/sell-tickets-on-your-website\">widgets</a>.</p>\r\n                                <p>\r\n                                    Once you select your <strong>Event Type</strong>, the preview of your event page will reflect your selection. Make sure to click <strong>Save Changes </strong>once you\'re finished.</p>\r\n                                <p>\r\n                                    <img width=\"604\" height=\"752\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-type/eventlanguage2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You can change your <strong>Event Type </strong>selection at any point, even after your event is live! Your <strong>Event Type</strong> selection will automatically carry over for the next event you create, though you\'re free to change the selection.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can always check out your actual event page by clicking<strong> View </strong>or <strong>Preview</strong> in the upper left of the <strong>Manage</strong> page.</p>\r\n                                <p>\r\n                                    If you selected <strong>Registration Event</strong>, you\'ll see that the box that was once labeled <strong>Ticket Information</strong> has changed to <strong>Registration</strong>.</p>\r\n                                <img width=\"600\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-type/terminology61.png\" alt=\"\"><p>\r\n                                    And if you selected <strong>Ticketed Event</strong>, the box is now labeled <strong>Ticket Information</strong>, instead of <strong>Registration</strong>.</p>\r\n                                <img width=\"600\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-type/terminology51.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349230037', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('117', '8', 'Reuse a custom URL/web address', 'reuse-a-custom-url-web-address', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Reuse a custom URL/web address</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Use a custom URL from a past event for a new event</li>\r\n                                <li>\r\n                                    People going to that URL can seamlessly register for the new event</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve <a href=\"/en_us/t/event-page-creation\">created your new event</a>, or <a href=\"/en_us/t/copy-an-event-page\">copied a past event</a> and updated the relevant details, save the new event as a draft by selecting the <strong>Save as Draft</strong> button on the <strong>Edit</strong> page of your event.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Locate your old event, which has the personalized link/URL that you\'d like to use for your new event, by clicking <strong>My Events</strong> at the top of the page, then selecting the <strong>Completed</strong> tab. Once you have found your previous event, click the <strong>Edit</strong> link, under <strong>Quick Links</strong>, to the right of the event\'s title.<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"619\" height=\"218\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/completed.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Scroll to the bottom of <strong>Step 3: Add Event Details</strong> to view the area to <strong>Personalize Your Link</strong>. Using your mouse, highlight and delete the text found in the <strong>Personalize Your Link</strong> box. Scroll back to the top of the page and click <strong>Save Changes</strong>. This will free up that personalized link/URL to use for your new event.</p>\r\n                                <img width=\"615\" height=\"328\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/URL1.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Don\'t forget to jot down or copy the personalized link, so that you remember the exact link when you are ready to input it into the <strong>Personalize Your Link</strong> box on your new event\'s <strong>Edit </strong>page!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Hang in there, you\'re almost done! Let\'s apply that personalized link to your new event and then you\'ll be done! To do this, click <strong>My Events</strong> at the top of the page, then select the <strong>Edit</strong> link to the right of the title of your current event, under <strong>Quick Links</strong>. (If your new event is still in draft mode, click the <strong>Draft</strong> tab to view your event, then click on <strong>Edit</strong> as described above.)</p>\r\n                                <img width=\"610\" height=\"346\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/URL2.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Scroll to the bottom of <strong>Step 3: Edit Event Details </strong>to view the<strong> Personalize Your Link</strong> text box and enter the link that you deleted from your old event. Scroll to the top of the page and click <strong>Save Changes</strong>.</p>\r\n                                <p>\r\n                                    Well done! Your personalized that you used for your old event will now redirect new attendees to your new event page when they visit that link!</p>\r\n                            </div>\r\n                            <br/>\r\n\r\n                            <div class=\"block-step\">\r\n                                <div class=\"block-heading\">\r\n                                    <h3>\r\n                                        <a name=\"newcreate\"></a> Reuse a custom URL/web address</h3>\r\n                                    <ul>\r\n                                        <li>\r\n                                            Use a custom URL from a past event for a new event</li>\r\n                                        <li>\r\n                                            People going to that URL can seamlessly register for the new event</li>\r\n                                    </ul>\r\n                                </div>\r\n                            </div>\r\n                            <br><div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    1</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        To get started, either <a href=\"/en_us/t/event-page-creation\">create a new event</a>, or <a href=\"/en_us/t/copy-an-event-page\">copy a past event</a> and then update the relevant details in the new draft event. Then click <strong>Save </strong>or <strong>Make Event Live</strong>.</p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    2</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Locate your old event that has the customized web address/URL that you\'d like to use for your new event by clicking <strong>My Events</strong> at the top of the page, then selecting the <strong>Completed</strong> tab. Once you’ve found your old event, click the <strong>Edit</strong> link, under <strong>Quick Links</strong>, to the right of the event\'s title.</p>\r\n                                    <p>\r\n                                        <img width=\"620\" height=\"234\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/1.png\" alt=\"\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    3</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Scroll down to <strong>Customize Web Address</strong> in <strong>Step 3: </strong><strong>Privacy Settings</strong>. Using your mouse, highlight and delete the customized portion.</p>\r\n                                    <p>\r\n                                        <img width=\"609\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/changecustomurl.png\" alt=\"\"></p>\r\n                                    <p>\r\n                                        Now click <strong>Save</strong> in the upper or lower right of the page. That customized web address is now free for you to use for your new event.</p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    4</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Now we can apply that personalized link to your new event. To do this, click <strong>My Events</strong> at the top of the page, and then click <strong>Edit</strong>, under <strong>Quick Links</strong>, to the right of the title of your event. (If your new event is still in draft mode, first click the <strong>Draft</strong> tab, then click on <strong>Edit</strong> as described above.)</p>\r\n                                    <img width=\"619\" height=\"226\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/3.png\" alt=\"\">\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    5</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Scroll down to the <strong>Customize Web Address</strong> field in <strong>Step 3: </strong><strong>Privacy Settings</strong> and enter the customized web address that you deleted from your old event. Make sure to click <strong>Save</strong> in the upper or lower right of the page.</p>\r\n                                    <p>\r\n                                        <img width=\"609\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-reuse-personalized-link-from-a-completed-event/changecustomurl1.png\" alt=\"\"></p>\r\n                                    <p>\r\n                                        Presto! Your customized web address/URL that you used for your old event will now redirect people to your new event page when they visit that link!</p>\r\n                                </div>\r\n                            </div>\r\n\r\n\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349230043', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('118', '8', 'Repeating events', 'repeating-events', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Repeating events</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    One registration page for an event happening on multiple dates/times</li>\r\n                                <li>\r\n                                    All dates/times must be at the same location</li>\r\n                                <li>\r\n                                    Attendees can choose date/time they want to attend</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use the <strong>Repeat Event</strong> feature to offer multiple event dates on one registration page. There is a limit of 200 dates per repeating event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In the <strong>WHEN</strong> section of the <strong>Event Creation</strong> page, check the box titled <strong>Yes, This Event Repeats</strong></p>\r\n                                <img width=\"324\" height=\"412\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_repeating_event/add_when.png\" alt=\"How to create a repeating event\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Choose from a repeating pattern of <strong>Daily, Weekly,</strong> or <strong>Monthly</strong>. For each option, there are sub-options to further specify the repeating pattern of your event. Alternatively, you can choose the \'Other\' option to select your own repeating event pattern.&nbsp;</p>\r\n                                <img width=\"318\" height=\"270\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_repeating_event/daily_repeating.png\" alt=\"How to create a repeating event\"><img width=\"315\" height=\"444\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_repeating_event/other_repeating.png\" alt=\"\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    When attendees register for your event, they will be able to choose from the multiple dates that you\'ve created.</p>\r\n                                <img width=\"541\" height=\"189\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_repeating_event/ticket.png\" alt=\"How to create a repeating event\"></div>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349230047', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('119', '8', '\"Event capacity\" explained', '-event-capacity--explained', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>\"Event capacity\" explained</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Cap the number of tickets available across all ticket types</li>\r\n                                <li>\r\n                                    Guarantee you don\'t oversell your venue</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <strong>Tip:</strong> The <strong>Event Capacity</strong> tool sets the maximum number of tickets--across <strong>all </strong>of your ticket types--that can be sold for your event. For example, this feature lets you create two or more ticket types--each with its own <strong>Quantity Available</strong>--and allows you to cap the total number of tickets sold for the event across all ticket types.</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to view or edit the <strong>Event Capacity</strong> for an already-created event--after having created at least two <a href=\"/en_us/t/ticket-type-creation\">ticket types</a> for that event--click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 595px; height: 267px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/managing-your-event-capacity/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On your event\'s <strong>Edit</strong> page, go to <strong>Step 2: Add Ticket Information</strong>.</p>\r\n                                <p>\r\n                                    Find the <strong>Event Capacity</strong> field. This field is blank by default, which will allow you to sell up to the <strong>Quantity Available</strong> that you have set for each of your ticket types. (To edit the <strong>Quantity Available</strong> for a ticket type, click on the name of that ticket type to open the <strong>Edit Ticket</strong> window.)</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 595px; height: 226px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/managing-your-event-capacity/capacity.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img style=\"border-width: 0px; border-style: solid;\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If your event has only one ticket type, there\'s no need to set an <strong>Event Capacity</strong> for your event. Your event page will display as&nbsp;<strong>Sold Out</strong> once ticket sales reach the <strong>Quantity Available </strong>for that ticket type, which is set and can be edited as described in step 2 above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    If your event has two or more ticket types, you can set an <strong>Event Capacity</strong> for your event, if you\'d like. This will set the overall maximum number of tickets that can be sold for the event.</p>\r\n                                <p>\r\n                                    As an example, let\'s say you have a venue with a total capacity of 100 people. You\'re selling two ticket types--General Admission and VIP--and don\'t care about the combination of ticket types that you sell, as long as you don\'t sell more than 100 total tickets. In this case, you\'d want to set the <strong>Quantity Available</strong> for each ticket type--as described in step 2 above--to 100, and then also set the <strong>Event Capacity</strong> to 100.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 595px; height: 248px;\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/managing-your-event-capacity/example.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349230052', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('120', '8', 'Design tips for your event page', 'design-tips-for-your-event-page', 'Design tips for your event page', '1349230057', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('121', '8', 'Videos for event page', 'videos-for-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Videos for event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Embed YouTube or video from another video-sharing site</li>\r\n                                <li>\r\n                                    Include promo videos or footage of keynote speakers</li>\r\n                                <li>\r\n                                    Attendees know what to expect at your event</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In order to add video to the <strong>Event Details</strong> section, you first need to upload a video file to a video-hosting site--like&nbsp;<a href=\"http://www.youtube.com\">YouTube</a>--or else choose a video already uploaded to that site.</p>\r\n                                <p>\r\n                                    Then select and copy the <strong>old</strong> <strong>embed code</strong>--often accessed via a button that looks like <strong>&lt;embed&gt;</strong>--for that video, after setting the video\'s width to 600 pixels or less.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The embedded video <strong>cannot</strong> contain iframes.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on <strong>Edit </strong>next to the title of the event to go to the <strong>Edit </strong>page for that event. (If you are setting up a new event page--that is, you just pressed <strong>Create Event</strong> and the page you are on says <strong>Create an Event</strong>--you can skip this step and go directly to step 3 below.)</p>\r\n                                <p>\r\n                                    <img width=\"615\" height=\"276\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 3: Add Event Details</strong>, click the <strong>HTML</strong> button on the right side of the toolbar (It will say \"Edit HTML Source\" when you position your cursor over it).</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"291\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/html.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Paste the <strong>embed code </strong>into the HTML code at the point where you\'d like the video to appear. (Don\'t worry, you can always easily move it around in the event editor later). Click <strong>Update</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"610\" height=\"498\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/update.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Your video won\'t be viewable in the event editor, but will be viewable on the <strong>Preview </strong>page and on the published event page. Click <strong>Preview </strong>(for an unpublished event) or <strong>View</strong> (for a published event) to see how your updated event page looks.</p>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    5</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Your video is now in the <strong>Event Details</strong> box. Excellent! In the event editor, you can reposition the video by dragging it or copying/pasting it within the box. You can also center or align the video by clicking on the video and using the text-alignment buttons in the HTML toolbar.</p>\r\n                                    <p>\r\n                                        <img width=\"601\" height=\"553\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/page.png\" alt=\"\"><br>\r\n                                        <br>\r\n                                        <br>\r\n                                        &nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Add a video to your event page</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Embed YouTube or video from another video-sharing site</li>\r\n                                    <li>\r\n                                        Include promo videos or footage of keynote speakers</li>\r\n                                    <li>\r\n                                        Attendees know what to expect at your event</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In order to add video to the <strong>Event Details</strong> section, you first need to upload a video file to a video-hosting site--like <a href=\"www.youtube.com\">YouTube</a>--or else choose a video already uploaded to that site. Then select and copy the <strong>old</strong> <strong>embed code</strong>--often accessed via a button that looks like <strong>&lt;embed&gt;</strong>--for that video, after setting the video\'s width to 609 pixels or less.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The embedded video <strong>cannot</strong> contain iframes.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started in Eventbrite, first <a href=\"http://eventbrite.com/signup\">sign up</a> for a free account or <a href=\"http://eventbrite.com/login\">log in</a> to your existing Eventbrite account and <a href=\"http://eventbrite.com/create\">create an event</a>.</p>\r\n                                <p>\r\n                                    If you would like to edit an exisiting event, click<strong> My Events</strong> at the top of the screen, and then click on <strong>Edit</strong> to the right of your event\'s name.</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"200\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/edit1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    In <strong>Step 1: Add your Event Details</strong>, click the <strong>HTML</strong> button on the right side of the toolbar (which is titled \"Edit HTML Source\" when you position your cursor over the button).</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"307\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/html1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Paste the <strong>embed code </strong>into the HTML code at the point where you\'d like the video to appear. (Don\'t worry, you can always easily move it around in the event editor later). Click <strong>Update</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"500\" height=\"431\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/source.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> Your video won\'t be viewable in the event editor, but can be previewed on your event page by clicking <strong>Colors &amp; Options</strong> in the upper left.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Your video is now in the <strong>Event Details</strong> box. Excellent! In the event editor, you can reposition the video by dragging it or copying/pasting it within the box. You can also center or align the video by clicking on the video and using the text-alignment buttons in the HTML toolbar.</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"361\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/show.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Click <strong>Colors &amp; Options </strong>in the upper left to take a look at how your event page will look! Make sure to click <strong>Save</strong> to save your changes.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"684\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/how-to-add-video-to-event-details/video.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230063', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('122', '8', 'Custom header or footer image', 'custom-header-or-footer-image', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Custom header or footer image</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Liven up your event page</li>\r\n                                <li>\r\n                                    Mimic your website or branding with images or graphics</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Here is what you will need before you get started:&nbsp;</p>\r\n                                <p>\r\n                                    - an Eventbrite account</p>\r\n                                <p>\r\n                                    - image-editing software (e.g., Adobe Photoshop or Adobe Illustrator)</p>\r\n                                <p>\r\n                                    - an image-hosting site / account&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Create and correctly size your image</strong></p>\r\n                                <p>\r\n                                    In the image editor of your choice, create an image that is <strong>960 pixels wide</strong>, which is the width of your event page (the height of the image can vary). Here is an example (note: we\'ve shrunk the example to fit this space):</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"117\" border=\"0\" src=\"http://blog.eventbrite.com/wp-content/uploads/2010/09/smphdr-956px.jpg\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Upload your image to an image-hosting site</strong></p>\r\n                                <p>\r\n                                    There are many image hosting solutions to chose from. Once you have uploaded your image, you will see a number of sharing options. Highlight the URL provided for the <strong>direct link</strong> and copy the URL to your clipboard.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/direct_link.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Insert your image into the custom header or footer on your event page</strong></p>\r\n                                <p>\r\n                                    If you haven\'t done so already, check the <strong>Show custom header &amp; footer</strong> option under <strong>Additional Options</strong> on the <strong>Edit </strong>page for your event.&nbsp;</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/show_header.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once the <strong>Custom Header </strong>(or <strong>Custom Footer</strong>) box pops up, enter the line of code shown in the image below, pasting the URL for your image in place of the URL highlighted in blue below. Remember to put quotations before and after your pasted URL. Click on <strong>Apply</strong>, and then on <strong>Save Changes</strong> in the upper or lower right of the main <strong>Edit</strong> page.</p>\r\n                                <img width=\"609\" height=\"215\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/URL.png\" alt=\"\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Adjust the color scheme of your event page, if needed</strong></p>\r\n                                <p>\r\n                                    To customize the color scheme of your event page--e.g., to match the custom header and/or footer you just inserted--go the <strong>Step 7: Add Colors</strong> on the <strong>Edit </strong>page for your event. Click the <strong>Choose your own colors</strong> tab.</p>\r\n                                <img width=\"604\" height=\"217\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/step_7.png\" alt=\"\">\r\n                                <p>\r\n                                    To alter the color of a given element--such as the background or links--click on the colored rectangle next the name of that element. In the pop-up window, move the color-selector circle within the color field until you find the exact hue for you. Click <strong>Save</strong>, then make sure to click <strong>Save Changes</strong> in the upper or lower right of the main <strong>Edit</strong> page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/color_select.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To mirror your website\'s color scheme on your event page, check out <a href=\"http://www.colorzilla.com/firefox/\">ColorZilla</a>, a handy Firefox add-on.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Preview or view the changes you\'ve made to your page</strong></p>\r\n                                <p>\r\n                                    To see what your page looks like, click on the <strong>View </strong>or <strong>Preview</strong> button in the upper left. Well done!&nbsp;Your event page just got <strong><em>briter</em></strong>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:&nbsp;</strong>Click <a href=\"http://www.eventbrite.com/event/2606983558\">here</a> to take a look at a sample event page that has an image in the custom header!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Insert an image into the custom header / footer</h3>\r\n                            </div>\r\n                            <ul>\r\n                                <li>\r\n                                    Liven up your event page</li>\r\n                                <li>\r\n                                    Mimic your website or branding with images or graphics</li>\r\n                            </ul>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Here is what you will need before you get started:</p>\r\n                                <p>\r\n                                    - to have <a href=\"https://www.eventbrite.com/signup\">signed up for a free Eventbrite account</a> or <a href=\"https://www.eventbrite.com/login\">logged in to your existing Eventbrite account</a> and <a href=\"/en_us/t/event-page-creation\">created an event</a></p>\r\n                                <p>\r\n                                    - image-editing software (e.g., Adobe Photoshop, Adobe Illustrator, Snag-It or Skitch)</p>\r\n                                <p>\r\n                                    - an image-hosting site / account (we recommend <a href=\"http://tinypic.com/\">TinyPic</a>)</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Create and correctly size your image</strong></p>\r\n                                <p>\r\n                                    In the image editor of your choice, create an image that is <strong>960 pixels wide</strong>, which is the width of your event page. (The height of the image can vary.) Here is an example (note that we\'ve shrunk the example to fit this space):</p>\r\n                                <p>\r\n                                    <img width=\"592\" height=\"117\" border=\"0\" src=\"http://blog.eventbrite.com/wp-content/uploads/2010/09/smphdr-956px.jpg\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Upload your image to an image-hosting site</strong></p>\r\n                                <p>\r\n                                    There are many image-hosting solutions to choose from. For this tutorial, we’ll use <a href=\"http://tinypic.com/\">TinyPic</a>. Once you’ve uploaded your image, you’ll see a number of sharing options. Highlight the URL provided for the <strong>direct link</strong> and copy the URL to your clipboard.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/direct_link.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Insert your image into the custom header or footer on your event page</strong></p>\r\n                                <p>\r\n                                    Back in Eventbrite, go to the <strong>Colors &amp; Options&nbsp;</strong>page for your event, if you’re not already there.</p>\r\n                                <p>\r\n                                    (To get there, click <strong>My Events </strong>at the top of the screen, then click <strong>Edit</strong>, under <strong>Quick Links</strong>, to the right of the title of your event. Next click <strong>Colors &amp; Options</strong> in the upper left.)</p>\r\n                                <p>\r\n                                    On the <strong>Colors &amp; Options</strong> page, click the <strong>Add HTML</strong> link.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 612px; height: 184px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/custom_3.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once the <strong>Add HTML</strong> window opens, enter the line of code shown in the image below, pasting the URL for your image in place of the URL between the quotation marks, as shown below. Remember to put quotations before and after your pasted URL.</p>\r\n                                <p>\r\n                                    <img width=\"506\" height=\"350\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/custom_4.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    <strong>Note</strong>: You can edit the custom footer by clicking the <strong>Custom Footer</strong> link in the <strong>Add HTML </strong>window.</p>\r\n                                <p>\r\n                                    Click <strong>Save</strong> to close the <strong>Add HTML </strong>window when you’ve finished. Then click <strong>Save </strong>in the upper right of the <strong>Colors &amp; Options</strong>&nbsp;page. Well done!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Adjust the color scheme of your event page, if needed</strong></p>\r\n                                <p>\r\n                                    To customize the color scheme of your event page&mdash;e.g., to match the custom header and/or footer you just inserted&mdash;click <strong>modify colors</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 614px; height: 189px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/custom_5.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To alter the color of a given element&mdash;such as the background or links&mdash;click on the colored square next the name of that element. In the pop-up window, move the vertical-sliding bar and/or color-selector circle within the color field until you find the exact hue for you. (Or you can enter the <a href=\"http://www.w3schools.com/html/html_colors.asp\">hexadecimal value</a> for a color in the alphanumeric field.) The preview of your event page will update automatically.</p>\r\n                                <p>\r\n                                    <img width=\"347\" height=\"294\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/insert-image-into-custom-header-or-footer/custom_6.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Click <strong>Save</strong> once you’ve made your color selection for an element.<strong> </strong>And then make sure to click <strong>Save </strong>in the upper right of the <strong>Colors &amp; Options</strong> page once you’re done!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> To help you mirror your website\'s color scheme on your event page, check out <a href=\"http://www.colorzilla.com/firefox/\">ColorZilla</a>, a handy Firefox add-on, which allows you to get the exact hexadecimal value for any color on any webpage.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <a href=\"http://www.eventbrite.com/event/2606983558\">here</a> to take a look at a sample event page that has an image in the custom header!</p>\r\n                                <p>\r\n                                    To learn about more advanced customization options that require knowledge of both HTML and CSS, check out this <a href=\"http://blog.eventbrite.com/how-to-design-a-great-event-page-its-better-than-using-azul\">blog post</a> by Anthony, one of our designers here at Eventbrite. Also, check out <a href=\"http://www.w3schools.com/\">W3 Schools</a>, a great resource for <a href=\"http://www.w3schools.com/html/default.asp\">HTML</a> instruction, as well as examples of <a href=\"http://www.w3schools.com/css/css_examples.asp\">CSS</a> that you can modify and use on your event page!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"stepcontent\">\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n\r\n\r\n                        <!--step-->', '1349230071', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('123', '8', 'Add PDF to event page', 'add-pdf-to-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Add PDF to event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Convert PDF to image file for easiest insertion</li>\r\n                                <li>\r\n                                    Use Scribd to embed PDF (advanced option)</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> These are a few ways you can convert your .pdf file to an image file for use in your event page. If someone designed your flier for you, you may also contact them to see if they can provide you with an image file (.jpeg, .jpg, .gif, or .png) instead. For more on inserting images into your event details, click <a href=\"/en_us/t/images-for-event-page\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Option 1 (the easiest): convert your PDF into an </strong><strong>image file</strong></p>\r\n                                <p>\r\n                                    There are several ways to convert a .pdf into an image file. One way is a free web-based service called <a href=\"http://imgur.com/\">Imgur </a>that will allow you to convert your .pdf to an image.</p>\r\n                                <p>\r\n                                    The first step is to upload your .pdf to Imgur. Once it\'s uploaded, right click on<strong> </strong>the preview image and click <strong>save as</strong> to save the image to your desktop. This will save your new .jpg file to your computer\'s hard drive. To give it a try, click <a href=\"http://imgur.com/\">here</a>.</p>\r\n                                <p>\r\n                                    Once you have your .jpg file, go <a href=\"/en_us/t/images-for-event-page\">here</a> to learn how to insert it into your event details.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Option 2: take a screenshot of the PDF and save as an image file</strong></p>\r\n                                <p>\r\n                                    The best way to take a screenshot will depend on your computer\'s operating system. Click <a href=\"http://take-a-screenshot.org/\">here</a> to learn how to take a screenshot.</p>\r\n                                <p>\r\n                                    Once you have your .jpg file, go <a href=\"/en_us/t/t/how-to-add-images-to-event-details\">here </a>to learn how to insert it into your event details.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong><strong>Option 3 (advanced): use Scribd to embed the PDF in your event details</strong> </strong></p>\r\n                                <p>\r\n                                    The final and slightly more involved option is a web service called Scribd. Scribd is a free web service where you can host .pdf files and that then will auto-generate the HTML code you need in order to embed .pdf files in your event page.</p>\r\n                                <p>\r\n                                    - To embed a .pdf using Scribd, go to <a target=\"_blank\" href=\"http://www.scribd.com/\">http://www.scribd.com</a>.</p>\r\n                                <p>\r\n                                    - Click the <strong>Upload</strong> button to choose your file to upload and then upload it to Scribd. You’ll be prompted to enter your email address to create an account on Scribd.</p>\r\n                                <p>\r\n                                    - On Scribd’s <strong>My Documents</strong> page, and click on the name of the document you wish to embed.</p>\r\n                                <p>\r\n                                    - Once your document opens, click <strong>Embed</strong> in the <strong>Share &amp;amp; Embed </strong>section. A dialog box will open. Click on the tab marked <strong>Flash</strong>. (Note: Clicking the<strong> Flash</strong> option is very important.)</p>\r\n                                <div>\r\n                                    <img width=\"500px\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/scribdshare.png\" alt=\"\">\r\n                                </div>\r\n                                <div>\r\n                                    <img width=\"500px\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/scribdflash.png\" alt=\"\">\r\n                                </div>\r\n                                <p>\r\n                                    - Click the <strong>Copy</strong> button to copy this embed code to your clipboard.</p>\r\n                                <p>\r\n                                    - Finally, go back to Eventbrite and bring up the <strong>Edit</strong> page for the event you are working on. In <strong>Step 3: Add Event Details</strong>, click on the <strong>HTML</strong> button in the toolbar. <img width=\"595\" height=\"500\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/htmlbutton.png\" alt=\"\"><br>\r\n                                    - Once the <strong>HTML Source Editor</strong> window opens, paste the embed code from your clipboard into the HTML code for your page. Now click <strong>Update</strong> at the bottom of the <strong>HTML Source Editor</strong>. Remember to save your changes before leaving the <strong>Edit </strong>page!<br><br><br>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Add PDF to event page</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        Convert PDF to image file for easiest insertion</li>\r\n                                    <li>\r\n                                        Use Scribd to embed PDF (advanced option)</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> These are a few ways you can convert your .pdf file to an image file for use in your event page. If someone designed a .pdf flier for you, you may also contact them to see if they can provide you with an image file (.jpeg, .jpg, .gif, or .png) instead. For more on inserting images into your event details, click <a href=\"/en_us/t/images-for-event-page\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Option 1 (the easiest): convert your PDF into an </strong><strong>image file</strong></p>\r\n                                <p>\r\n                                    There are several ways to convert a .pdf into an image file. One way is a free web-based service called <a href=\"http://imgur.com/\">Imgur </a>that will allow you to convert your .pdf to an image.</p>\r\n                                <p>\r\n                                    The first step is to upload your .pdf to Imgur. Once it\'s uploaded, right click on<strong> </strong>the preview image and click <strong>save as</strong> to save the image to your desktop. This will save your new .jpg file to your computer\'s hard drive. To give it a try, click <a href=\"http://imgur.com/\">here</a>.</p>\r\n                                <p>\r\n                                    Once you have your .jpg file, go <a href=\"/en_us/t/images-for-event-page\">here </a>to learn how to insert it into your event details.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Option 2: take a screenshot of the PDF and save as an image file</strong></p>\r\n                                <p>\r\n                                    The best way to take a screenshot will depend on your computer\'s operating system. Click <a href=\"http://take-a-screenshot.org/\">here</a> to learn how to take a screenshot.</p>\r\n                                <p>\r\n                                    Once you have your .jpg file, go <a href=\"t/how-to-add-images-to-event-details\">here </a>to learn how to insert it into your event details.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Option 3 (advanced): use Scribd to embed the PDF in your event details</strong></p>\r\n                                <p>\r\n                                    The final and slightly more involved option is a web service called Scribd. Scribd is a free web service where you can host .pdf files and that then will auto-generate the HTML code you need in order to embed .pdf files in your event page.</p>\r\n                                <p>\r\n                                    - To embed a .pdf using Scribd, go to <a target=\"_blank\" href=\"http://www.scribd.com/\">http://www.scribd.com</a>.</p>\r\n                                <p>\r\n                                    - Click the <strong>Upload</strong> button to choose your file to upload and then upload it to Scribd. You’ll be prompted to enter your email address to create an account on Scribd.</p>\r\n                                <p>\r\n                                    - On Scribd’s <strong>My Documents</strong> page, and click on the name of the document you wish to embed.</p>\r\n                                <p>\r\n                                    - Once your document opens, click <strong>Embed</strong> in the <strong>Share &amp;amp; Embed</strong> section. A dialog box will open. Click on the tab marked <strong>Flash</strong>. (Note: Clicking the <strong>Flash </strong>option is very important.)</p>\r\n                                <div>\r\n                                    <img width=\"500px\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/scribdshare.png\" alt=\"\">\r\n                                </div>\r\n                                <div>\r\n                                    <img width=\"500px\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/scribdflash.png\" alt=\"\">\r\n                                </div>\r\n                                <p>\r\n                                    - Click the <strong>Copy</strong> button to copy this embed code to your clipboard.</p>\r\n                                <p>\r\n                                    - Finally, go back to Eventbrite and bring up the <strong>Event Details</strong> page for the event you are working on. In <strong>Step 1: Add your Event Details</strong>, click on the <strong>HTML</strong> button in the toolbar above the <strong>Event Details</strong> section.</p>\r\n                                <div>\r\n                                    <img width=\"610\" height=\"262\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add-PDF-to-event-page/HTML.png\" alt=\"\">\r\n                                </div>\r\n                                <p>\r\n                                    - Once the <strong>HTML Source Editor</strong> window opens, paste the embed code from your clipboard into the HTML code for your page. Now click <strong>Update</strong> at the bottom of the <strong>HTML Source Editor</strong>. Remember to click <strong>Save</strong> to save your changes before leaving the <strong>Event Details</strong> page!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230075', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('124', '8', 'Language settings for event page', 'language-settings-for-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Language settings for event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Translated event page and registration experience</li>\r\n                                <li>\r\n                                    English (US, UK, Canada, Australia/New Zealand)</li>\r\n                                <li>\r\n                                    French (Canada, France); Spanish (Spain); Swedish</li>\r\n                                <li>\r\n                                    German, Dutch, Portuguese (Portugal), Italian</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>This option is different than <a href=\"/en_us/t/language-and-region-settings\">changing the region/language of your entire account</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To change the language for your event, from the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage</strong> page for that event. On the <strong>Manage</strong> page, select <strong>Event Type &amp; Language</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"229\" height=\"197\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-page-language/features22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select the language in which you would like to display your event page. See the note above for which parts of the \"attendee experience\" that will be translated to that language. Click <strong>Save Changes</strong> near the bottom of the page once you\'ve made your selection.</p>\r\n                                <p>\r\n                                    <img width=\"606\" height=\"339\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-page-language/select.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To see what your event page looks like, click <strong>View </strong>or <strong>Preview </strong>in the upper left part of the page. You will see that all of the hard-coded text on your event page is now translated to the language of your choice!</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"296\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/change-event-page-language/eventpage2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230082', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('125', '8', 'List attendees on event page', 'list-attendees-on-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>List attendees on event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    People can see who is already attending</li>\r\n                                <li>\r\n                                    Include answers to custom questions in attendee list</li>\r\n                                <li>\r\n                                    Good for conferences, seminars, meetings, etc.</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> This option is not currently available for <a href=\"/en_us/t/repeating-events\">repeating events</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Event Creation</strong> page, select <strong>\"Show attendee list on the registration page\"</strong> from the <strong>Additional Options</strong> section located at the bottom of the page.</p>\r\n                                <p>\r\n                                    <img width=\"584\" height=\"347\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/attendee_list_selected.png\" alt=\"How to show a list of attendees on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Use the check boxes to select <strong>the attendee information</strong> you\'d like to include on your event registration page. You can show the attendee\'s name along with their survey answers.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    A <strong>real time</strong> list of confirmed attendees will appear on your event registration page.</p>\r\n                                <p>\r\n                                    <img width=\"598\" height=\"191\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/attendee_list.png\" alt=\"How to show a list of attendees on the event page\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>Show the attendee list on your event page</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        People can see who is already attending</li>\r\n                                    <li>\r\n                                        Include answers to custom questions in attendee list</li>\r\n                                    <li>\r\n                                        Good for conferences, seminars, meetings, etc.</li>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> This option is not currently available for <a href=\"/en_us/t/repeating-events\">repeating events</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first create <a href=\"https://www.eventbrite.com/signup\">sign</a><a href=\"https://www.eventbrite.com/signup\">up</a> or <a href=\"https://www.eventbrite.com/login\">log</a><a href=\"https://www.eventbrite.com/login\">in</a> to Eventbrite and <a href=\"/en_us/t/event-page-creation\">create</a><a href=\"/en_us/t/event-page-creation\">an</a><a href=\"/en_us/t/event-page-creation\">event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 279px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/edit.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Next, click <strong>Colors &amp; Options</strong> in the upper left. Select <strong>Display Attendees</strong> and make sure to <strong>Save</strong> your changes.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 609px; height: 188px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/display.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once the <strong>Display attendees</strong> option is selected, a <strong>Display Attendee Information</strong> window will open. Here you can select all the information about each attendee that you’d like to display on your event page.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/en_US/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong>You can only display information you’ve chosen to collect from your attendees at registration. Click <a href=\"/en_us/t/collect-info-from-each-attendee\">here</a> to learn more about how to choose which information to collect from your attendees.</p>\r\n                                <p>\r\n                                    <img width=\"523\" height=\"258\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/list.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Click <strong>Save </strong>in the upper right to save your changes. A real-time list of confirmed attendees will now appear on your event page, below your event details!</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 608px; height: 802px; \" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/show_attendee_list/page.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230087', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('126', '8', 'Invitation-only events', 'invitation-only-events', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Invitation-only events</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    You must send invitations through Eventbrite</li>\r\n                                <li>\r\n                                    Recipients click through invitation to register</li>\r\n                                <li>\r\n                                    Good for VIP or members-only events</li>\r\n                                <br>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you select the invite-only feature, <em>only guests who receive an invitation created within Eventbrite will be able to register for your event</em>. The invite-only feature does <em>not </em>work in conjunction with invitations sent from MailChimp, Paperless Post or any other outside email client or service. If you would like to use the invite-only feature for your event, you will need to create and send your email invites directly from within Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To set your event to invite-only, from the <strong>My Events</strong> page, click on <strong>Edit </strong>next to the name of an event to go to the <strong>Edit </strong>page for that event. (If you are setting up a new event page--that is, you just pressed <strong>Create Event</strong> and the page you are on says <strong>Create an Event</strong>--you can skip this step and go directly to step 2 below.)<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"329\" border=\"0\" title=\"Send Email Invitations\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/my%20events.png\" alt=\"Send email invitations\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Scroll down to <strong>Step 8: Set Privacy</strong> and select the option \"This event is private and should not be listed in the directory or on search engines\"&nbsp; to classify your event as private. To activate the invite-only feature, then click the checkbox next to \"<label for=\"invites_only\">Event is invite-only and attendees MUST receive an Eventbrite invitation to register.</label>\" Click the <strong>Save Changes </strong>button at the bottom right.&nbsp;<br>\r\n                                    &nbsp;</p>\r\n                                <p><img width=\"607\" height=\"152\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly2.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div>\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you’ve selected the invite-only privacy setting, we place a large notification message at the top of the <strong>Event Summary</strong> on the <strong>Manage </strong>page for your event to highlight that invitations must be sent for attendees to register.</p>\r\n                                <p>\r\n                                    Click <strong>Create Invitations </strong> in the notification message or on the features menu on the left side of the page to format your <a href=\"/en_us/t/email-invitations\">email invitations</a> and select your guest list and then send your invitations.</p>\r\n                            </div>\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p><img width=\"614\" height=\"333\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly3.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    4</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        An invitation for an invite-only event will contain the wording \"Event registration is by invite only. Register with your email address to attend this event.\" When an attendee receives an invitation, they <em>must</em> click the <strong>Attend</strong> button in the invite in order to go to your event page and be able to register. Once on the event page, they are able to select any number of tickets and then register for your event.&nbsp;<br>\r\n                                        &nbsp;</p>\r\n                                    <p><img width=\"593\" height=\"998\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly5.png\" alt=\"\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note: </strong>If you\'d like to limit the number of tickets for which an attendee can register in an order, you can do that when setting up the <a href=\"/en_us/t/ticket-type-creation\">ticket type(s)</a> for your event.&nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    5</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        On the <strong>Registration</strong> <strong>Information</strong> page, where an attendee enters their information once they have selected a number of tickets, the email address to which you sent their invitation is hard-coded in and cannot be changed. This ensures that only those that you have invited can register for your event.<br>\r\n                                        &nbsp;</p>\r\n                                    <p>\r\n                                        <img width=\"582\" height=\"430\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly4.png\" alt=\"\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    &nbsp;</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        Once the invitation has been used to register for the event, it will not be able to be used&nbsp; to place another order. If the invitation is clicked on again, the invitee will see the message: \"We\'re sorry, this invitation has already been used.\"<br>\r\n                                        &nbsp;</p>\r\n                                    <p>\r\n                                        <img width=\"597\" height=\"363\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly7.png\" alt=\"\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note: </strong>The recipient can forward an unused email invitation to others to register for them, but the hard-coded email address will remain that of the original recipient of your invitation.</p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    6</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        With your event set to invite-only, if someone visits your event page without having received an email invite and/or clicking on the <strong>Attend</strong> link in the invite, the <strong>Ticket Information</strong> box will be hidden and they will not be able to register for your event. There is, however, a link to contact you, the host, with any questions about the event.</p>\r\n                                </div>\r\n                                <div class=\"stepnumber\">\r\n                                    <p><img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></p>\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note: </strong>You will want to disable any <a href=\"/en_us/t/sell-tickets-on-your-website\">promotional widgets</a> for your invite-only event that you\'ve inserted on your outside website or blog, as clicking on the widget will display the locked event page, as described above.<br>\r\n                                        &nbsp;</p>\r\n                                </div>\r\n                                <div class=\"stepnumber\">\r\n                                    &nbsp;</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p><img width=\"592\" height=\"391\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly6.png\" alt=\"How to resend the order confirmation email\"></p>\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <br><br>\r\n                                        &nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <div class=\"block-step\">\r\n                            <div class=\"block-heading\">\r\n                                <h3>How to set up an invitation-only event</h3>\r\n                                <ul>\r\n                                    <li>\r\n                                        You must send invitations through Eventbrite</li>\r\n                                    <li>\r\n                                        Recipients click through invitation to register</li>\r\n                                    <li>\r\n                                        Good for VIP or members-only events</li>\r\n                                    <br>\r\n                                </ul>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:&nbsp;</strong> If you select the Invitation Only feature, <em>only guests who receive an invitation created within Eventbrite will be able to register for your event</em>. The Invitation Only feature does <em>not </em>work in conjunction with invitations sent from MailChimp, Paperless Post or any other outside email client or service. If you would like to use the Invitation Only feature for your event, you will need to create and send your email invites directly from within Eventbrite.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, first <a href=\"https://www.eventbrite.com/signup\">sign up</a> for a free Eventbrite account or<a href=\"https://www.eventbrite.com/login\"> log in</a> to your existing Eventbrite account and<a href=\"/en_us/t/event-page-creation\"> create an event</a>.</p>\r\n                                <p>\r\n                                    If you’d like to edit an already-created event, click <strong>My Events</strong> at the top of the screen and then click on <strong>Edit </strong>to the right of your event’s name.<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"608\" height=\"200\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/edit1.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Scroll down to <strong>Step 3: Promote your Event Page</strong> (or <strong>Privacy Settings</strong>)<strong> </strong>and select the <strong>Private</strong> option&nbsp;from the <strong>Publicize, or keep it private</strong> dropdown menu, if you haven’t already. To activate the <strong>Invitation Only</strong> feature, click the checkbox next to <strong>Invitation Only</strong>. Then click <strong>Save </strong>or <strong>Make Event Live </strong>button in the upper right.<br>\r\n                                    &nbsp;</p>\r\n                                <p><img width=\"614\" height=\"223\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/invite-only.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div>\r\n                                &nbsp;</div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you’ve selected the <strong>Invitation Only</strong> setting and made your event live, we place a large notification message at the top of the <strong>Event Summary</strong> on the <strong>Manage </strong>page for your event to highlight that invitations must be sent for attendees to register.</p>\r\n                                <p>\r\n                                    Click <strong>Create Invitations </strong>in the notification message or on the features menu on the left side of the page once your event is live to format your <a href=\"/en_us/t/email-invitations\">email invitations</a>, select your guest list, and then send your invitations.<br>\r\n                                    &nbsp;</p>\r\n                                <p><img width=\"610\" height=\"352\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/button.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    An invitation for an <strong>Invitation Only </strong>event will contain the wording \"Event registration is by invite only. Register with your email address to attend this event.\" When an attendee receives an invitation, they <em>must</em> click the <strong>Attend</strong>&mdash;or <strong>Yes</strong>, for an invitation with the RSVP option enabled&mdash;button in the invite in order to go to your event page and be able to register. Once on the event page, they are able to select the number of tickets that they’d like and then register for your event.<br>\r\n                                    &nbsp;</p>\r\n                                <p><img width=\"593\" height=\"998\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly5.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you\'d like to limit the number of tickets for which an attendee can register in an order, you can do that when setting up the <a href=\"/en_us/t/ticket-type-creation\">ticket type(s)</a> for your event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the <strong>Registration</strong> <strong>Information</strong> page, where an attendee enters their information once they have selected a number of tickets, the email address to which you sent their invitation is hard-coded in and cannot be changed. This ensures that only those that you have invited can register for your event.<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"582\" height=\"430\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly4.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Once the invitation has been used to register for the event, it will not be able to be used&nbsp;to place another order. If the invitation is clicked on again, the invitee will see the message: \"We\'re sorry, this invitation has already been used.\"<br>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"597\" height=\"363\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly7.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> The recipient can forward an unused email invitation to others to register for them, but the hard-coded email address will remain that of the original recipient of your invitation.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                6</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    With your event set to <strong>Invitation Only</strong>, if someone visits your event page without having received an email invite and/or clicking on the <strong>Attend</strong> or <strong>Yes </strong>button in the invite, the <strong>Ticket Information</strong> box will be hidden and they will not be able to register for your event. There is, however, a link to contact you, the host, with any questions about the event.<br>\r\n                                    &nbsp;</p>\r\n                                <p><img width=\"592\" height=\"391\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/invite-only-feature/inviteonly6.png\" alt=\"How to resend the order confirmation email\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>You will want to disable any <a href=\"/en_us/t/sell-tickets-on-your-website\">promotional widgets</a> for your invite-only event that you\'ve inserted on your outside website or blog, as clicking on the widget will display the locked event page, as described above.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230094', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('127', '8', 'Time zone settings', 'time-zone-settings', 'Time zone settings', '1349230099', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('128', '8', 'More info about our new design', 'more-info-about-our-new-design', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>More info about our new design</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Learn how to switch</li>\r\n                                <li>\r\n                                    Frequently asked questions</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> You can learn more about how to use the different sections of our new design by going <a href=\"/en_us/t/event-page-creation\">here</a>, then clicking on <strong>Jump to new help </strong>at the top of the page.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: medium;\"><strong>How to switch to our new design</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <br>\r\n                                <p>\r\n                                    When creating or editing your event page, you\'ll see a message at the top of the page prompting you to try out our new design. Click <strong>Give it a try</strong> to make the switch. All of your existing information will be carried over to our new design. Don\'t worry--you can always switch back, if you\'d like, without losing any of your information!</p>\r\n                                <p>\r\n                                    <img width=\"516\" height=\"242\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/more-info-about-our-new-create-flow/opt%20in.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    To learn more about creating or editing an event page using our new design, go <a href=\"/en_us/t/event-page-creation\">here</a>, then click on <strong>Jump to new help </strong>at the top of the page.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: medium;\"><strong>How to switch back to our old design</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <br>\r\n                                <p>\r\n                                    Once using our new design, you can return to our old design at any time when creating or editing your event page. Make sure to click <strong>Save</strong> to save your changes, then scroll down to the bottom of the page and click on <strong>Switch back</strong>. All information that you\'ve been working on will be carried over to our old design!</p>\r\n                                <p>\r\n                                    <img width=\"612\" height=\"248\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/more-info-about-our-new-create-flow/switchback2.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p style=\"text-align: center;\">\r\n                            <span style=\"font-size: medium;\"><strong><strong>Have questions? Here are some handy FAQs about our new design.</strong> </strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <br>\r\n                                <p>\r\n                                    <strong>+ Why did Eventbrite build a new Create page?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    The old version of the Create page is a few years old, and we’ve received lots of great feedback from our event organizers about how we can improve the experience and functionality of this page. The new Create page is the result of up-front design research and usability sessions conducted with many of our users, and we’ve incorporated your feedback into our product. The new design repackages all the key functionality from the old page and offers several new features in a very user-friendly manner, which will simplify the event-creation process.</p>\r\n                                <p>\r\n                                    <strong>+ I created an event using the old design a few weeks ago. Why am I seeing the new design when I go to edit my event? Did all of my settings get transferred over?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    When you switch to our new design, it changes the setting on all events you create (and have created) in your account. You can switch back to the old design at any point--see above to learn how. All of the data from your event settings have been transferred from the old design to the new design.</p>\r\n                                <p>\r\n                                    <strong>+ Did you make any changes to Eventbrite\'s fees or pricing?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    Nope! There were no changes to our pricing or fees. This project was focused solely on taking customer feedback to improve the experience of creating and editing your events.</p>\r\n                                <p>\r\n                                    <strong>+ Where do I set my payment options settings in the new design? I used to enter this info in Step 2: Add Ticket Information in the old design.</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    The <strong>Payment Options</strong> section has moved to the features menu on the left side of your event\'s <strong>Manage</strong> page. You will be able to set your payment options once you\'ve made your event live. If you are a returning user, we will carry over your previous event settings for payment options.</p>\r\n                                <p>\r\n                                    <strong>+ What are the main changes in the new design?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    - <span style=\"text-decoration: underline;\">Payment options</span>: This section has moved to the features menu on the left side of your event\'s <strong>Manage</strong> page. You will be able to set your payment options once you have made your event live. If you are a returning user, we will carry over your previous event settings for payment options.</p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    - <span style=\"text-decoration: underline;\">Google Maps</span>: We\'ve added a new plug-in to find your location. Simply enter the name of your venue (optional) and your address and select the address you\'re looking for from the dropdown menu. If Google can’t find your location, you can always manually enter it by clicking on the <strong>Can’t find your location?</strong> link.</p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    - <span style=\"text-decoration: underline;\">Displaying attendees on your event page</span>: You can find this option on the <strong>Colors &amp; Options</strong> page.</p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    - <span style=\"text-decoration: underline;\">Global service fee</span>: This option allows you to easily handle service fees in the same manner for all ticket types in your event. If you’d like to offer a different fee structure on a per-ticket-type basis, click on the <strong>Settings</strong> link for each ticket type.</p>\r\n                                <p>\r\n                                    <strong>+ Where are my previously saved locations?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    Your previously saved locations are available by clicking on <strong>Can\'t find your location?</strong> in <strong>Step 1: Add your Event Details</strong>, then clicking on the dropdown menu.</p>\r\n                                <p>\r\n                                    <strong>+ How can I offer feedback about the new design?</strong></p>\r\n                                <p style=\"padding-left: 30px;\">\r\n                                    We\'d love to get your feedback! Feel free to <a href=\"http://www.eventbrite.com/contact-us\">contact us</a> with any questions or comments about our new design.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230120', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('129', '8', 'Post news & updates on event page', 'post-news---updates-on-event-page', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Post news & updates on event page</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Keep your attendees in the loop with news about the event</li>\r\n                                <li>\r\n                                    Heighten anticipation for your event with strategically timed updates</li>\r\n                            </ul>\r\n                        </div>\r\n\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div>\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the News and Updates feature, go to your event\'s <b>Manage</b> page by clicking on <b>My Events</b> at the top of the screen and then clicking on the title of your event. From the <b>Manage</b> page, select <b>Add News and Updates </b>from the features menu on the left side of the <b>Manage </b>page.&nbsp;</p>\r\n                                <p>\r\n                                    <img border=\"0\" title=\"Add News &amp;amp; Updates\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/news_updates/news22.png\" alt=\"Add news and updates\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Create your newest entry in the text box. Feel free to use the rich-text editor to change the colors, font, add images, etc. (If you\'re adding images, keep in mind that the <b>News &amp;amp; Updates</b> box on your event page is 298 pixels wide.)</p>\r\n                                <p>\r\n                                    <img width=\"614\" height=\"321\" border=\"0\" title=\"Add News &amp;amp; Updates\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/news_updates/news2.png\" alt=\"Add News &amp;amp; Updates\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Any news or updates that you add will appear on the right side of your event page in a dedicated section. Visitors to your event page can click on the orange RSS icon to subscribe to an <a href=\"http://en.wikipedia.org/wiki/RSS\">RSS feed</a> of any news or updates that you add for this event.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"280\" height=\"340\" border=\"0\" title=\"Add News &amp;amp; Updates\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/news_updates/news_on_event_page.png\" alt=\"Add News &amp;amp; Updates\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230144', null, '0', '0');
