INSERT INTO `etk_faqs` VALUES ('138', '13', 'Refund an order', 'refund-an-order', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Refund an order</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    No fees charged on fully refunded orders</li>\r\n                                <li>\r\n                                    Fees for partially refunded orders calculated based on new total</li>\r\n                                <li>\r\n                                    Email notification sent to refunded attendees</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>All Eventbrite fees are refunded for orders for which you issue a complete--i.e., 100%--refund (though if you\'re using PayPal or Google Checkout for payment processing, the PayPal or Google Checkout processing fee may still apply).</p>\r\n                                <p>\r\n                                    Refunds can take up to 3-5 business days after you issue the refund to appear in your attendee\'s bank account, depending on their bank.</p>\r\n                                <p>\r\n                                    If you\'re using our standard <a href=\"/en_us/t/credit-card-processing-faq\">credit card processing</a> and have already received your payout for the event, you\'ll need to process any refunds for that event offline--by sending the attendee a check, for instance.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: medium;\"><strong>Here\'s a quick tutorial video (or find written instructions below):</strong></span></p>\r\n                        <p class=\"center\">\r\n                            <object width=\"555\" height=\"480\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/uyXWrpmiAfw?version=3&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\">\r\n                                <param value=\"always\" name=\"allowscriptaccess\">\r\n                                <param value=\"http://www.youtube.com/v/uyXWrpmiAfw?version=3&amp;hl=en_US\" name=\"src\">\r\n                                <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: medium;\"><strong>How to issue a refund for an attendee or order in your account:</strong></span></p>\r\n\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Event Reports</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"231\" height=\"199\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/event%20reports22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Set the <strong>Report Type </strong>dropdown menu to <strong>Order Details. </strong>Find the order you would like to refund in the <strong>Search for Orders</strong> field by order number, name or email address.</p>\r\n                                <p>\r\n                                    <strong><img width=\"611\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund1.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Refund this Order</strong> from the top <strong>Quick Actions</strong> menu located on the right side of the gray order box.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"258\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note:</strong> If you are using <strong>PayPal</strong> or <strong>Google Checkout</strong> for payment processing, you\'ll be redirected to that site to complete the refund (and you can skip to step 5 below). For more information on processing refunds through PayPal, click <a title=\"here\" href=\"https://www.paypal.com/cgi-bin/webscr?cmd=xpt/Marketing/bizui/SendRefund-outside\">here</a>. For more information on processing refunds through Google Checkout, click <a title=\"here\" href=\"http://checkout.google.com/support/sell/bin/answer.py?answer=134379\">here</a>. Any refunds that you initiate in your PayPal or Google Checkout account will automatically update the corresponding order in your Eventbrite account!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4a</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To issue a complete refund on an order:</strong></p>\r\n                                <img width=\"611\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund3.png\" alt=\"\"><p>\r\n                                    Find the attendee you would like to refund and select the <strong>Refund Quantity</strong> from the dropdown menu. The refund amount should automatically fill with the <strong>Total Refund</strong> amount. Select <strong>Continue</strong> to go to the next screen, where you can review the refund before it is processed.</p>\r\n                                <p>\r\n                                    On the next screen, you can leave a message in the <strong>Note to Buyer</strong> text field. This message will appear in the email that the attendee receives when the order is refunded.</p>\r\n                                <img width=\"611\" height=\"372\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund4.png\" alt=\"\"><p>\r\n                                    If you need to make any changes, click the <strong>Cancel</strong> button and restart the process. If you are set to refund, go ahead and click the <strong>Issue Refund </strong>button!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4b</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To issue a partial refund on an order:</strong></p>\r\n                                <img width=\"611\" height=\"440\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund5.png\" alt=\"\"><p>\r\n                                    Select a <strong>Refund Quantity </strong>of 1. In the <strong>Refund Amount</strong> field, enter the amount you would like to partially refund. The <strong>Total Refund</strong> amount will adjust to the amount entered above.</p>\r\n                                <p>\r\n                                    Click <strong>Continue</strong> to issue the partial refund.</p>\r\n                                <p>\r\n                                    Like a full refund, you can review the refund before it is processed. To finalize the refund click <strong>Issue Refund</strong>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you have completed the refund, the <strong>Order Details</strong> report and <strong>Attendee Summary</strong> for your event will be automatically updated. Your refunded attendees/orders will be viewable in the <strong>Refunded/Canceled</strong> orders list on the <strong>Order Details</strong> report.</p>\r\n                                <p>\r\n                                    A full refund will appear as follows:</p>\r\n                                <img width=\"619\" height=\"447\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/full_refund_order_report.png\" alt=\"\"><p>\r\n                                    A partial refund will appear as follows:</p>\r\n                                <img width=\"611\" height=\"489\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/howtorefund6.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Don\'t see the option to refund an order in the top <strong>Quick Actions</strong> dropdown menu?</p>\r\n                                <p>\r\n                                    If the order was placed more than six months ago, you\'ll need to send your attendee an offline refund (e.g., a check), as our payment gateway does not store credit card info for more than six months.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img border=\"0\" src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>By default, the attendee will be able to click on a <a href=\"/en_us/t/can-i-get-a-refund\">Request Refund</a> link when they log in to their Eventbrite account to manage their order for your event. You will receive an email requesting the refund. You can disable this feature by clicking on&nbsp;<strong>Customize Order Form</strong> found on the left side of your event\'s <strong>Manage</strong> page.</p>\r\n                                <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/create_survey/customizeorderold.png\" alt=\"\"><p>\r\n                                    Scroll down to the <strong>Optional Settings</strong> section and uncheck the box next to <strong>Return Policy</strong> and click <strong>Save Changes</strong>.</p>\r\n                                <img width=\"607\" height=\"471\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/refund_attendee/refundpolicy2.png\" alt=\"\"><p>\r\n                                    You\'ve done it!&nbsp; Your attendees will not see the <strong>Request Refund</strong> link when they log in to their account. We do recommend placing a refund policy on your event page so that your attendees are aware of your policy.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230569', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('139', '13', 'Attendee summary report', 'attendee-summary-report', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Attendee summary</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Names and email addresses for everyone attending</li>\r\n                                <li>\r\n                                    All the information you collect from your attendees</li>\r\n                                <li>\r\n                                    Easily export all information to Excel</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> If you\'re a Salesforce user and want to import your Eventbrite attendees into Salesforce, take a look at this tutorial to learn how to run an Attendee Summary report and then export the report to Excel as a .csv file. Then you\'ll be able to import the .csv file into Salesforce!</p>\r\n                            </div>\r\n                        </div>\r\n                        <p>\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <p><object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/syxZ8W9unMA?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/syxZ8W9unMA?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <p>\r\n                            <strong>How to view and/or export to Excel the Attendee Summary report:</strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use the <strong>Attendee Summary</strong> to view your attendees\' information and all their answers to your <a href=\"/en_us/t/collect-information-from-attendees\">custom survey</a> and questions.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To access the <strong>Attendee Summary</strong> from the <strong>My Events</strong> page, click on the name of an event&nbsp;or&nbsp;<strong>Manage</strong>&nbsp;under&nbsp;<strong>Quick Links</strong>&nbsp;to go to the&nbsp;<strong>Manage&nbsp;</strong>page for that event. On the <strong>Manage</strong> page, select <strong>Event Reports</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/eventreporto.png\" alt=\"Screenshot\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Attendee Summary </strong>from the <strong>Report Type</strong> dropdown menu, if it isn\'t already selected. To sort, filter and save settings for your report, select the <strong>More Options</strong> link located to the right of the <strong>Report Type</strong> dropdown menu.</p>\r\n                                <p>\r\n                                    <img style=\"width: 573px; height: 209px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/options.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can now view the <strong>Attendee Summary</strong>, which includes the answers to your custom questions that you set up before publishing your event using the <a href=\"/en_us/t/collect-information-from-attendees\"><strong><strong>Customize Order Form</strong></strong></a> feature.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 525px; height: 368px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/summary.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You can edit an attendee\'s information or order by using the <strong>Quick Actions</strong> dropdown menu, located on the far right of the row containing the attendee\'s name. (You may need to scroll to the right to see this menu.)</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 235px; height: 189px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/quick actions.png\" alt=\"Screenshot\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To export the <strong>Attendee Summary</strong> to Excel or as a .csv or text file, click the, click the&nbsp;<strong>File Type&nbsp;</strong>button to the right of&nbsp;<strong>Export</strong>.</p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 358px; height: 57px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/export.png\" alt=\"Screenshot\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\"></div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To view payment information and fees for all orders in your event--as well as issue any <a href=\"/en_us/t/refund-an-order\">refunds</a>--consult the <a href=\"/en_us/t/order-details-report\"><strong>Order Details</strong></a> report.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230580', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('140', '13', 'Order Details report', 'order-details-report', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Order Details report</h3>\r\n                            <ul>\r\n\r\n\r\n                                <li>\r\n                                    Revenue and fee information for each order</li>\r\n                                <li>\r\n                                    Export to Excel to view info for all orders, including billing address</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <p class=\"center\">\r\n                            <strong>Here\'s a handy tutorial video. You can also find written instructions below the video.</strong></p>\r\n                        <br/><br/>\r\n\r\n\r\n                        <p><object width=\"555\" height=\"385\" type=\"application/x-shockwave-flash\" data=\"http://www.youtube.com/v/syxZ8W9unMA?fs=1&amp;hl=en_US\"><param value=\"true\" name=\"allowFullScreen\"> <param value=\"always\" name=\"allowscriptaccess\"> <param value=\"http://www.youtube.com/v/syxZ8W9unMA?fs=1&amp;hl=en_US\" name=\"src\"> <param value=\"true\" name=\"allowfullscreen\"></object></p>\r\n                        <br/><br/><br/>\r\n                        <p class=\"center\">\r\n                            <strong>Here\'s how to view and/or export to Excel the Order Details report</strong></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Use the Order Details report to view payment information and fees for all orders, as well as edit ticket orders for your event. You also issue any <a href=\"/en_us/t/refund-an-order\">refunds</a> from within the Order Details report.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the title of your event&nbsp;or&nbsp;<strong>Manage</strong>&nbsp;under&nbsp;<strong>Quick Links</strong>&nbsp;to go to the&nbsp;<strong>Manage&nbsp;</strong>page for that event. On the <strong>Manage </strong>page, select <strong>Event Reports</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    Using the <strong>Report Type</strong> drop-down menu located at the top of the page, choose <strong>Order Details</strong>.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/order_report/vieworderreport1.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    <img style=\"border-width: 0px; border-style: solid; width: 500px; height: 179px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/order_report/vieworderreport2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    The Order Details report includes a list of all ticket orders for your event. It also includes information about the fees and attendees for each order. You can sort, filter, and export the list of orders.</p>\r\n                                <img style=\"border-width: 0px; border-style: solid; width: 600px; height: 538px; \" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/order_report/vieworderreport32.png\" alt=\"\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p style=\"text-align: left;\">\r\n                                    Each order and each attendee can be edited by using the <strong>Quick Actions</strong> menu located within the order report. The <strong>Quick Actions</strong> menu also gives you the option to refund your attendees. Click <a href=\"/en_us/t/refund-an-order\">here</a> to learn more about how to make a refund.</p>\r\n                            </div>\r\n                        </div>\r\n\r\n                        <!--step-->', '1349230585', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('141', '13', 'Resend order confirmation email', 'resend-order-confirmation-email', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Resend order confirmation email</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Copy of receipt for an attendee</li>\r\n                                <li>\r\n                                    Send confirmation to a different email address</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you\'re an attendee who has misplaced the original order confirmation email and/or your PDF tickets, or are wishing for a receipt, you\'ll need to contact the organizer of the event. Learn how to do that <a href=\"/en_us/t/i-have-a-question-about-an-event\">here</a>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    For organizers wanting to resend an order confirmation email, first go to the <strong>My Events</strong> page. Then click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Event Reports</strong> from the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img width=\"231\" height=\"199\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/resend_conf_email/event%20reports22.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Filter the <strong>Report Type </strong>dropdown menu to <strong>Order Details </strong>and locate the order in the report.&nbsp;</p>\r\n                                <p>\r\n                                    <strong><img width=\"481\" height=\"107\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/resend_conf_email/order%20report%20selected.png\" alt=\"\"></strong></p>\r\n                                <p>\r\n                                    From the <strong>Quick Actions</strong> dropdown menu located on the right side of the order header, select <strong>Resend Confirmation Email.</strong></p>\r\n                                <p>\r\n                                    <img width=\"511\" height=\"116\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/resend_conf_email/quick%20actions%20menu.png.png\" alt=\"How to resend the order confirmation email\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next page, verify the recipient\'s email address. (You can also enter in a different address to which to resend the order confirmation email.) Click <strong>Send</strong>.</p>\r\n                            </div>\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you do enter in a different email address to which to send the order confirmation email, doing so will <em>not</em> permanently change the email address associated with that order.</p>\r\n                                <p>\r\n                                    <img width=\"432\" height=\"131\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/resend_conf_email/email%20to.png\" alt=\"How to resend the order confirmation email\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230589', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('142', '13', 'Add attendees manually', 'add-attendees-manually', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Add attendees manually</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Good for complimentary tickets or for people who have already paid</li>\r\n                                <li>\r\n                                    No fees charged on manually added orders</li>\r\n                                <li>\r\n                                    Sending an order confirmation email is optional</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> We unfortunately don\'t support a bulk-upload feature to add multiple attendees at this time. If you\'d like to add multiple attendees, you can add them one by one using the steps below.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <b>My Events</b> page, click on the title of your event to go to the <b>Manage</b> page for that event. On the <b>Manage</b> page, select <b>Add A</b><b>ttendees</b> from the features menu on the left side of the&nbsp;page.</p>\r\n                                <img border=\"0\" title=\"Manually add attendees to your event.\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add_attendee/manage_bar.png\" alt=\"Manually add attendees to your event.\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Enter the <b>Q</b><b>uantity </b>of tickets for the attendee. The <b>Amount Paid</b> and <b>Total Paid</b> fields will automatically update with the ticket price x quantity, but these fields can also be edited if you need to make special adjustments.</p>\r\n                                <p>\r\n                                    Then enter the <b>P</b><b>ayment Type</b>&nbsp;and any notes you\'d like to associate with that order. Select <b>Continue</b>.</p>\r\n                                <p>\r\n                                    <img width=\"602\" height=\"139\" border=\"0\" title=\"Manually add attendees to your event.\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add_attendee/add_attendees_box.png\" alt=\"Manually add attendees to your event.\"></p>\r\n                                <p>\r\n                                    <img width=\"538\" height=\"146\" border=\"0\" title=\"Manually add attendees to your event.\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add_attendee/payment_method.png\" alt=\"Manually add attendees to your event.\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    On the next page, fill out the registration information for the attendee(s). On this page, there is a checkbox where you can opt to send an <a href=\"/en_us/t/order-confirmation-customization\">order confirmation email</a> to the attendee.</p>\r\n                                <p>\r\n                                    Select<b>&nbsp;Complete Registration</b>&nbsp;and you will be sent to the <a href=\"/en_us/t/order-details-report\">Order Details report</a>, where you can view the details of this order.</p>\r\n                                <img width=\"599\" height=\"579\" border=\"0\" title=\"Manually add attendees to your event.\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add_attendee/add1.JPG\" alt=\"Manually add attendees to your event.\">\r\n                                <p>\r\n                                    Here\'s a look at an example record from an <a href=\"/en_us/t/order-details-report\">Order Details report</a>.</p>\r\n                                <img width=\"599\" height=\"486\" border=\"0\" title=\"Manually add attendees to your event.\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/add_attendee/add2.JPG\" alt=\"Manually add attendees to your event.\"></div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <b>Tip:</b> Manually adding yourself as an attendee of your event is a great way to see what your event\'s registration flow will look like for your attendees! Once you\'ve added yourself, you can easily <a href=\"/en_us/t/cancel-or-delete-a-free-order\">delete</a> or <a href=\"/en_us/t/refund-an-order\">refund</a> your order to remove yourself from the guest list.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230594', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('143', '13', 'Cancel or delete a free order', 'cancel-or-delete-a-free-order', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Cancel or delete a free order</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Free up a spot at your event</li>\r\n                                <li>\r\n                                    Canceling an order will send email notification to attendee</li>\r\n                                <li>\r\n                                    Deleting an order will not send an email notification to attendee</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>If you registered for an event and would like to cancel your registration, go <a href=\"/en_us/t/can-i-get-a-refund\">here</a> for more information. If you are an event organizer and would like to issue a complete or partial refund for a <em>paid </em>order, go <a href=\"/en_us/t/refund-an-order\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>My Events</strong> page, click on the title of your event to go to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Event Reports</strong> from the features menu on the left side of the page.</p>\r\n                                <img width=\"216\" height=\"163\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/event_reports/eventreporto.png\" alt=\"\">\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Set the <strong>Report Type </strong>dropdown menu to <strong>Order Details. </strong>Find the order you would like to delete in the <strong>Search for Orders</strong>&nbsp;field by order number, name or email address.</p>\r\n                                <p>\r\n                                    <strong><img width=\"605\" height=\"188\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete-a-free-order-or-attendee/order_details.png\" alt=\"\"></strong></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To delete or cancel a completed order:</strong></p>\r\n                                <p>\r\n                                    Select <strong>Delete this order</strong> or <strong>Cancel this order</strong> from the top <strong>Quick Actions</strong> menu located on the right side of the gray order box. When asked to confirm the action, click <strong>Yes</strong>.</p>\r\n                                <p>\r\n                                    The only difference between deleting and canceling an order is that an email notification will be sent to the attendee(s) if you choose to cancel the order; no email will be sent if you choose to delete the order.</p>\r\n                                <p>\r\n                                    <img width=\"609\" height=\"200\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete-a-free-order-or-attendee/delete%20order.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>Once an order has been deleted or canceled, the name(s) of the attendee(s) in that order won\'t show up in the attendee list. To view a deleted or canceled order, select <strong>Refunded and Canceled Orders </strong>from the <strong>Order Type</strong> dropdown menu when viewing an <strong>Order Details </strong>report.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>To delete or cancel just one attendee in an order that includes other attendees:</strong></p>\r\n                                <p>\r\n                                    Find the order in which the attendee appears, per step 2 above.</p>\r\n                                <p>\r\n                                    Select <strong>Delete this Attendee </strong>or<strong> Cancel this Attendee </strong>from the <strong>Quick Actions</strong> menu located to the right of that particular attendee\'s name. When asked to confirm the action, click <strong>Yes</strong>.</p>\r\n                                <p>\r\n                                    Unlike deleting or canceling an entire order--as detailed in step 3 above--there is no difference between deleting or canceling an individual attendee: the attendee will <em>not</em> receive a notification email.&nbsp;</p>\r\n                                <p>\r\n                                    <img width=\"607\" height=\"204\" border=\"0\" src=\"https://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/delete-a-free-order-or-attendee/delete%20attendee.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>The deleted or canceled attendee\'s name will no longer appear in the order or in the attendee list for the event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230599', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('144', '13', 'Transfer attendee to another event', 'transfer-attendee-to-another-event', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Transfer attendee to another event</h3>\r\n                            <ul>\r\n                                <li>\r\n                                    Instructions below for events that are using PayPal or Google Checkout</li>\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>To transfer an attendee from an event that uses <a href=\"/en_us/t/credit-card-processing-faq\">credit card processing</a>, you will instead need to <a href=\"/en_us/t/refund-an-order\">refund</a> the order and request that the attendee buy a ticket for the other event.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    You\'ll need to first mark the original order as canceled by following the instructions below. (This will <strong>not</strong> refund the order in your PayPal or Google Checkout account.)</p>\r\n                                <p>\r\n                                    To get started, go to the <strong>Manage</strong> page of the event for which the original order was placed. You can get there by clicking on <strong>My Events</strong> at the top of the page, and then clicking on the name of your event.</p>\r\n                                <p>\r\n                                    Once on the <strong>Manage </strong>page, select <strong>Event Reports</strong> on the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/transfer-attendee-from-one-event-to-another/features.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    From the <strong>Report Type </strong>dropdown menu, select <strong>Order Details</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"621\" height=\"188\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/transfer-attendee-from-one-event-to-another/order%20details.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Find the order in question by searching for the attendee\'s name, email address, or order number.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                3</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Select <strong>Edit Tickets/Amount</strong> from the bottom <strong>Quick Actions</strong> dropdown menu, next to the name of the attendee.</p>\r\n                                <p>\r\n                                    <img width=\"620\" height=\"461\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/transfer-attendee-from-one-event-to-another/edit_tickets_amount.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                4</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Reduce the <strong>Quantity</strong> of the ticket type(s) that you\'d like to transfer by the number of that ticket type that you want to transfer to the other event. You\'ll see the amount in the <strong>Paid</strong> column(s) reduce by the corresponding amount(s).</p>\r\n                                <p>\r\n                                    Select <strong>Yes, update the order total to reflect my changes</strong>, include any notes you\'d like for internal purposes, and click <strong>Save Changes</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"619\" height=\"543\" border=\"0\" src=\"http://eventbrite-s3.s3.amazonaws.com/static/images/tutorials/transfer-attendee-from-one-event-to-another/quantity.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                            </div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Note: </strong>This will <strong>not</strong> affect the transaction in your PayPal or Google Checkout account. In order to refund the order, you would need to log in to your PayPal or Google Checkout account and manually process the refund there.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                5</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    After you\'ve marked the original order as canceled by following the instructions above, now you\'ll need to add the attendee to your other event. Learn how to manually add an attendee to an event by going <a href=\"/en_us/t/add-attendees-manually\">here</a>.&nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230603', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('145', '13', 'Export attendee data', 'export-attendee-data', 'Export attendee data', '1349230608', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('146', '13', 'Multiple-event reports', 'multiple-event-reports', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Multiple-event reports</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Sales and fee details for multiple events</li>\r\n                                <li>\r\n                                    Attendee information for multiple events</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To get started, from the <strong>My Events</strong> page, click on the title of a live or completed event. This will take you to the <strong>Manage </strong>page for that event. On the <strong>Manage </strong>page, select <strong>Cross Event Reports</strong> from the <strong>Analyze</strong> section of the features menu on the left side of the page.</p>\r\n                                <p>\r\n                                    <img src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cross_event_reporting/crossreport.png\" alt=\"\"></p>\r\n                                <p>\r\n                                    Using the <strong>Report Type</strong> dropdown menu located at the top of the page, choose the type of report you would like to view, such as <strong>Order Details</strong> or <strong>Attendee summary</strong>.</p>\r\n                                <p>\r\n                                    <img width=\"593\" height=\"257\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cross_event_reporting/crossevent2.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> The quickest way to get to the cross event reports feature is to click on <strong>My Events</strong> at the top of the page. Then, click on the <strong>Cross Event Reports</strong>&nbsp;link found on the right side of the page below the <strong>Account</strong> and <strong>Help</strong> tabs.</p>\r\n                                <p>\r\n                                    <img width=\"595\" height=\"87\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cross_event_reporting/crossevent5.png\" alt=\"\"></p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    Once you\'ve chosen the type of report you\'d like to view, you\'ll be able to select which events you\'d like the report to include.</p>\r\n                                <p>\r\n                                    Click on the <strong>Select All</strong> link to choose all of your events, or simply click on the name of the individual events for which you\'d like information. The report will include any events that you highlight in yellow.</p>\r\n                                <p>\r\n                                    <img width=\"611\" height=\"352\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cross_event_reporting/crossevent3.png\" alt=\"\"></p>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    3</div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        You can export the report to an Excel spreadsheet by clicking the green <strong>File Type</strong> button next to the <strong>Export</strong> heading.</p>\r\n                                    <p>\r\n                                        <img width=\"595\" height=\"138\" border=\"0\" src=\"http://evbdn.eventbrite.com/s3-s3/static/images/en_US/tutorials/cross_event_reporting/crossevent4.png\" alt=\"\"></p>\r\n                                </div>\r\n                            </div>\r\n                            <div class=\"step\">\r\n                                <div class=\"stepnumber\">\r\n                                    <img src=\"http://ebmedia.eventbrite.com/s3-s3/static/images/helpcenter/article_note.png\" alt=\"Note\">\r\n                                </div>\r\n                                <div class=\"stepcontent\">\r\n                                    <p>\r\n                                        <strong>Note: </strong>To learn more about viewing and exporting an <strong>Attendee Summary</strong> report for a single event, click <a href=\"/en_us/t/attendance-summary\">here</a>. For more information on viewing and exporting an <strong>Order Details</strong> report, click <a href=\"/en_us/t/order-details-report\">here</a>.&nbsp;</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230613', null, '0', '0');
INSERT INTO `etk_faqs` VALUES ('147', '13', 'Salesforce integration', 'salesforce-integration', '<!--CONTENT-->\r\n                        <div class=\"block-heading\">\r\n                            <h3>Salesforce integration</h3>\r\n                            <ul>\r\n\r\n                                <li>\r\n                                    Import attendees into Leads or Contacts in Salesforce</li>\r\n                                <li>\r\n                                    Read info about Eventbrite events and attendees in Salesforce</li>\r\n\r\n                            </ul>\r\n                        </div>\r\n                        <!--\r\n                        <div class=\"alert alert-block\">\r\n                            Are you using our new design to create or edit your events?\r\n                            <a href=\"#newcreate\"> Jump to new help </a>\r\n                            <span>\r\n                                or <a href=\"#\"> Learn more </a>\r\n                            </span>\r\n                        </div> -->\r\n\r\n                        <!--step-->\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                1</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    To download Eventbrite Sync, go <a href=\"https://sites.secure.force.com/appexchange/listingDetail?listingId=a0N30000005uwzXEAQ\">here</a>.</p>\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                2</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    For installation and configuration instructions, click <a href=\"http://groundwire.org/apps/eventbrite-sync/install-configuration-guide\">here</a>. And to learn more about Eventbrite Sync, check out the <a href=\"http://groundwire.org/apps/eventbrite-sync/users-guide\">user\'s guide</a>. You can also watch <a href=\"http://youtu.be/E8HFlkl_eUw\">this handy video introduction</a> to Eventbrite Sync.</p>\r\n                                <p>\r\n                                    &nbsp;</p>\r\n                            </div>\r\n                        </div>\r\n                        <p class=\"center\">\r\n                            <span style=\"font-size: large;\"><strong>Check out these great features of Eventbrite Sync</strong></span></p>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    - Connect to an Eventbrite account from within Salesforce and read information about events, registrants and attendees</p>\r\n                                <p>\r\n                                    - Selectively import event registrant and attendee information into Salesforce and optionally match on email address</p>\r\n                                <p>\r\n                                    - Import into Leads or Contacts</p>\r\n                                <p>\r\n                                    - Records tickets checked in</p>\r\n                                <p>\r\n                                    - Optionally associate imported leads or contacts to a campaign</p>\r\n                                <p>\r\n                                    - Optionally create Opportunity records when payment transactions present</p>\r\n                                <p>\r\n                                    - Remembers your option selections for next import</p>\r\n                            </div>\r\n                            <p>\r\n                                &nbsp;</p>\r\n                        </div>\r\n                        <div class=\"step\">\r\n                            <div class=\"stepnumber\">\r\n                                &nbsp;</div>\r\n                            <div class=\"stepcontent\">\r\n                                <p>\r\n                                    <strong>Tip:</strong> Visit our <a href=\"http://eventbrite.appstores.com/products/home\">Partnership Showcase</a> for other handy apps and <a href=\"http://eventbrite.appstores.com/t/categories/crm\">CRM-related tools</a>!</p>\r\n                            </div>\r\n                        </div>\r\n                        <!--step-->', '1349230618', null, '0', '0');
